                    <CardContent className="p-6">
                      <div className="flex items-center space-x-3 mb-4">
                        <div className="p-2 bg-orange-100 dark:bg-orange-900/20 rounded-lg group-hover:bg-orange-200 transition-colors">
                          <Presentation className="h-6 w-6 text-orange-600" />
                        </div>
                        <h3 className="text-lg font-semibold text-gray-900 dark:text-white group-hover:text-orange-600 transition-colors">
                          {ppt.title}
                        </h3>
                      </div>
                      {ppt.description && (
                        <p className="text-gray-600 dark:text-gray-400 text-sm mb-4 line-clamp-2">
                          {ppt.description}
                        </p>
                      )}
                      <div className="flex items-center justify-between">
                        <Badge variant="default" className="bg-orange-100 text-orange-800 hover:bg-orange-200">
