            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {subjects.filter(subject => subject.isActive).map((subject) => (
                  <Card 
                    key={subject.id} 
                    className="shadow-sm hover:shadow-lg transition-all duration-300 cursor-pointer group border-2 border-pink-100 hover:border-pink-300"
                    onClick={() => {
                      setSelectedSubject(subject);
                      setView('pdfs');
                    }}
                  >
                    <CardContent className="p-6">
                      <div className="flex items-center space-x-3 mb-4">
                        <div className="p-2 bg-pink-100 dark:bg-pink-900/20 rounded-lg group-hover:bg-pink-200 transition-colors">
                          <BookOpen className="h-6 w-6 text-pink-600" />
                        </div>
                        <h3 className="text-lg font-semibold text-gray-900 dark:text-white group-hover:text-pink-600 transition-colors">
                          {subject.name}
                        </h3>
                      </div>
                      {subject.description && (
                        <p className="text-gray-600 dark:text-gray-400 text-sm mb-4 line-clamp-2">
