import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Folder, Plus, Edit, Trash2, ArrowLeft, FileText, Eye, FolderOpen, Upload } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { EnhancedFileForm } from "@/components/enhanced-file-form";

interface PdfBatch {
  id: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PdfCourse {
  id: string;
  batchId: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PdfSubject {
  id: string;
  courseId: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PdfFile {
  id: string;
  title: string;
  description: string | null;
  subjectId: string | null;
  courseId: string | null;
  batchId: string;
  fileUrl: string;
  embedUrl: string;
  platform: string;
  pageCount: number;
  createdAt: string;
  updatedAt: string;
}

type ViewLevel = "batches" | "courses" | "subjects" | "files";

export default function PDFManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Navigation states
  const [currentView, setCurrentView] = useState<ViewLevel>("batches");
  const [selectedBatch, setSelectedBatch] = useState<PdfBatch | null>(null);
  const [selectedCourse, setSelectedCourse] = useState<PdfCourse | null>(null);
  const [selectedSubject, setSelectedSubject] = useState<PdfSubject | null>(null);

  // Dialog states
  const [showBatchDialog, setShowBatchDialog] = useState(false);
  const [showCourseDialog, setShowCourseDialog] = useState(false);
  const [showSubjectDialog, setShowSubjectDialog] = useState(false);
  const [showFileDialog, setShowFileDialog] = useState(false);
  const [showAddToCategoryDialog, setShowAddToCategoryDialog] = useState<string | null>(null);
  const [showAddToModuleDialog, setShowAddToModuleDialog] = useState<string | null>(null);
  const [showAddToSectionDialog, setShowAddToSectionDialog] = useState<string | null>(null);
  const [showAddToCategoriesDialog, setShowAddToCategoriesDialog] = useState(false);
  const [showAddToModulesDialog, setShowAddToModulesDialog] = useState(false);
  const [showAddToSectionsDialog, setShowAddToSectionsDialog] = useState(false);
  const [showVisualEditorDialog, setShowVisualEditorDialog] = useState<string | null>(null);
  const [visualEditorContext, setVisualEditorContext] = useState<{type: string, item: any} | null>(null);

  // Edit states
  const [editingBatch, setEditingBatch] = useState<PdfBatch | null>(null);
  const [editingCourse, setEditingCourse] = useState<PdfCourse | null>(null);
  const [editingSubject, setEditingSubject] = useState<PdfSubject | null>(null);
  const [editingFile, setEditingFile] = useState<PdfFile | null>(null);

  // Queries
  const { data: batches } = useQuery({
    queryKey: ["/api/pdf/batches"],
    enabled: currentView === "batches"
  });

  const { data: courses } = useQuery({
    queryKey: ["/api/pdf/batches", selectedBatch?.id, "courses"],
    enabled: currentView === "courses" && !!selectedBatch
  });

  const { data: batchFiles } = useQuery({
    queryKey: ["/api/pdf/batches", selectedBatch?.id, "files"],
    enabled: currentView === "courses" && !!selectedBatch
  });

  const { data: subjects } = useQuery({
    queryKey: ["/api/pdf/courses", selectedCourse?.id, "subjects"],
    enabled: currentView === "subjects" && !!selectedCourse
  });

  const { data: files } = useQuery({
    queryKey: ["/api/pdf/subjects", selectedSubject?.id, "files"],
    enabled: currentView === "files" && !!selectedSubject
  });

  // Navigation handlers
  const navigateToCourses = (batch: PdfBatch) => {
    setSelectedBatch(batch);
    setCurrentView("courses");
  };

  const navigateToSubjects = (course: PdfCourse) => {
    setSelectedCourse(course);
    setCurrentView("subjects");
  };

  const navigateToFiles = (subject: PdfSubject) => {
    setSelectedSubject(subject);
    setCurrentView("files");
  };

  const goBack = () => {
    switch (currentView) {
      case "courses":
        setCurrentView("batches");
        setSelectedBatch(null);
        break;
      case "subjects":
        setCurrentView("courses");
        setSelectedCourse(null);
        break;
      case "files":
        setCurrentView("subjects");
        setSelectedSubject(null);
        break;
    }
  };

  // Delete handlers
  const deleteBatch = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/pdf/batches/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches"] });
      toast({ title: "Success", description: "PDF Category deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PDF category", variant: "destructive" });
    }
  });

  const deleteCourse = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/pdf/courses/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches", selectedBatch?.id, "courses"] });
      toast({ title: "Success", description: "PDF Module deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PDF module", variant: "destructive" });
    }
  });

  const deleteSubject = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/pdf/subjects/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/courses", selectedCourse?.id, "subjects"] });
      toast({ title: "Success", description: "PDF Section deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PDF section", variant: "destructive" });
    }
  });

  const deleteFile = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/pdf/files/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/subjects", selectedSubject?.id, "files"] });
      toast({ title: "Success", description: "PDF File deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PDF file", variant: "destructive" });
    }
  });

  // Render functions
  const renderBreadcrumb = () => (
    <div className="flex items-center gap-2 mb-6 text-sm text-gray-600">
      <span className="font-medium">PDF Management</span>
      {selectedBatch && (
        <>
          <span>/</span>
          <span>{selectedBatch.title}</span>
        </>
      )}
      {selectedCourse && (
        <>
          <span>/</span>
          <span>{selectedCourse.title}</span>
        </>
      )}
      {selectedSubject && (
        <>
          <span>/</span>
          <span>{selectedSubject.title}</span>
        </>
      )}
    </div>
  );

  const renderBatchesView = () => (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold flex items-center">
          <Folder className="h-6 w-6 mr-3 text-amber-600" />
          PDF Categories - Main Folders
        </h2>
        <Dialog open={showBatchDialog} onOpenChange={setShowBatchDialog}>
          <DialogTrigger asChild>
            <Button className="bg-amber-600 hover:bg-amber-700">
              <Plus className="h-4 w-4 mr-2" />
              Create New Category
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create PDF Category</DialogTitle>
              <DialogDescription>Create a new main category folder for organizing PDF content</DialogDescription>
            </DialogHeader>
            <BatchForm onClose={() => setShowBatchDialog(false)} />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {batches && Array.isArray(batches) && batches.map((batch: PdfBatch) => (
          <Card key={batch.id} className="hover:shadow-lg transition-shadow cursor-pointer border-amber-200">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <FolderOpen className="h-8 w-8 text-amber-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{batch.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{batch.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingBatch(batch);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteBatch.mutate(batch.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToCourses(batch)}
                  className="w-full bg-amber-100 hover:bg-amber-200 text-amber-800"
                  variant="ghost"
                >
                  <FolderOpen className="h-4 w-4 mr-2" />
                  Open Category Folder
                </Button>


              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderCoursesView = () => (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button variant="ghost" onClick={goBack}>
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Categories
          </Button>
          <h2 className="text-2xl font-bold flex items-center">
            <Folder className="h-6 w-6 mr-3 text-blue-600" />
            PDF Modules in "{selectedBatch?.title}"
          </h2>
        </div>
        <div className="flex space-x-2">
          <Dialog open={showCourseDialog} onOpenChange={setShowCourseDialog}>
            <DialogTrigger asChild>
              <Button className="bg-blue-600 hover:bg-blue-700">
                <Plus className="h-4 w-4 mr-2" />
                Create New Module
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create PDF Module</DialogTitle>
                <DialogDescription>Create a new module folder within {selectedBatch?.title}</DialogDescription>
              </DialogHeader>
              <CourseForm batchId={selectedBatch!.id} onClose={() => setShowCourseDialog(false)} />
            </DialogContent>
          </Dialog>

          <Dialog open={showAddToModulesDialog} onOpenChange={setShowAddToModulesDialog}>
            <DialogTrigger asChild>
              <Button variant="outline" className="bg-green-50 hover:bg-green-100 text-green-700 border-green-200">
                <Plus className="h-4 w-4 mr-2" />
                Add PDF File
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Add PDF File to Modules</DialogTitle>
                <DialogDescription>Add a PDF file directly to the modules level in {selectedBatch?.title}</DialogDescription>
              </DialogHeader>
              <EnhancedFileForm 
                batchId={selectedBatch!.id}
                subjectId="modules-direct"
                fileType="pdf" 
                onClose={() => {
                  setShowAddToModulesDialog(false);
                  queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches", selectedBatch?.id, "courses"] });
                  queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches", selectedBatch?.id, "files"] });
                  queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches"] });
                }}
              />
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Direct Files in Category */}
      {batchFiles && Array.isArray(batchFiles) && batchFiles.length > 0 && (
        <div className="mb-8">
          <h3 className="text-lg font-semibold mb-4 flex items-center">
            <FileText className="h-5 w-5 mr-2 text-green-600" />
            PDF Files in "{selectedBatch?.title}" Category
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {batchFiles.map((file: PdfFile) => (
              <Card key={file.id} className="hover:shadow-lg transition-shadow border-green-200">
                <CardContent className="p-4">
                  <div className="flex items-start justify-between mb-3">
                    <div className="flex items-center flex-1">
                      <FileText className="h-6 w-6 text-green-600 mr-3" />
                      <div className="flex-1">
                        <h4 className="font-medium text-sm">{file.title}</h4>
                        <p className="text-xs text-gray-500 mt-1">{file.description}</p>
                      </div>
                    </div>
                    <div className="flex gap-1">
                      <Button size="sm" variant="ghost" onClick={() => setEditingFile(file)}>
                        <Edit className="h-3 w-3" />
                      </Button>
                      <Button size="sm" variant="ghost" className="text-red-600">
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </div>
                  </div>

                  <div className="flex items-center justify-between">
                    <Badge variant="outline" className="text-xs">
                      {file.platform}
                    </Badge>
                    <Badge variant="secondary" className="text-xs">
                      {file.pageCount || 0} pages
                    </Badge>
                    <Button size="sm" variant="ghost">
                      <Eye className="h-3 w-3" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
