          </Button>
          <h2 className="text-2xl font-bold flex items-center">
            <Folder className="h-6 w-6 mr-3 text-purple-600" />
            PDF Sections in "{selectedCourse?.title}"
          </h2>
        </div>
        <div className="flex space-x-2">
          <Dialog open={showSubjectDialog} onOpenChange={setShowSubjectDialog}>
            <DialogTrigger asChild>
              <Button className="bg-purple-600 hover:bg-purple-700">
                <Plus className="h-4 w-4 mr-2" />
                Create New Section
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create PDF Section</DialogTitle>
                <DialogDescription>Create a new section folder within {selectedCourse?.title}</DialogDescription>
              </DialogHeader>
              <SubjectForm courseId={selectedCourse!.id} onClose={() => setShowSubjectDialog(false)} />
            </DialogContent>
          </Dialog>

          <Dialog open={showAddToSectionsDialog} onOpenChange={setShowAddToSectionsDialog}>
            <DialogTrigger asChild>
              <Button variant="outline" className="bg-green-50 hover:bg-green-100 text-green-700 border-green-200">
                <Plus className="h-4 w-4 mr-2" />
