            <DialogTitle>
              Visual Editor - {visualEditorContext?.item?.title || 'PDF Management'}
            </DialogTitle>
            <DialogDescription>
              {visualEditorContext?.type === 'batch' && `Add or manage PDF files for category: ${visualEditorContext.item.title}`}
              {visualEditorContext?.type === 'course' && `Add or manage PDF files for module: ${visualEditorContext.item.title}`}
              {visualEditorContext?.type === 'subject' && `Add or manage PDF files for section: ${visualEditorContext.item.title}`}
              {visualEditorContext?.type === 'file' && `Edit PDF file: ${visualEditorContext.item.title}`}
            </DialogDescription>
          </DialogHeader>

          {visualEditorContext && (
            <div className="space-y-6">
              {/* Add New PDF Section */}
              <div className="border rounded-lg p-4 bg-gray-50">
                <h3 className="font-semibold mb-3 text-lg">Add New PDF File</h3>
                <EnhancedFileForm 
                  batchId={
                    visualEditorContext.type === 'batch' ? visualEditorContext.item.id :
                    visualEditorContext.type === 'course' ? selectedBatch?.id :
                    visualEditorContext.type === 'subject' ? selectedBatch?.id :
                    visualEditorContext.type === 'file' ? visualEditorContext.item.batchId : ''
                  }
                  courseId={
                    visualEditorContext.type === 'course' ? visualEditorContext.item.id :
                    visualEditorContext.type === 'subject' ? selectedCourse?.id :
                    visualEditorContext.type === 'file' ? visualEditorContext.item.courseId : undefined
                  }
                  subjectId={
                    visualEditorContext.type === 'batch' ? 'batch-direct' :
                    visualEditorContext.type === 'course' ? 'course-direct' :
                    visualEditorContext.type === 'subject' ? visualEditorContext.item.id :
                    visualEditorContext.type === 'file' ? visualEditorContext.item.subjectId : 'direct'
                  }
                  fileType="pdf" 
                  editingFile={visualEditorContext.type === 'file' ? visualEditorContext.item : undefined}
                  onClose={() => {
                    setShowVisualEditorDialog(null);
                    setVisualEditorContext(null);
                    queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches"] });
                    if (selectedBatch) {
                      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches", selectedBatch.id, "courses"] });
                    }
                    if (selectedCourse) {
                      queryClient.invalidateQueries({ queryKey: ["/api/pdf/courses", selectedCourse.id, "subjects"] });
                    }
                    if (selectedSubject) {
                      queryClient.invalidateQueries({ queryKey: ["/api/pdf/subjects", selectedSubject.id, "files"] });
                    }
                  }}
                />
              </div>

              {/* Context Information */}
              <div className="border rounded-lg p-4 bg-blue-50">
                <h3 className="font-semibold mb-2">Context Information</h3>
                <div className="text-sm text-gray-700">
                  <p><strong>Type:</strong> {visualEditorContext.type.charAt(0).toUpperCase() + visualEditorContext.type.slice(1)}</p>
                  <p><strong>Name:</strong> {visualEditorContext.item.title}</p>
                  {visualEditorContext.item.description && (
                    <p><strong>Description:</strong> {visualEditorContext.item.description}</p>
                  )}
                  {visualEditorContext.type === 'file' && (
                    <>
                      <p><strong>Platform:</strong> {visualEditorContext.item.platform}</p>
                      <p><strong>Pages:</strong> {visualEditorContext.item.pageCount}</p>
                      {visualEditorContext.item.fileUrl && (
                        <p><strong>File URL:</strong> <a href={visualEditorContext.item.fileUrl} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">View File</a></p>
                      )}
                    </>
                  )}
                </div>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

// Form Components
function BatchForm({ onClose }: { onClose: () => void }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createMutation = useMutation({
    mutationFn: async (data: { title: string; description: string }) => {
      return await apiRequest("POST", "/api/pdf/batches", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches"] });
      toast({ title: "Success", description: "PDF Category created successfully" });
      onClose();
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create PDF category", variant: "destructive" });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    createMutation.mutate({ title, description });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <Label htmlFor="title">Category Name</Label>
        <Input
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="e.g., Engineering Mathematics"
          required
        />
      </div>
      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Brief description of this category"
        />
      </div>
      <div className="flex justify-end space-x-2">
        <Button type="button" variant="outline" onClick={onClose}>
          Cancel
        </Button>
        <Button type="submit" disabled={createMutation.isPending}>
          {createMutation.isPending ? "Creating..." : "Create Category"}
        </Button>
      </div>
    </form>
  );
}

