// Edit Forms
function EditBatchForm({ batch, onClose }: { batch: PdfBatch; onClose: () => void }) {
  const [title, setTitle] = useState(batch.title);
  const [description, setDescription] = useState(batch.description || "");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateMutation = useMutation({
    mutationFn: async (data: { title: string; description: string }) => {
      return await apiRequest("PATCH", `/api/pdf/batches/${batch.id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches"] });
      toast({ title: "Success", description: "PDF Category updated successfully" });
      onClose();
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to update PDF category", variant: "destructive" });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateMutation.mutate({ title, description });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <Label htmlFor="title">Category Name</Label>
        <Input
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <div className="flex justify-end space-x-2">
        <Button type="button" variant="outline" onClick={onClose}>
          Cancel
        </Button>
        <Button type="submit" disabled={updateMutation.isPending}>
          {updateMutation.isPending ? "Updating..." : "Update Category"}
        </Button>
      </div>
    </form>
  );
}

function EditCourseForm({ course, onClose }: { course: PdfCourse; onClose: () => void }) {
  const [title, setTitle] = useState(course.title);
  const [description, setDescription] = useState(course.description || "");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateMutation = useMutation({
    mutationFn: async (data: { title: string; description: string }) => {
      return await apiRequest("PATCH", `/api/pdf/courses/${course.id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches", course.batchId, "courses"] });
      toast({ title: "Success", description: "PDF Module updated successfully" });
      onClose();
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to update PDF module", variant: "destructive" });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateMutation.mutate({ title, description });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <Label htmlFor="title">Module Name</Label>
        <Input
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <div className="flex justify-end space-x-2">
        <Button type="button" variant="outline" onClick={onClose}>
          Cancel
        </Button>
        <Button type="submit" disabled={updateMutation.isPending}>
          {updateMutation.isPending ? "Updating..." : "Update Module"}
        </Button>
      </div>
    </form>
  );
}

function EditSubjectForm({ subject, onClose }: { subject: PdfSubject; onClose: () => void }) {
  const [title, setTitle] = useState(subject.title);
  const [description, setDescription] = useState(subject.description || "");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateMutation = useMutation({
    mutationFn: async (data: { title: string; description: string }) => {
      return await apiRequest("PATCH", `/api/pdf/subjects/${subject.id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/courses", subject.courseId, "subjects"] });
      toast({ title: "Success", description: "PDF Section updated successfully" });
      onClose();
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to update PDF section", variant: "destructive" });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateMutation.mutate({ title, description });
