import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Folder, Plus, Edit, Trash2, ArrowLeft, FileText, Eye, FolderOpen, Upload, Presentation } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { EnhancedFileForm } from "@/components/enhanced-file-form";

interface PptBatch {
  id: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PptCourse {
  id: string;
  batchId: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PptSubject {
  id: string;
  courseId: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PptFile {
  id: string;
  title: string;
  description: string | null;
  subjectId: string | null;
  courseId: string | null;
  batchId: string;
  fileUrl: string;
  embedUrl: string;
  platform: string;
  pageCount: number;
  createdAt: string;
  updatedAt: string;
}

type ViewLevel = "batches" | "courses" | "subjects" | "files";

export default function PPTManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Navigation states
  const [currentView, setCurrentView] = useState<ViewLevel>("batches");
  const [selectedBatch, setSelectedBatch] = useState<PptBatch | null>(null);
  const [selectedCourse, setSelectedCourse] = useState<PptCourse | null>(null);
  const [selectedSubject, setSelectedSubject] = useState<PptSubject | null>(null);

  // Dialog states
  const [showBatchDialog, setShowBatchDialog] = useState(false);
  const [showCourseDialog, setShowCourseDialog] = useState(false);
  const [showSubjectDialog, setShowSubjectDialog] = useState(false);
  const [showFileDialog, setShowFileDialog] = useState(false);

  // Edit states
  const [editingBatch, setEditingBatch] = useState<PptBatch | null>(null);
  const [editingCourse, setEditingCourse] = useState<PptCourse | null>(null);
  const [editingSubject, setEditingSubject] = useState<PptSubject | null>(null);
  const [editingFile, setEditingFile] = useState<PptFile | null>(null);

  // Queries
  const { data: batches } = useQuery({
    queryKey: ["/api/ppt/batches"],
    enabled: currentView === "batches"
  });

  const { data: courses } = useQuery({
    queryKey: ["/api/ppt/batches", selectedBatch?.id, "courses"],
    enabled: currentView === "courses" && !!selectedBatch
  });

  const { data: subjects } = useQuery({
    queryKey: ["/api/ppt/courses", selectedCourse?.id, "subjects"],
    enabled: currentView === "subjects" && !!selectedCourse
  });

  const { data: files } = useQuery({
    queryKey: ["/api/ppt/subjects", selectedSubject?.id, "files"],
    enabled: currentView === "files" && !!selectedSubject
  });

  // Navigation handlers
  const navigateToCourses = (batch: PptBatch) => {
    setSelectedBatch(batch);
    setCurrentView("courses");
  };

  const navigateToSubjects = (course: PptCourse) => {
    setSelectedCourse(course);
    setCurrentView("subjects");
  };

  const navigateToFiles = (subject: PptSubject) => {
    setSelectedSubject(subject);
    setCurrentView("files");
  };

  const goBack = () => {
    switch (currentView) {
      case "courses":
        setCurrentView("batches");
        setSelectedBatch(null);
        break;
      case "subjects":
        setCurrentView("courses");
        setSelectedCourse(null);
        break;
      case "files":
        setCurrentView("subjects");
        setSelectedSubject(null);
        break;
    }
  };

  // Delete handlers
  const deleteBatch = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/ppt/batches/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches"] });
      toast({ title: "Success", description: "PPT Category deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PPT category", variant: "destructive" });
    }
  });

  const deleteCourse = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/ppt/courses/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches", selectedBatch?.id, "courses"] });
      toast({ title: "Success", description: "PPT Module deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PPT module", variant: "destructive" });
    }
  });

  const deleteSubject = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/ppt/subjects/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/courses", selectedCourse?.id, "subjects"] });
      toast({ title: "Success", description: "PPT Section deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PPT section", variant: "destructive" });
    }
  });

  const deleteFile = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/ppt/files/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/subjects", selectedSubject?.id, "files"] });
      toast({ title: "Success", description: "PPT File deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PPT file", variant: "destructive" });
    }
  });

  // Render functions
  const renderBreadcrumb = () => (
    <div className="flex items-center gap-2 mb-6 text-sm text-gray-600">
      <span className="font-medium">PPT Management</span>
      {selectedBatch && (
        <>
          <span>/</span>
          <span>{selectedBatch.title}</span>
        </>
      )}
      {selectedCourse && (
        <>
          <span>/</span>
          <span>{selectedCourse.title}</span>
        </>
      )}
      {selectedSubject && (
        <>
          <span>/</span>
          <span>{selectedSubject.title}</span>
        </>
      )}
    </div>
  );

  const renderBatchesView = () => (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold flex items-center">
          <Folder className="h-6 w-6 mr-3 text-orange-600" />
          PPT Categories - Main Folders
        </h2>
        <Dialog open={showBatchDialog} onOpenChange={setShowBatchDialog}>
          <DialogTrigger asChild>
            <Button className="bg-orange-600 hover:bg-orange-700">
              <Plus className="h-4 w-4 mr-2" />
              Create New Category
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create PPT Category</DialogTitle>
              <DialogDescription>Create a new main category folder for organizing PPT content</DialogDescription>
            </DialogHeader>
            <BatchForm onClose={() => setShowBatchDialog(false)} />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {batches && batches.map((batch: PptBatch) => (
          <Card key={batch.id} className="hover:shadow-lg transition-shadow cursor-pointer border-orange-200">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <FolderOpen className="h-8 w-8 text-orange-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{batch.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{batch.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingBatch(batch);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteBatch.mutate(batch.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToCourses(batch)}
                  className="w-full bg-orange-100 hover:bg-orange-200 text-orange-800"
                  variant="ghost"
                >
                  <FolderOpen className="h-4 w-4 mr-2" />
                  Open Category Folder
                </Button>

                <Dialog>
                  <DialogTrigger asChild>
                    <Button variant="outline" className="w-full">
                      <Upload className="h-4 w-4 mr-2" />
                      Add PPT to Category
                    </Button>
                  </DialogTrigger>
                  <DialogContent className="max-w-2xl">
                    <DialogHeader>
                      <DialogTitle>Add PPT to Category</DialogTitle>
                      <DialogDescription>Add PPT directly to {batch.title} category</DialogDescription>
                    </DialogHeader>
                    <EnhancedFileForm 
                      batchId={batch.id}
                      subjectId="batch-direct"
                      fileType="ppt" 
                      onClose={() => queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches"] })}
                    />
                  </DialogContent>
                </Dialog>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderCoursesView = () => (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button variant="ghost" onClick={goBack}>
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Categories
          </Button>
          <h2 className="text-2xl font-bold flex items-center">
            <Folder className="h-6 w-6 mr-3 text-cyan-600" />
            PPT Modules in "{selectedBatch?.title}"
          </h2>
        </div>
        <Dialog open={showCourseDialog} onOpenChange={setShowCourseDialog}>
          <DialogTrigger asChild>
            <Button className="bg-cyan-600 hover:bg-cyan-700">
              <Plus className="h-4 w-4 mr-2" />
              Create New Module
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create PPT Module</DialogTitle>
              <DialogDescription>Create a new module folder within {selectedBatch?.title}</DialogDescription>
            </DialogHeader>
            <CourseForm batchId={selectedBatch!.id} onClose={() => setShowCourseDialog(false)} />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {courses && courses.map((course: PptCourse) => (
          <Card key={course.id} className="hover:shadow-lg transition-shadow cursor-pointer border-cyan-200">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <FolderOpen className="h-8 w-8 text-cyan-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{course.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{course.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingCourse(course);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteCourse.mutate(course.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToSubjects(course)}
                  className="w-full bg-cyan-100 hover:bg-cyan-200 text-cyan-800"
                  variant="ghost"
                >
                  <FolderOpen className="h-4 w-4 mr-2" />
                  Open Module Folder
                </Button>

                <Dialog>
                  <DialogTrigger asChild>
                    <Button variant="outline" className="w-full">
                      <Upload className="h-4 w-4 mr-2" />
                      Add PPT to Module
                    </Button>
                  </DialogTrigger>
                  <DialogContent className="max-w-2xl">
                    <DialogHeader>
                      <DialogTitle>Add PPT to Module</DialogTitle>
                      <DialogDescription>Add PPT directly to {course.title} module</DialogDescription>
                    </DialogHeader>
                    <EnhancedFileForm 
                      batchId={selectedBatch!.id}
                      courseId={course.id}
                      subjectId="course-direct"
                      fileType="ppt" 
                      onClose={() => queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches", selectedBatch?.id, "courses"] })}
                    />
                  </DialogContent>
                </Dialog>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderSubjectsView = () => (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button variant="ghost" onClick={goBack}>
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Modules
          </Button>
          <h2 className="text-2xl font-bold flex items-center">
            <Folder className="h-6 w-6 mr-3 text-pink-600" />
            PPT Sections in "{selectedCourse?.title}"
          </h2>
        </div>
        <Dialog open={showSubjectDialog} onOpenChange={setShowSubjectDialog}>
          <DialogTrigger asChild>
            <Button className="bg-pink-600 hover:bg-pink-700">
              <Plus className="h-4 w-4 mr-2" />
              Create New Section
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create PPT Section</DialogTitle>
              <DialogDescription>Create a new section folder within {selectedCourse?.title}</DialogDescription>
            </DialogHeader>
            <SubjectForm courseId={selectedCourse!.id} onClose={() => setShowSubjectDialog(false)} />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {subjects && subjects.map((subject: PptSubject) => (
          <Card key={subject.id} className="hover:shadow-lg transition-shadow cursor-pointer border-pink-200">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <FolderOpen className="h-8 w-8 text-pink-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{subject.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{subject.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingSubject(subject);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteSubject.mutate(subject.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToFiles(subject)}
                  className="w-full bg-pink-100 hover:bg-pink-200 text-pink-800"
                  variant="ghost"
                >
                  <Presentation className="h-4 w-4 mr-2" />
                  View PPT Files
                </Button>

                <Dialog>
                  <DialogTrigger asChild>
                    <Button variant="outline" className="w-full">
                      <Upload className="h-4 w-4 mr-2" />
                      Add PPT to Section
                    </Button>
                  </DialogTrigger>
                  <DialogContent className="max-w-2xl">
                    <DialogHeader>
                      <DialogTitle>Add PPT to Section</DialogTitle>
                      <DialogDescription>Add PPT to {subject.title} section</DialogDescription>
                    </DialogHeader>
                    <EnhancedFileForm 
                      batchId={selectedBatch!.id}
                      courseId={selectedCourse!.id}
                      subjectId={subject.id}
                      fileType="ppt" 
                      onClose={() => queryClient.invalidateQueries({ queryKey: ["/api/ppt/courses", selectedCourse?.id, "subjects"] })}
                    />
                  </DialogContent>
                </Dialog>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderFilesView = () => (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button variant="ghost" onClick={goBack}>
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Sections
          </Button>
          <h2 className="text-2xl font-bold flex items-center">
            <Presentation className="h-6 w-6 mr-3 text-indigo-600" />
            PPT Files in "{selectedSubject?.title}"
          </h2>
        </div>
        <Dialog open={showFileDialog} onOpenChange={setShowFileDialog}>
          <DialogTrigger asChild>
            <Button className="bg-indigo-600 hover:bg-indigo-700">
              <Plus className="h-4 w-4 mr-2" />
              Add PPT File
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Add PPT File</DialogTitle>
              <DialogDescription>Add a new PPT file to {selectedSubject?.title}</DialogDescription>
            </DialogHeader>
            <EnhancedFileForm 
              batchId={selectedBatch!.id}
              courseId={selectedCourse!.id}
              subjectId={selectedSubject!.id}
              fileType="ppt" 
              onClose={() => setShowFileDialog(false)}
            />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {files && files.map((file: PptFile) => (
          <Card key={file.id} className="hover:shadow-lg transition-shadow border-indigo-200">
            <CardContent className="p-4">
              <div className="flex items-start justify-between mb-3">
                <div className="flex items-center">
                  <Presentation className="h-6 w-6 text-indigo-600 mr-3" />
                  <div>
                    <h3 className="font-medium">{file.title}</h3>
                    <p className="text-xs text-gray-500 mt-1">{file.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={() => {
                    toast({ title: "Coming Soon", description: "PPT viewer will be available soon" });
                  }}>
                    <Eye className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" onClick={() => setEditingFile(file)}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={() => {
                    deleteFile.mutate(file.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="flex items-center justify-between">
                <Badge variant="secondary" className="text-xs">
                  {file.platform}
                </Badge>
                <Badge variant="outline" className="text-xs">
                  {file.pageCount} slides
                </Badge>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  return (
    <div className="container mx-auto px-4 py-8">
      {renderBreadcrumb()}

      {currentView === "batches" && renderBatchesView()}
      {currentView === "courses" && renderCoursesView()}
      {currentView === "subjects" && renderSubjectsView()}
      {currentView === "files" && renderFilesView()}

      {/* Edit Dialogs */}
      <Dialog open={!!editingBatch} onOpenChange={() => setEditingBatch(null)}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit PPT Category</DialogTitle>
          </DialogHeader>
          {editingBatch && (
            <EditBatchForm batch={editingBatch} onClose={() => setEditingBatch(null)} />
          )}
        </DialogContent>
      </Dialog>

      <Dialog open={!!editingCourse} onOpenChange={() => setEditingCourse(null)}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit PPT Module</DialogTitle>
          </DialogHeader>
          {editingCourse && (
            <EditCourseForm course={editingCourse} onClose={() => setEditingCourse(null)} />
          )}
        </DialogContent>
      </Dialog>

      <Dialog open={!!editingSubject} onOpenChange={() => setEditingSubject(null)}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit PPT Section</DialogTitle>
          </DialogHeader>
          {editingSubject && (
            <EditSubjectForm subject={editingSubject} onClose={() => setEditingSubject(null)} />
          )}
        </DialogContent>
      </Dialog>

      <Dialog open={!!editingFile} onOpenChange={() => setEditingFile(null)}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Edit PPT File</DialogTitle>
          </DialogHeader>
          {editingFile && (
            <EditFileForm file={editingFile} onClose={() => setEditingFile(null)} />
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

// Form Components
function BatchForm({ onClose }: { onClose: () => void }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createMutation = useMutation({
    mutationFn: async (data: { title: string; description: string }) => {
      return await apiRequest("POST", "/api/ppt/batches", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches"] });
      toast({ title: "Success", description: "PPT Category created successfully" });
      onClose();
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create PPT category", variant: "destructive" });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    createMutation.mutate({ title, description });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <Label htmlFor="title">Category Name</Label>
        <Input
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="e.g., Business Presentations"
          required
        />
      </div>
      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Brief description of this category"
        />
      </div>
      <div className="flex justify-end space-x-2">
        <Button type="button" variant="outline" onClick={onClose}>
          Cancel
        </Button>
        <Button type="submit" disabled={createMutation.isPending}>
          {createMutation.isPending ? "Creating..." : "Create Category"}
        </Button>
      </div>
    </form>
  );
}

