import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { FileText, FolderOpen, ArrowLeft, Home, Eye, Book } from "lucide-react";
import PDFViewer from "@/components/pdf-viewer";
import { Link } from "wouter";

interface PdfBatch {
  id: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PdfCourse {
  id: string;
  batchId: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PdfSubject {
  id: string;
  courseId: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PdfFile {
  id: string;
  title: string;
  description: string | null;
  subjectId: string | null;
  courseId: string | null;
  batchId: string;
  fileUrl: string;
  embedUrl: string;
  platform: string;
  pageCount: number;
  createdAt: string;
  updatedAt: string;
}

type ViewLevel = "batches" | "courses" | "subjects" | "files" | "viewer";

export default function PDFContent() {
  const [currentView, setCurrentView] = useState<ViewLevel>("batches");
  const [selectedBatch, setSelectedBatch] = useState<PdfBatch | null>(null);
  const [selectedCourse, setSelectedCourse] = useState<PdfCourse | null>(null);
  const [selectedSubject, setSelectedSubject] = useState<PdfSubject | null>(null);
  const [selectedPdf, setSelectedPdf] = useState<PdfFile | null>(null);

  // Queries
  const { data: batches = [], isLoading: batchesLoading } = useQuery({
    queryKey: ["/api/pdf/batches"],
    enabled: currentView === "batches"
  });

  const { data: courses = [], isLoading: coursesLoading } = useQuery({
    queryKey: ["/api/pdf/batches", selectedBatch?.id, "courses"],
    enabled: currentView === "courses" && !!selectedBatch
  });

  const { data: subjects = [], isLoading: subjectsLoading } = useQuery({
    queryKey: ["/api/pdf/courses", selectedCourse?.id, "subjects"],
    enabled: currentView === "subjects" && !!selectedCourse
  });

  const { data: files = [], isLoading: filesLoading } = useQuery({
    queryKey: ["/api/pdf/subjects", selectedSubject?.id, "files"],
    enabled: currentView === "files" && !!selectedSubject
  });

  const { data: batchFiles = [], isLoading: batchFilesLoading } = useQuery({
    queryKey: ["/api/pdf/batches", selectedBatch?.id, "files"],
    enabled: currentView === "courses" && !!selectedBatch
  });

  // Navigation handlers
  const navigateToCourses = (batch: PdfBatch) => {
    setSelectedBatch(batch);
    setCurrentView("courses");
  };

  const navigateToSubjects = (course: PdfCourse) => {
    setSelectedCourse(course);
    setCurrentView("subjects");
  };

  const navigateToFiles = (subject: PdfSubject) => {
    setSelectedSubject(subject);
    setCurrentView("files");
  };

  const openPdfViewer = (file: PdfFile) => {
    setSelectedPdf(file);
    setCurrentView("viewer");
  };

  const goBack = () => {
    switch (currentView) {
      case "courses":
        setCurrentView("batches");
        setSelectedBatch(null);
        break;
      case "subjects":
        setCurrentView("courses");
        setSelectedCourse(null);
        break;
      case "files":
        setCurrentView("subjects");
        setSelectedSubject(null);
        break;
      case "viewer":
        setCurrentView("files");
        setSelectedPdf(null);
        break;
      default:
        setCurrentView("batches");
        break;
    }
  };

  const goHome = () => {
    setCurrentView("batches");
    setSelectedBatch(null);
    setSelectedCourse(null);
    setSelectedSubject(null);
    setSelectedPdf(null);
  };

  // Render PDF viewer
  if (currentView === "viewer" && selectedPdf) {
    return (
      <div className="h-screen">
        <PDFViewer
          pdfUrl={selectedPdf.embedUrl || selectedPdf.fileUrl}
          title={selectedPdf.title}
          pageCount={selectedPdf.pageCount || 0}
          onPageChange={() => {}}
        />
        <Button
          className="absolute top-4 left-4 z-20"
          onClick={goBack}
          variant="secondary"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Files
        </Button>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-amber-50 to-orange-100 dark:from-gray-900 dark:to-amber-900">
      <div className="container mx-auto px-4 py-8">

        {/* Header and Navigation */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-4">
            <Link href="/">
              <Button variant="outline" size="sm">
                <Home className="w-4 h-4 mr-2" />
                Home
              </Button>
            </Link>
            {currentView !== "batches" && (
              <Button variant="outline" size="sm" onClick={goBack}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back
              </Button>
            )}
          </div>
          <div className="text-sm text-gray-600">
            PDF Content Library
            {selectedBatch && ` / ${selectedBatch.title}`}
            {selectedCourse && ` / ${selectedCourse.title}`}
            {selectedSubject && ` / ${selectedSubject.title}`}
          </div>
        </div>

        {/* Title */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <div className="p-3 bg-amber-100 dark:bg-amber-900 rounded-full">
              <FileText className="h-8 w-8 text-amber-600 dark:text-amber-400" />
            </div>
          </div>
          <h1 className="text-3xl font-bold text-gray-800 dark:text-white mb-2">
            PDF Content Library
          </h1>
          <p className="text-gray-600 dark:text-gray-300">
            Browse and view PDF documents organized by categories
          </p>
        </div>

        {/* Content based on current view */}
        {currentView === "batches" && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold flex items-center mb-4">
              <Book className="h-6 w-6 mr-3 text-amber-600" />
              PDF Categories
            </h2>

            {batchesLoading ? (
              <div className="text-center py-8">Loading categories...</div>
            ) : batches.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                No PDF categories available yet
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {batches.map((batch: PdfBatch) => (
                  <Card 
                    key={batch.id} 
                    className="hover:shadow-lg transition-shadow cursor-pointer border-amber-200"
                    onClick={() => navigateToCourses(batch)}
                  >
                    <CardContent className="p-6">
                      <div className="flex items-center mb-4">
                        <FolderOpen className="h-8 w-8 text-amber-600 mr-3" />
                        <div>
                          <h3 className="font-semibold text-lg">{batch.title}</h3>
                          <p className="text-sm text-gray-600 mt-1">{batch.description}</p>
                        </div>
                      </div>
                      <Badge variant="secondary" className="bg-amber-100 text-amber-800">
                        Category
                      </Badge>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        )}

        {currentView === "courses" && selectedBatch && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold flex items-center mb-4">
              <FolderOpen className="h-6 w-6 mr-3 text-blue-600" />
              Modules in "{selectedBatch.title}"
            </h2>

            {/* Direct Files in Category */}
            {batchFilesLoading ? (
              <div className="text-center py-4">Loading category files...</div>
            ) : batchFiles.length > 0 ? (
              <div className="mb-8">
                <h3 className="text-lg font-semibold mb-4 flex items-center">
                  <FileText className="h-5 w-5 mr-2 text-green-600" />
                  PDF Files in this Category
                </h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {batchFiles.map((file: PdfFile) => (
                    <Card 
                      key={file.id} 
                      className="hover:shadow-lg transition-shadow cursor-pointer border-green-200"
                      onClick={() => openPdfViewer(file)}
                    >
                      <CardContent className="p-4">
                        <div className="flex items-center mb-3">
                          <FileText className="h-6 w-6 text-green-600 mr-3" />
                          <div className="flex-1">
                            <h4 className="font-medium text-sm">{file.title}</h4>
                            <p className="text-xs text-gray-500 mt-1">{file.description}</p>
                          </div>
                        </div>
                        <div className="flex items-center justify-between">
                          <Badge variant="outline" className="text-xs">
                            {file.platform}
                          </Badge>
                          <Badge variant="secondary" className="text-xs">
                            {file.pageCount || 0} pages
                          </Badge>
                          <Button size="sm" variant="ghost" onClick={(e) => {
                            e.stopPropagation();
                            openPdfViewer(file);
                          }}>
                            <Eye className="h-3 w-3" />
                          </Button>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </div>
            ) : null}

            {/* Modules */}
            {coursesLoading ? (
              <div className="text-center py-8">Loading modules...</div>
            ) : courses.length === 0 && batchFiles.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                No modules or files found in this category
              </div>
            ) : courses.length > 0 ? (
              <div>
                <h3 className="text-lg font-semibold mb-4 flex items-center">
                  <FolderOpen className="h-5 w-5 mr-2 text-blue-600" />
                  Modules
                </h3>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
