}

client/src/pages/ppt-content.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Presentation, FolderOpen, ArrowLeft, Home, Eye, Book } from "lucide-react";
import PPTViewer from "@/components/ppt-viewer";
import { Link } from "wouter";

interface PptBatch {
  id: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PptCourse {
  id: string;
  batchId: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PptSubject {
  id: string;
  courseId: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PptFile {
  id: string;
  title: string;
  description: string | null;
  subjectId: string | null;
  courseId: string | null;
  batchId: string;
  fileUrl: string;
  embedUrl: string;
  platform: string;
  pageCount: number;
  createdAt: string;
  updatedAt: string;
}

type ViewLevel = "batches" | "courses" | "subjects" | "files" | "viewer";

export default function PPTContent() {
  const [currentView, setCurrentView] = useState<ViewLevel>("batches");
  const [selectedBatch, setSelectedBatch] = useState<PptBatch | null>(null);
  const [selectedCourse, setSelectedCourse] = useState<PptCourse | null>(null);
  const [selectedSubject, setSelectedSubject] = useState<PptSubject | null>(null);
  const [selectedPpt, setSelectedPpt] = useState<PptFile | null>(null);

  // Queries
  const { data: batches = [], isLoading: batchesLoading } = useQuery({
    queryKey: ["/api/ppt/batches"],
    enabled: currentView === "batches"
  });

  const { data: courses = [], isLoading: coursesLoading } = useQuery({
    queryKey: ["/api/ppt/batches", selectedBatch?.id, "courses"],
    enabled: currentView === "courses" && !!selectedBatch
  });

  const { data: subjects = [], isLoading: subjectsLoading } = useQuery({
    queryKey: ["/api/ppt/courses", selectedCourse?.id, "subjects"],
    enabled: currentView === "subjects" && !!selectedCourse
  });

  const { data: files = [], isLoading: filesLoading } = useQuery({
    queryKey: ["/api/ppt/subjects", selectedSubject?.id, "files"],
    enabled: currentView === "files" && !!selectedSubject
  });

  // Navigation handlers
  const navigateToCourses = (batch: PptBatch) => {
    setSelectedBatch(batch);
    setCurrentView("courses");
  };

  const navigateToSubjects = (course: PptCourse) => {
    setSelectedCourse(course);
    setCurrentView("subjects");
  };

  const navigateToFiles = (subject: PptSubject) => {
    setSelectedSubject(subject);
    setCurrentView("files");
  };

  const openPptViewer = (file: PptFile) => {
    setSelectedPpt(file);
    setCurrentView("viewer");
  };

  const goBack = () => {
    switch (currentView) {
      case "courses":
        setCurrentView("batches");
        setSelectedBatch(null);
        break;
      case "subjects":
        setCurrentView("courses");
        setSelectedCourse(null);
        break;
      case "files":
        setCurrentView("subjects");
        setSelectedSubject(null);
        break;
      case "viewer":
        setCurrentView("files");
        setSelectedPpt(null);
        break;
      default:
        setCurrentView("batches");
        break;
    }
  };

  const goHome = () => {
    setCurrentView("batches");
    setSelectedBatch(null);
    setSelectedCourse(null);
    setSelectedSubject(null);
    setSelectedPpt(null);
  };

  // Render PPT viewer
  if (currentView === "viewer" && selectedPpt) {
    return (
      <div className="h-screen">
        <PPTViewer
          pptUrl={selectedPpt.embedUrl || selectedPpt.fileUrl}
          title={selectedPpt.title}
          slideCount={selectedPpt.pageCount || 0}
          onSlideChange={() => {}}
        />
        <Button
          className="absolute top-4 left-4 z-20"
          onClick={goBack}
          variant="secondary"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Back to Files
        </Button>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-blue-900">
      <div className="container mx-auto px-4 py-8">

        {/* Header and Navigation */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-4">
            <Link href="/">
              <Button variant="outline" size="sm">
                <Home className="w-4 h-4 mr-2" />
                Home
              </Button>
            </Link>
            {currentView !== "batches" && (
              <Button variant="outline" size="sm" onClick={goBack}>
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back
              </Button>
            )}
          </div>
          <div className="text-sm text-gray-600">
            PPT Content Library
            {selectedBatch && ` / ${selectedBatch.title}`}
            {selectedCourse && ` / ${selectedCourse.title}`}
            {selectedSubject && ` / ${selectedSubject.title}`}
          </div>
        </div>

        {/* Title */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <div className="p-3 bg-blue-100 dark:bg-blue-900 rounded-full">
              <Presentation className="h-8 w-8 text-blue-600 dark:text-blue-400" />
            </div>
          </div>
          <h1 className="text-3xl font-bold text-gray-800 dark:text-white mb-2">
            PPT Content Library
          </h1>
          <p className="text-gray-600 dark:text-gray-300">
            Browse and view PowerPoint presentations organized by categories
          </p>
        </div>

        {/* Content based on current view */}
        {currentView === "batches" && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold flex items-center mb-4">
              <Book className="h-6 w-6 mr-3 text-blue-600" />
              PPT Categories
            </h2>

            {batchesLoading ? (
              <div className="text-center py-8">Loading categories...</div>
            ) : batches.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                No PPT categories available yet
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {batches.map((batch: PptBatch) => (
                  <Card 
                    key={batch.id} 
                    className="hover:shadow-lg transition-shadow cursor-pointer border-blue-200"
                    onClick={() => navigateToCourses(batch)}
                  >
                    <CardContent className="p-6">
                      <div className="flex items-center mb-4">
                        <FolderOpen className="h-8 w-8 text-blue-600 mr-3" />
                        <div>
                          <h3 className="font-semibold text-lg">{batch.title}</h3>
                          <p className="text-sm text-gray-600 mt-1">{batch.description}</p>
                        </div>
                      </div>
                      <Badge variant="secondary" className="bg-blue-100 text-blue-800">
                        Category
                      </Badge>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        )}

        {currentView === "courses" && selectedBatch && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold flex items-center mb-4">
              <FolderOpen className="h-6 w-6 mr-3 text-cyan-600" />
              Modules in "{selectedBatch.title}"
            </h2>

            {coursesLoading ? (
              <div className="text-center py-8">Loading modules...</div>
            ) : courses.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                No modules found in this category
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {courses.map((course: PptCourse) => (
                  <Card 
                    key={course.id} 
                    className="hover:shadow-lg transition-shadow cursor-pointer border-cyan-200"
                    onClick={() => navigateToSubjects(course)}
                  >
                    <CardContent className="p-6">
                      <div className="flex items-center mb-4">
                        <FolderOpen className="h-8 w-8 text-cyan-600 mr-3" />
                        <div>
                          <h3 className="font-semibold text-lg">{course.title}</h3>
                          <p className="text-sm text-gray-600 mt-1">{course.description}</p>
                        </div>
                      </div>
                      <Badge variant="secondary" className="bg-cyan-100 text-cyan-800">
                        Module
                      </Badge>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        )}

        {currentView === "subjects" && selectedCourse && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold flex items-center mb-4">
              <FolderOpen className="h-6 w-6 mr-3 text-pink-600" />
              Sections in "{selectedCourse.title}"
            </h2>

            {subjectsLoading ? (
              <div className="text-center py-8">Loading sections...</div>
            ) : subjects.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                No sections found in this module
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {subjects.map((subject: PptSubject) => (
                  <Card 
                    key={subject.id} 
                    className="hover:shadow-lg transition-shadow cursor-pointer border-pink-200"
                    onClick={() => navigateToFiles(subject)}
                  >
                    <CardContent className="p-6">
                      <div className="flex items-center mb-4">
                        <FolderOpen className="h-8 w-8 text-pink-600 mr-3" />
                        <div>
                          <h3 className="font-semibold text-lg">{subject.title}</h3>
                          <p className="text-sm text-gray-600 mt-1">{subject.description}</p>
                        </div>
                      </div>
                      <Badge variant="secondary" className="bg-pink-100 text-pink-800">
                        Section
                      </Badge>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        )}

        {currentView === "files" && selectedSubject && (
          <div className="space-y-6">
            <h2 className="text-2xl font-bold flex items-center mb-4">
              <Presentation className="h-6 w-6 mr-3 text-indigo-600" />
              PPT Files in "{selectedSubject.title}"
            </h2>

            {filesLoading ? (
              <div className="text-center py-8">Loading PPT files...</div>
            ) : files.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                No PPT files found in this section
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {files.map((file: PptFile) => (
                  <Card 
                    key={file.id} 
                    className="hover:shadow-lg transition-shadow border-indigo-200"
                  >
                    <CardContent className="p-6">
                      <div className="flex items-center mb-4">
                        <Presentation className="h-8 w-8 text-indigo-600 mr-3" />
                        <div className="flex-1">
                          <h3 className="font-semibold text-lg">{file.title}</h3>
                          <p className="text-sm text-gray-600 mt-1">{file.description}</p>
                        </div>
                      </div>

                      <div className="flex items-center justify-between mb-4">
                        <Badge variant="secondary" className="text-xs">
                          {file.platform}
                        </Badge>
                        <Badge variant="outline" className="text-xs">
                          {file.pageCount} slides
                        </Badge>
                      </div>

                      <Button 
                        onClick={() => openPptViewer(file)}
                        className="w-full bg-indigo-600 hover:bg-indigo-700"
                      >
                        <Eye className="h-4 w-4 mr-2" />
                        View PPT
                      </Button>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/admin/file-management.t.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Search, FileText, Presentation, Eye, Trash2, Folder, Calendar, Filter, FolderTree, ChevronRight, Bot, Zap } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import FilePreview from "@/components/file-preview";

interface FileWithStructure {
  id: string;
  title: string;
  description?: string | null;
  platform: string;
  embedUrl?: string;
  fileUrl?: string;
  pageCount?: number;
  slideCount?: number;
  fileSize?: number;
  thumbnailUrl?: string;
  createdAt: string;
  batchTitle?: string | null;
  courseTitle?: string | null;
  subjectTitle?: string | null;
  batchId?: string;
  courseId?: string;
  subjectId?: string;
  type: "pdf" | "ppt";
}

export default function FileManagement() {
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedFile, setSelectedFile] = useState<FileWithStructure | null>(null);
  const [activeTab, setActiveTab] = useState("all");
  const [selectedCategory, setSelectedCategory] = useState<string>("all");
  const [selectedFolder, setSelectedFolder] = useState<string>("all");

  // Fetch all PDF files with structure
  const { data: pdfFiles = [], isLoading: pdfLoading } = useQuery<FileWithStructure[]>({
    queryKey: ["/api/admin/pdf/all-files"],
  });

  // Fetch all PPT files with structure
  const { data: pptFiles = [], isLoading: pptLoading } = useQuery<FileWithStructure[]>({
    queryKey: ["/api/admin/ppt/all-files"],
  });

  // Combine all files
  const allFiles = [
    ...pdfFiles.map(file => ({ ...file, type: "pdf" as const })),
    ...pptFiles.map(file => ({ ...file, type: "ppt" as const }))
  ];

  // Get unique categories and folders for filters
  const categories = Array.from(new Set(allFiles.map(file => file.batchTitle).filter(Boolean)));
  const folders = Array.from(new Set(allFiles.map(file => file.courseTitle).filter(Boolean)));

  // Filter files based on search term, tab, category and folder
  const filteredFiles = allFiles.filter(file => {
    const matchesSearch = 
      file.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      file.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      file.batchTitle?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      file.courseTitle?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      file.subjectTitle?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesTab = 
      activeTab === "all" || 
      (activeTab === "pdf" && file.type === "pdf") ||
      (activeTab === "ppt" && file.type === "ppt");

    const matchesCategory = 
      selectedCategory === "all" || 
      file.batchTitle === selectedCategory;

    const matchesFolder = 
      selectedFolder === "all" || 
      file.courseTitle === selectedFolder;

    return matchesSearch && matchesTab && matchesCategory && matchesFolder;
  });

  // Group files by folder structure for organized display
  const groupedFiles = filteredFiles.reduce((acc, file) => {
    const categoryKey = file.batchTitle || "Unknown Category";
    const folderKey = file.courseTitle || "Unknown Folder";
    const sectionKey = file.subjectTitle || "Main Folder";

    if (!acc[categoryKey]) acc[categoryKey] = {};
    if (!acc[categoryKey][folderKey]) acc[categoryKey][folderKey] = {};
    if (!acc[categoryKey][folderKey][sectionKey]) acc[categoryKey][folderKey][sectionKey] = [];

    acc[categoryKey][folderKey][sectionKey].push(file);
    return acc;
  }, {} as Record<string, Record<string, Record<string, (FileWithStructure & { type: "pdf" | "ppt" })[]>>>);

  // Delete mutations
  const deletePdfFile = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest(`/api/pdf/files/${id}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/pdf/all-files"] });
      toast({ title: "Success", description: "PDF file deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PDF file", variant: "destructive" });
    }
  });

  const deletePptFile = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest(`/api/ppt/files/${id}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/ppt/all-files"] });
      toast({ title: "Success", description: "PPT file deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete PPT file", variant: "destructive" });
    }
  });

  const handleDeleteFile = (file: FileWithStructure & { type: "pdf" | "ppt" }) => {
    if (file.type === "pdf") {
      deletePdfFile.mutate(file.id);
    } else {
      deletePptFile.mutate(file.id);
    }
  };

  const getFileLocation = (file: FileWithStructure) => {
    const parts = [];
    if (file.batchTitle) parts.push(file.batchTitle);
    if (file.courseTitle) parts.push(file.courseTitle);
    if (file.subjectTitle) parts.push(file.subjectTitle);
    return parts.join(" / ") || "Unknown Location";
  };

  if (pdfLoading || pptLoading) {
    return (
      <div className="p-6">
        <div className="text-center">Loading...</div>
      </div>
    );
  }

  return (
    <div className="w-full max-w-full overflow-hidden space-y-6">
      <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-4">
        <h1 className="text-2xl lg:text-3xl font-bold text-gray-900">File Management Center</h1>
        <div className="flex flex-wrap items-center gap-4 text-sm text-gray-600">
          <div className="flex items-center gap-2">
            <FileText className="h-4 w-4" />
            <span>Total PDF: {pdfFiles.length}</span>
          </div>
          <div className="flex items-center gap-2">
            <Presentation className="h-4 w-4" />
            <span>Total PPT: {pptFiles.length}</span>
          </div>
          <div className="flex items-center gap-2">
            <Folder className="h-4 w-4" />
            <span>Total Files: {allFiles.length}</span>
          </div>
        </div>
      </div>

      {/* Search and Filter Controls */}
      <div className="space-y-4">
        <div className="flex flex-col sm:flex-row items-stretch sm:items-center gap-4">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <Input
              placeholder="Search files, folders or descriptions..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>
          <Tabs value={activeTab} onValueChange={setActiveTab} className="w-auto">
            <TabsList>
              <TabsTrigger value="all">All Files</TabsTrigger>
              <TabsTrigger value="pdf">PDF Files</TabsTrigger>
              <TabsTrigger value="ppt">PPT Files</TabsTrigger>
            </TabsList>
          </Tabs>
        </div>

        {/* Category and Folder Filters */}
        <div className="flex flex-col sm:flex-row items-stretch sm:items-center gap-4 flex-wrap">
          <div className="flex items-center gap-2">
            <Filter className="h-4 w-4 text-gray-500" />
            <span className="text-sm font-medium text-gray-700">Filters:</span>
          </div>
          <Select value={selectedCategory} onValueChange={setSelectedCategory}>
            <SelectTrigger className="w-full sm:w-48">
              <SelectValue placeholder="Select Category" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Categories</SelectItem>
              {categories.map(category => (
                <SelectItem key={category} value={category || ""}>{category}</SelectItem>
              ))}
            </SelectContent>
          </Select>
          <Select value={selectedFolder} onValueChange={setSelectedFolder}>
            <SelectTrigger className="w-full sm:w-48">
              <SelectValue placeholder="Select Folder" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Folders</SelectItem>
              {folders.map(folder => (
                <SelectItem key={folder} value={folder || ""}>{folder}</SelectItem>
              ))}
            </SelectContent>
          </Select>
          {(selectedCategory !== "all" || selectedFolder !== "all") && (
            <Button 
              variant="outline" 
              size="sm" 
              onClick={() => {
                setSelectedCategory("all");
                setSelectedFolder("all");
              }}
            >
              Clear Filters
            </Button>
          )}
        </div>
      </div>

      {/* Organized File Display */}
      <div className="space-y-6">
        {Object.entries(groupedFiles).map(([categoryName, categoryFolders]) => (
          <div key={categoryName} className="border rounded-lg p-4 bg-gray-50">
            <h2 className="text-lg font-semibold text-gray-800 mb-4 flex items-center gap-2">
              <FolderTree className="h-5 w-5 text-blue-600" />
              {categoryName}
              <Badge variant="secondary" className="ml-2">
                {Object.values(categoryFolders).reduce((acc, folderSections) => 
                  acc + Object.values(folderSections).reduce((secAcc, files) => secAcc + files.length, 0), 0
                )} Files
              </Badge>
            </h2>

            <div className="space-y-4">
              {Object.entries(categoryFolders).map(([folderName, folderSections]) => (
                <div key={folderName} className="bg-white rounded-lg p-4 border">
                  <h3 className="text-md font-medium text-gray-700 mb-3 flex items-center gap-2">
                    <Folder className="h-4 w-4 text-orange-600" />
                    {folderName}
                    <ChevronRight className="h-3 w-3 text-gray-400" />
                    <Badge variant="outline" className="ml-1">
                      {Object.values(folderSections).reduce((acc, files) => acc + files.length, 0)} Files
                    </Badge>
                  </h3>

                  <div className="space-y-3">
                    {Object.entries(folderSections).map(([sectionName, files]) => (
                      <div key={sectionName} className="border-l-2 border-gray-200 pl-4">
                        <h4 className="text-sm font-medium text-gray-600 mb-2 flex items-center gap-2">
                          <Folder className="h-3 w-3 text-purple-600" />
                          {sectionName}
                          <Badge variant="outline">
                            {files.length}
                          </Badge>
                        </h4>

                        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
                          {files.map((file) => (
                            <FilePreview
                              key={file.id}
                              id={file.id}
                              title={file.title}
                              description={file.description || undefined}
                              platform={file.platform}
                              fileUrl={file.fileUrl || ""}
                              embedUrl={file.embedUrl || undefined}
                              pageCount={file.pageCount}
                              slideCount={file.slideCount}
                              thumbnailUrl={file.thumbnailUrl}
                              fileSize={file.fileSize}
                              type={file.type}
                              onView={() => {
                                // Use dedicated PDF viewer instead of modal
                                window.open(`/pdf-viewer/${file.id}`, '_blank');
                              }}
                              onDelete={() => handleDeleteFile(file)}
                            />
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {/* File Detail Modal */}
      {selectedFile && (
        <Dialog open={!!selectedFile} onOpenChange={() => setSelectedFile(null)}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                {selectedFile.type === 'pdf' ? (
                  <FileText className="h-5 w-5 text-red-600" />
                ) : (
                  <Presentation className="h-5 w-5 text-blue-600" />
                )}
                {selectedFile.title}
              </DialogTitle>
            </DialogHeader>

            <div className="space-y-6">
              {/* Metadata Section */}
              <div className="bg-gradient-to-r from-blue-50 to-purple-50 p-4 rounded-lg border">
                <h3 className="font-semibold text-gray-800 mb-3 flex items-center gap-2">
                  <Bot className="h-4 w-4 text-blue-600" />
                  Auto-Detected Metadata
                  <Zap className="h-3 w-3 text-yellow-500" />
                </h3>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                  <div>
                    <span className="text-gray-600">Platform:</span>
                    <Badge className="ml-2 text-xs">
                      {selectedFile.platform === 'google_drive' ? 'Google Drive' :
                       selectedFile.platform === 'onedrive' ? 'OneDrive' :
                       selectedFile.platform === 'dropbox' ? 'Dropbox' :
                       selectedFile.platform === 'scribd' ? 'Scribd' :
                       selectedFile.platform === 'canva' ? 'Canva' :
                       selectedFile.platform}
                    </Badge>
                  </div>
                  <div>
                    <span className="text-gray-600">{selectedFile.type === 'pdf' ? 'Pages:' : 'Slides:'}</span>
                    <span className="ml-2 font-semibold text-green-600">
                      {selectedFile.type === 'pdf' ? (selectedFile.pageCount || 'Auto-detected') : (selectedFile.slideCount || 'Auto-detected')}
                    </span>
                  </div>
                  <div>
                    <span className="text-gray-600">File Size:</span>
                    <span className="ml-2 font-medium">
                      {selectedFile.fileSize ? 
                        `${(selectedFile.fileSize / (1024 * 1024)).toFixed(1)} MB` : 
                        'Detecting...'
                      }
                    </span>
                  </div>
                  <div>
                    <span className="text-gray-600">Type:</span>
                    <span className="ml-2 font-medium uppercase">{selectedFile.type}</span>
                  </div>
                </div>
              </div>

              {/* File Location */}
              <div>
                <h3 className="font-semibold text-gray-800 mb-2">File Location</h3>
                <p className="text-sm text-gray-600">{getFileLocation(selectedFile)}</p>
              </div>

              {/* Description */}
              {selectedFile.description && (
                <div>
                  <h3 className="font-semibold text-gray-800 mb-2">Description</h3>
                  <p className="text-sm text-gray-600">{selectedFile.description}</p>
                </div>
              )}

              {/* Action Buttons */}
              <div className="flex flex-wrap gap-3 pt-4 border-t">
                <Button
                  onClick={() => window.open(`/pdf-viewer/${selectedFile.id}`, '_blank')}
                  className="flex-1 sm:flex-none"
                >
                  <Eye className="h-4 w-4 mr-2" />
                  View in PDF Viewer
                </Button>
                <Button
                  variant="outline"
                  onClick={() => window.open(selectedFile.fileUrl, '_blank')}
                  className="flex-1 sm:flex-none"
                >
                  <FileText className="h-4 w-4 mr-2" />
                  Open Original
                </Button>
                <Button
                  variant="destructive"
                  onClick={() => {
                    handleDeleteFile(selectedFile);
                    setSelectedFile(null);
                  }}
                  className="flex-1 sm:flex-none"
                >
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}

      {/* Empty State */}
      {filteredFiles.length === 0 && (
        <div className="text-center py-12">
          <div className="mx-auto w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center mb-4">
            <Search className="h-12 w-12 text-gray-400" />
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No files found</h3>
          <p className="text-gray-500 max-w-md mx-auto">
            {searchTerm || selectedCategory !== "all" || selectedFolder !== "all" ? 
              "Try adjusting your search or filter criteria." :
              "Upload your first PDF or PPT file to get started with the file management system."
            }
          </p>
        </div>
      )}
    </div>
  );
}

