import React, { useState, useRef, useEffect } from 'react';
import { Button } from "@/components/ui/button";
import { Slider } from "@/components/ui/slider";
import { SkipBack, SkipForward, Play, Pause, Volume2, VolumeX } from "lucide-react";

interface DirectYouTubePlayerProps {
  videoId: string;
  className?: string;
}

export default function DirectYouTubePlayer({ videoId, className = "" }: DirectYouTubePlayerProps) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(75);
  const [isMuted, setIsMuted] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const [localTime, setLocalTime] = useState(0);
  const [showControls, setShowControls] = useState(false);

  const playerRef = useRef<any>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const intervalRef = useRef<NodeJS.Timeout>();
  const playerInitialized = useRef(false);

  // YouTube Player API initialization
  useEffect(() => {
    // Load YouTube API script
    if (!window.YT) {
      const script = document.createElement('script');
      script.src = 'https://www.youtube.com/iframe_api';
      script.async = true;
      document.head.appendChild(script);

      window.onYouTubeIframeAPIReady = initializePlayer;
    } else {
      initializePlayer();
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
      if (playerRef.current && typeof playerRef.current.destroy === 'function') {
        playerRef.current.destroy();
      }
    };
  }, [videoId]);

  const initializePlayer = () => {
    if (playerInitialized.current || !containerRef.current) return;

    playerRef.current = new window.YT.Player(containerRef.current, {
      height: '100%',
      width: '100%',
      videoId: videoId,
      playerVars: {
        controls: 0,
        modestbranding: 1,
        rel: 0,
        showinfo: 0,
        fs: 0,
        disablekb: 1,
        enablejsapi: 1,
        origin: window.location.origin
      },
      events: {
        onReady: handlePlayerReady,
        onStateChange: handleStateChange,
        onError: handleError
      }
    });

    playerInitialized.current = true;
  };

  const handlePlayerReady = (event: any) => {
    console.log('Player ready');
    const player = event.target;

    // Get initial video data
    setDuration(player.getDuration());
    setCurrentTime(player.getCurrentTime());

    // Start time tracking
    intervalRef.current = setInterval(() => {
      if (player && typeof player.getCurrentTime === 'function') {
        const time = player.getCurrentTime();
        setCurrentTime(time);
        if (!isDragging) {
          setLocalTime(time);
        }
      }
    }, 100); // Very frequent updates for smooth progress
  };

  const handleStateChange = (event: any) => {
    const state = event.data;
    setIsPlaying(state === window.YT.PlayerState.PLAYING);

    if (state === window.YT.PlayerState.PLAYING) {
      console.log('Video playing');
    } else if (state === window.YT.PlayerState.PAUSED) {
      console.log('Video paused');
    }
  };

  const handleError = (event: any) => {
    console.error('YouTube Player Error:', event.data);
  };

  // Direct control functions
  const handleSeek = (time: number) => {
    if (!playerRef.current || typeof playerRef.current.seekTo !== 'function') return;

    const seekTime = Math.max(0, time);
    console.log(`Seeking to: ${seekTime}s`);

    try {
      playerRef.current.seekTo(seekTime, true);
      setCurrentTime(seekTime);
      setLocalTime(seekTime);

      // Force update after seek
      setTimeout(() => {
        if (playerRef.current && typeof playerRef.current.getCurrentTime === 'function') {
          const actualTime = playerRef.current.getCurrentTime();
          setCurrentTime(actualTime);
          setLocalTime(actualTime);
        }
      }, 200);
    } catch (error) {
      console.error('Seek error:', error);
    }
  };

  const handleSkipForward = () => {
    const newTime = currentTime + 10;
    console.log(`Skip Forward: ${currentTime} -> ${newTime}`);
    handleSeek(newTime);
  };

  const handleSkipBackward = () => {
    const newTime = Math.max(0, currentTime - 10);
    console.log(`Skip Backward: ${currentTime} -> ${newTime}`);
    handleSeek(newTime);
  };

  const handlePlayPause = () => {
    if (!playerRef.current) return;

    try {
      if (isPlaying) {
        playerRef.current.pauseVideo();
      } else {
        playerRef.current.playVideo();
      }
    } catch (error) {
      console.error('Play/Pause error:', error);
    }
  };

  const handleVolumeChange = (value: number[]) => {
    const newVolume = value[0];
    setVolume(newVolume);
    setIsMuted(newVolume === 0);

    if (playerRef.current && typeof playerRef.current.setVolume === 'function') {
      playerRef.current.setVolume(newVolume);
    }
  };

  const toggleMute = () => {
    if (!playerRef.current) return;

    try {
      if (isMuted) {
        playerRef.current.unMute();
        setVolume(75);
      } else {
        playerRef.current.mute();
        setVolume(0);
      }
      setIsMuted(!isMuted);
    } catch (error) {
      console.error('Mute/Unmute error:', error);
    }
  };

  // Slider handling
  const handleSeekChange = (value: number[]) => {
    const newTime = value[0];
    setLocalTime(newTime);
    setIsDragging(true);
  };

  const handleSeekCommit = (value: number[]) => {
    const newTime = value[0];
    handleSeek(newTime);
    setIsDragging(false);
  };

  // Time formatting
  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  // Update local time when not dragging
  useEffect(() => {
    if (!isDragging) {
      setLocalTime(currentTime);
    }
  }, [currentTime, isDragging]);

  return (
    <div 
      className={`relative bg-black video-player-container ${className}`}
      style={{ paddingBottom: "56.25%", height: 0 }}
      onMouseEnter={() => setShowControls(true)}
      onMouseLeave={() => setShowControls(false)}
    >
      {/* YouTube player container */}
      <div 
        ref={containerRef}
        className="absolute top-0 left-0 w-full h-full"
      />

      {/* Custom controls overlay */}
      {showControls && (
        <div className="absolute bottom-4 left-4 right-4 z-[1000]">
          <div className="bg-black/90 backdrop-blur-sm text-white transition-all duration-300 animate-in slide-in-from-bottom-2">
            {/* Progress bar */}
            <div className="px-3 py-1">
              <div className="flex items-center space-x-2 text-xs">
                <span className="min-w-[35px] text-[10px]">{formatTime(localTime)}</span>
                <div className="flex-1">
                  <Slider
                    value={[localTime]}
                    max={Math.max(duration, 100)}
                    step={0.1}
                    onValueChange={handleSeekChange}
                    onValueCommit={handleSeekCommit}
                    className="w-full h-1"
                  />
                </div>
                <span className="min-w-[35px] text-[10px]">{formatTime(duration)}</span>
              </div>
            </div>

            {/* Control buttons */}
            <div className="flex items-center justify-between px-3 py-1">
              <div className="flex items-center space-x-1">
                {/* Skip backward */}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleSkipBackward}
                  className="text-white hover:bg-white/20 p-1 h-6 w-6"
                  title="Skip backward 10 seconds"
                >
                  <SkipBack className="h-3 w-3" />
                </Button>

                {/* Play/Pause */}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handlePlayPause}
                  className="text-white hover:bg-white/20 p-1 h-7 w-7"
                  title={isPlaying ? "Pause" : "Play"}
                >
                  {isPlaying ? <Pause className="h-4 w-4" /> : <Play className="h-4 w-4" />}
                </Button>

                {/* Skip forward */}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleSkipForward}
                  className="text-white hover:bg-white/20 p-1 h-6 w-6"
                  title="Skip forward 10 seconds"
                >
                  <SkipForward className="h-3 w-3" />
                </Button>
              </div>

              {/* Volume controls */}
              <div className="flex items-center space-x-1">
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={toggleMute}
                  className="text-white hover:bg-white/20 p-1 h-6 w-6"
                  title={isMuted ? "Unmute" : "Mute"}
                >
                  {isMuted ? <VolumeX className="h-3 w-3" /> : <Volume2 className="h-3 w-3" />}
                </Button>
                <div className="w-12">
                  <Slider
                    value={[isMuted ? 0 : volume]}
                    max={100}
                    step={1}
                    onValueChange={handleVolumeChange}
                    className="w-full h-1"
                  />
                </div>

                {/* Time indicator */}
                <div className="text-[9px] text-gray-400 min-w-[30px] text-right">
                  {duration > 0 && (
                    <span>{Math.round((localTime / duration) * 100)}%</span>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// Global YouTube API ready handler
declare global {
  interface Window {
    YT: any;
    onYouTubeIframeAPIReady: () => void;
  }
}

client/src/components/enhanced-file-form.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import FilePreview from "./file-preview";

interface EnhancedFileFormProps {
  subjectId: string;
  batchId?: string;
  courseId?: string;
  onClose: () => void;
  fileType: 'pdf' | 'ppt';
}

export function EnhancedFileForm({ subjectId, batchId, courseId, onClose, fileType }: EnhancedFileFormProps) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [fileUrl, setFileUrl] = useState("");
  const [embedUrl, setEmbedUrl] = useState("");
  const [platform, setPlatform] = useState(fileType === 'pdf' ? "google_drive" : "google_slides");
  const [htmlCode, setHtmlCode] = useState("");
  const [pageCount, setPageCount] = useState("");
  const [slideCount, setSlideCount] = useState("");
  const [fileUpload, setFileUpload] = useState<File | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      // If we have a file upload, use FormData to send the file
      if (data.fileUpload) {
        const formData = new FormData();
        formData.append('file', data.fileUpload);
        formData.append('title', data.title);
        formData.append('description', data.description || '');
        formData.append('platform', data.platform);
        formData.append('subjectId', data.subjectId);
        if (data.batchId) formData.append('batchId', data.batchId);
        if (data.courseId) formData.append('courseId', data.courseId);

        const response = await fetch(`/api/${fileType}/upload`, {
          method: 'POST',
          body: formData
        });

        if (!response.ok) {
          throw new Error(`Upload failed: ${response.statusText}`);
        }

        return response.json();
      } else {
        return await apiRequest(`/api/${fileType}/files`, "POST", data);
      }
    },
    onSuccess: () => {
      // Invalidate ALL related caches to ensure fresh data
      queryClient.invalidateQueries({ queryKey: [`/api/${fileType}/batches`] });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/${fileType}/all-files`] });

      if (subjectId && subjectId !== "batch-direct" && subjectId !== "course-direct") {
        queryClient.invalidateQueries({ queryKey: [`/api/${fileType}/subjects`, subjectId, "files"] });
      }
      if (batchId) {
        queryClient.invalidateQueries({ queryKey: [`/api/${fileType}/batches`, batchId, "courses"] });
      }
      if (courseId) {
        queryClient.invalidateQueries({ queryKey: [`/api/${fileType}/courses`, courseId, "subjects"] });
      }

      toast({ title: "Success", description: `${fileType.toUpperCase()} file uploaded successfully with automatic metadata detection!` });
      onClose();
    },
    onError: (error: Error) => {
      console.error(`Error creating ${fileType} file:`, error);
      toast({ 
        title: "Error", 
        description: `Failed to add ${fileType.toUpperCase()} file. Please try again.`,
        variant: "destructive"
      });
    },
  });

  const getPlatformOptions = () => {
    if (fileType === 'pdf') {
      return [
        { value: "google_drive", label: "Google Drive" },
        { value: "onedrive", label: "OneDrive" },
        { value: "dropbox", label: "Dropbox" },
        { value: "scribd", label: "Scribd" },
        { value: "canva", label: "Canva" },
        { value: "upload", label: "File Upload" },
        { value: "html_embed", label: "HTML Embed Code" }
      ];
    } else {
      return [
        { value: "google_slides", label: "Google Slides" },
        { value: "powerpoint_online", label: "PowerPoint Online" },
        { value: "canva", label: "Canva" },
        { value: "scribd", label: "Scribd" },
        { value: "upload", label: "File Upload" },
        { value: "html_embed", label: "HTML Embed Code" }
      ];
    }
  };

  const getPlaceholder = () => {
    if (platform === 'scribd') {
      return 'https://www.scribd.com/document/...';
    } else if (platform === 'canva') {
      if (fileType === 'pdf') {
        return 'https://www.canva.com/design/.../view';
      } else {
        return 'https://www.canva.com/design/...';
      }
    } else if (platform === 'google_drive') {
      return 'https://drive.google.com/file/d/...';
    } else if (platform === 'onedrive') {
      return 'https://1drv.ms/...';
    } else if (platform === 'dropbox') {
      return 'https://www.dropbox.com/s/...';
    } else if (platform === 'google_slides') {
      return 'https://docs.google.com/presentation/d/...';
    } else if (platform === 'powerpoint_online') {
      return 'https://onedrive.live.com/embed?resid=...';
    } else if (platform === 'upload') {
      return 'Upload file URL...';
    } else if (fileType === 'pdf') {
      return 'https://drive.google.com/file/d/...';
    } else {
      return 'https://docs.google.com/presentation/d/...';
    }
  };

  const getEmbedPlaceholder = () => {
    if (platform === 'scribd') {
      return 'https://www.scribd.com/embeds/...';
    } else if (platform === 'canva') {
      if (fileType === 'pdf') {
        return 'https://www.canva.com/design/.../view?embed';
      } else {
        return 'https://www.canva.com/design/.../edit?embed';
      }
    } else if (platform === 'google_drive') {
      return 'https://drive.google.com/file/d/.../preview';
    } else if (platform === 'onedrive') {
      return 'https://1drv.ms/.../embed';
    } else if (platform === 'dropbox') {
      return 'https://www.dropbox.com/s/.../preview';
    } else if (platform === 'google_slides') {
      return 'https://docs.google.com/presentation/d/.../embed';
    } else if (platform === 'powerpoint_online') {
      return 'https://onedrive.live.com/embed?resid=...';
    } else if (fileType === 'pdf') {
      return 'https://drive.google.com/file/d/.../preview';
    } else {
      return 'https://docs.google.com/presentation/d/.../embed';
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const fileData: any = {
      title,
      description,
      fileUrl: platform === 'html_embed' ? htmlCode : fileUrl,
      embedUrl,
      platform,
    };

    // For file uploads, include the file
    if (platform === 'upload' && fileUpload) {
      fileData.fileUpload = fileUpload;
    } else {
      // For URL-based uploads, include page count if manually specified
      if (fileType === 'pdf') {
        fileData.pageCount = parseInt(pageCount) || undefined; // Let server auto-detect if not specified
      } else {
        fileData.slideCount = parseInt(slideCount) || undefined; // Let server auto-detect if not specified
      }
    }

    // Add appropriate ID based on context
    fileData.subjectId = subjectId;

    // Always include hierarchy IDs when available
    if (batchId) {
      fileData.batchId = batchId;
    }
    if (courseId) {
      fileData.courseId = courseId;
    }

    createMutation.mutate(fileData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <Label htmlFor="title">File Title</Label>
          <Input
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder={`e.g., ${fileType === 'pdf' ? 'Calculus Basics' : 'Introduction Slides'}`}
            required
          />
        </div>
        <div>
          <Label htmlFor="platform">Platform</Label>
          <Select value={platform} onValueChange={setPlatform}>
            <SelectTrigger>
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              {getPlatformOptions().map((option) => (
                <SelectItem key={option.value} value={option.value}>
                  {option.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <div>
        <Label htmlFor="description">Description</Label>
        <Textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder={`Brief description of this ${fileType.toUpperCase()} file`}
        />
      </div>

      {platform === 'html_embed' ? (
        <div>
          <Label htmlFor="htmlCode">HTML Embed Code</Label>
          <Textarea
            id="htmlCode"
            value={htmlCode}
            onChange={(e) => setHtmlCode(e.target.value)}
            placeholder='<iframe src="https://www.scribd.com/embeds/..." width="100%" height="500"></iframe>'
            className="min-h-[100px]"
            required
          />
        </div>
      ) : platform === 'upload' ? (
        <div>
          <Label htmlFor="fileUpload">Upload {fileType.toUpperCase()} File</Label>
          <Input
            id="fileUpload"
            type="file"
            accept={fileType === 'pdf' ? '.pdf' : '.ppt,.pptx'}
            onChange={(e) => setFileUpload(e.target.files?.[0] || null)}
            required
          />
        </div>
      ) : (
        <div>
          <Label htmlFor="fileUrl">
            {platform === 'scribd' ? 'Scribd Document URL' : 
             platform === 'canva' ? 'Canva Document URL' : 'File URL'}
          </Label>
          <Input
            id="fileUrl"
            value={fileUrl}
            onChange={(e) => setFileUrl(e.target.value)}
            placeholder={getPlaceholder()}
            required
          />
        </div>
      )}

      {platform !== 'html_embed' && platform !== 'upload' && (
        <div>
          <Label htmlFor="embedUrl">Embed URL</Label>
          <Input
            id="embedUrl"
            value={embedUrl}
            onChange={(e) => setEmbedUrl(e.target.value)}
            placeholder={getEmbedPlaceholder()}
            required={platform !== 'upload'}
          />
        </div>
      )}

      {/* Preview Section */}
      {((fileUrl && platform !== 'html_embed' && platform !== 'upload') || 
        (embedUrl && platform !== 'html_embed' && platform !== 'upload') ||
        (fileUpload && platform === 'upload')) && (
        <div>
          <Label>Preview</Label>
          <FilePreview
            url={platform === 'upload' ? (fileUpload ? URL.createObjectURL(fileUpload) : '') : (embedUrl || fileUrl)}
            title={title || 'File Preview'}
            platform={platform}
            showControls={true}
          />
        </div>
      )}

      <div>
        <Label htmlFor={fileType === 'pdf' ? 'pageCount' : 'slideCount'}>
          {fileType === 'pdf' ? 'Page Count' : 'Slide Count'} 
          <span className="text-sm text-gray-500 ml-2">(auto-detected when possible)</span>
        </Label>
        <Input
          id={fileType === 'pdf' ? 'pageCount' : 'slideCount'}
          type="number"
          value={fileType === 'pdf' ? pageCount : slideCount}
          onChange={(e) => {
            if (fileType === 'pdf') {
              setPageCount(e.target.value);
            } else {
              setSlideCount(e.target.value);
            }
          }}
          placeholder={`Leave empty for auto-detection (e.g., ${fileType === 'pdf' ? '25' : '15'})`}
          min="1"
        />
        <p className="text-xs text-gray-500 mt-1">
          System will automatically detect {fileType === 'pdf' ? 'page' : 'slide'} count from supported platforms (Google Drive, OneDrive, etc.)
        </p>
      </div>

      <div className="flex justify-end space-x-2">
        <Button type="button" variant="outline" onClick={onClose}>
          Cancel
        </Button>
        <Button type="submit" disabled={createMutation.isPending}>
          {createMutation.isPending ? "Adding..." : `Add ${fileType.toUpperCase()}`}
        </Button>
      </div>
    </form>
  );
}

client/src/components/pdf-viewer.tsx.content:
import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";
import { 
  ChevronLeft, 
  ChevronRight, 
  ZoomIn, 
  ZoomOut, 
  Maximize, 
  Minimize,
  RotateCcw,
  RotateCw,
  Download,
  BookOpen,
  Eye
} from "lucide-react";

interface PDFViewerProps {
  pdfUrl: string;
  title: string;
  pageCount?: number;
  onPageChange?: (page: number) => void;
}

export default function PDFViewer({ pdfUrl, title, pageCount = 0, onPageChange }: PDFViewerProps) {
  const [currentPage, setCurrentPage] = useState(1);
  const [zoom, setZoom] = useState(100);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [rotation, setRotation] = useState(0);
  const [showBookAnimation, setShowBookAnimation] = useState(true);
  const [isFlipping, setIsFlipping] = useState(false);
  const [jumpToPage, setJumpToPage] = useState('');
  const [viewMode, setViewMode] = useState<'book' | 'normal'>('book');

  const viewerRef = useRef<HTMLDivElement>(null);
  const iframeRef = useRef<HTMLIFrameElement>(null);

  // Book-like animation styles
  const bookStyles = `
    .pdf-book-container {
      perspective: 1200px;
      transform-style: preserve-3d;
      transition: all 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    }

    .pdf-page {
      transform-style: preserve-3d;
      transition: all 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94);
      box-shadow: 0 8px 25px rgba(0,0,0,0.3);
      border-radius: 8px;
      overflow: hidden;
    }

    .pdf-page.flipping {
      transform: rotateY(-15deg) scale(0.95);
      animation: pageFlip 0.8s ease-in-out;
    }

    @keyframes pageFlip {
      0% { transform: rotateY(0deg) scale(1); }
      25% { transform: rotateY(-90deg) scale(0.8); }
      75% { transform: rotateY(-90deg) scale(0.8); }
      100% { transform: rotateY(0deg) scale(1); }
    }

    .book-spine {
      position: absolute;
      left: -2px;
      top: 0;
      width: 4px;
      height: 100%;
      background: linear-gradient(to bottom, #8B4513, #A0522D, #8B4513);
      border-radius: 2px;
      box-shadow: inset 0 0 2px rgba(0,0,0,0.3);
    }

    .page-curl {
      position: absolute;
      top: 0;
      right: 0;
      width: 20px;
      height: 20px;
      background: linear-gradient(135deg, rgba(255,255,255,0.8), rgba(240,240,240,0.6));
      clip-path: polygon(0 0, 100% 0, 0 100%);
      transition: all 0.3s ease;
    }

    .pdf-page:hover .page-curl {
      width: 40px;
      height: 40px;
    }

    .book-cover {
      background: linear-gradient(135deg, #2C1810, #4A2C1A, #6B4226);
      color: white;
      padding: 20px;
      text-align: center;
      border-radius: 8px;
      box-shadow: 0 10px 30px rgba(0,0,0,0.4);
      border: 2px solid #8B4513;
    }
  `;

  // Page navigation with animation
  const goToPage = (page: number) => {
    if (page < 1 || page > pageCount) return;

    setIsFlipping(true);
    setTimeout(() => {
      setCurrentPage(page);
      onPageChange?.(page);
      setIsFlipping(false);
    }, 400);
  };

  const nextPage = () => {
    if (currentPage < pageCount) {
      goToPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      goToPage(currentPage - 1);      
    }
  };

  const handleJumpToPage = () => {
    const page = parseInt(jumpToPage);
    if (page && page >= 1 && page <= pageCount) {
      goToPage(page);
      setJumpToPage('');
    }
  };

  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      viewerRef.current?.requestFullscreen();
      setIsFullscreen(true);
    } else {
      document.exitFullscreen();
      setIsFullscreen(false);
    }
  };

  const handleZoom = (newZoom: number[]) => {
    setZoom(newZoom[0]);
  };

  const rotate = (direction: 'left' | 'right') => {
    const newRotation = direction === 'left' 
      ? rotation - 90 
      : rotation + 90;
    setRotation(newRotation);
  };

  // Embed URL construction for different platforms
  const getEmbedUrl = () => {
    if (pdfUrl.includes('drive.google.com')) {
      // Google Drive PDF
      const fileId = pdfUrl.match(/\/d\/([a-zA-Z0-9-_]+)/)?.[1];
      return `https://drive.google.com/file/d/${fileId}/preview`;
    } else if (pdfUrl.includes('onedrive.live.com') || pdfUrl.includes('1drv.ms')) {
      // OneDrive PDF  
      return pdfUrl.replace('/view', '/embed');
    } else if (pdfUrl.includes('mozilla.github.io/pdf.js')) {
      // Already a PDF.js viewer URL
      return pdfUrl;
    } else if (pdfUrl.includes('docs.google.com/viewer')) {
      // Already a Google Docs viewer URL
      return pdfUrl;
    } else {
      // For direct PDF URLs, use PDF.js viewer for better rendering
      return `https://mozilla.github.io/pdf.js/web/viewer.html?file=${encodeURIComponent(pdfUrl)}`;
    }
  };

  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = bookStyles;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return (
    <div className="flex flex-col lg:flex-row h-screen bg-gray-100 dark:bg-gray-900">
      <style dangerouslySetInnerHTML={{ __html: bookStyles }} />

      {/* PDF Viewer Area - A4 Vertical Format */}
      <div className="flex-1 flex items-center justify-center p-2 lg:p-4">
        <div 
          ref={viewerRef}
          className={`bg-white dark:bg-gray-800 shadow-2xl border border-gray-200 dark:border-gray-700 ${isFullscreen ? 'fixed inset-2 lg:inset-4 z-50' : 'w-full max-w-4xl'}`}
          style={{ 
            aspectRatio: '210/297', // A4 ratio
            height: isFullscreen ? 'calc(100vh - 1rem)' : typeof window !== 'undefined' && window.innerWidth < 1024 ? '70vh' : '85vh'
          }}
        >
          {/* Header Controls */}
          <div className="flex items-center justify-between bg-gray-50 dark:bg-gray-700 p-2 lg:p-3 border-b border-gray-200 dark:border-gray-600">
            <div className="flex items-center space-x-2 lg:space-x-3 flex-1 min-w-0">
              <BookOpen className="h-4 w-4 lg:h-5 lg:w-5 text-blue-600 flex-shrink-0" />
              <h3 className="font-semibold text-sm lg:text-base text-gray-800 dark:text-white truncate">{title}</h3>
            </div>

            <div className="flex items-center space-x-1 lg:space-x-2 flex-shrink-0">
              {/* Rotation Controls - Hidden on mobile */}
              <Button variant="ghost" size="sm" onClick={() => rotate('left')} className="hidden sm:flex">
                <RotateCcw className="h-4 w-4" />
              </Button>
              <Button variant="ghost" size="sm" onClick={() => rotate('right')} className="hidden sm:flex">
                <RotateCw className="h-4 w-4" />
              </Button>

              {/* Fullscreen Toggle */}
              <Button variant="ghost" size="sm" onClick={toggleFullscreen}>
                {isFullscreen ? <Minimize className="h-4 w-4" /> : <Maximize className="h-4 w-4" />}
              </Button>

              {/* Download Button */}
              <Button variant="ghost" size="sm" asChild>
                <a href={pdfUrl} download target="_blank" rel="noopener noreferrer">
                  <Download className="h-4 w-4" />
                </a>
              </Button>
            </div>
          </div>

          {/* PDF Content Area */}
          <div className="relative w-full h-full bg-white dark:bg-gray-800">
            <iframe
              ref={iframeRef}
              src={getEmbedUrl()}
              className="w-full h-full border-0"
              style={{
                transform: `rotate(${rotation}deg) scale(${zoom / 100})`,
                transformOrigin: 'center center',
                transition: 'transform 0.3s ease'
              }}
              title={title}
              allow="autoplay"
            />
          </div>
        </div>
      </div>

      {/* Controls Panel - Mobile Bottom, Desktop Right */}
      <div className="w-full lg:w-80 bg-white dark:bg-gray-800 border-t lg:border-t-0 lg:border-l border-gray-200 dark:border-gray-700 p-2 lg:p-4 overflow-y-auto max-h-60 lg:max-h-none">
        <Card className="shadow-lg">
          <CardContent className="p-3 lg:p-6">
            <h4 className="font-semibold text-base lg:text-lg mb-3 lg:mb-4 text-gray-800 dark:text-white">PDF Controls</h4>

            {/* Page Navigation */}
            <div className="space-y-3 lg:space-y-4">
              <div>
                <label className="text-xs lg:text-sm font-medium text-gray-600 dark:text-gray-300 mb-2 block">
                  Page Navigation
                </label>
                <div className="flex items-center justify-between mb-2 lg:mb-3">
                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={prevPage}
                    disabled={currentPage <= 1}
                    className="flex-1 mr-1 lg:mr-2 text-xs lg:text-sm"
                  >
                    <ChevronLeft className="h-3 w-3 lg:h-4 lg:w-4 lg:mr-1" />
                    <span className="hidden sm:inline">Previous</span>
                  </Button>

                  <span className="text-xs lg:text-sm font-medium px-2 lg:px-3 py-1 lg:py-2 bg-gray-100 dark:bg-gray-700 rounded text-center min-w-[60px] lg:min-w-[80px]">
                    {currentPage} / {pageCount}
                  </span>

                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={nextPage}
                    disabled={currentPage >= pageCount}
                    className="flex-1 ml-1 lg:ml-2 text-xs lg:text-sm"
                  >
                    <span className="hidden sm:inline">Next</span>
                    <ChevronRight className="h-3 w-3 lg:h-4 lg:w-4 lg:ml-1" />
                  </Button>
                </div>

                {/* Jump to Page - Compact on mobile */}
                <div className="flex items-center space-x-1 lg:space-x-2">
                  <Input
                    type="number"
                    placeholder="Page"
                    value={jumpToPage}
                    onChange={(e) => setJumpToPage(e.target.value)}
                    className="flex-1 text-xs lg:text-sm h-8 lg:h-auto"
                    min={1}
                    max={pageCount}
                  />
                  <Button size="sm" onClick={handleJumpToPage} className="text-xs lg:text-sm">
                    Go
                  </Button>
                </div>
              </div>

              {/* Page Progress */}
              <div>
                <label className="text-sm font-medium text-gray-600 dark:text-gray-300 mb-2 block">
                  Progress
                </label>
                <Slider
                  value={[currentPage]}
                  max={pageCount}
                  min={1}
                  step={1}
                  onValueChange={(value) => goToPage(value[0])}
                  className="w-full mb-2"
                />
                <div className="text-xs text-gray-500 dark:text-gray-400 text-center">
                  {Math.round((currentPage / pageCount) * 100)}% Complete
                </div>
              </div>

              {/* Zoom Controls */}
              <div>
                <label className="text-sm font-medium text-gray-600 dark:text-gray-300 mb-2 block">
                  Zoom Level
                </label>
                <div className="flex items-center justify-between mb-3">
                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={() => setZoom(Math.max(25, zoom - 25))}
                  >
                    <ZoomOut className="h-4 w-4" />
                  </Button>
                  <span className="text-sm font-medium px-3 py-2 bg-gray-100 dark:bg-gray-700 rounded text-center min-w-[70px]">
                    {zoom}%
                  </span>
                  <Button 
                    variant="outline" 
                    size="sm" 
                    onClick={() => setZoom(Math.min(200, zoom + 25))}
                  >
                    <ZoomIn className="h-4 w-4" />
                  </Button>
                </div>

                <Slider
                  value={[zoom]}
                  max={200}
                  min={25}
                  step={5}
                  onValueChange={handleZoom}
                  className="w-full"
                />
              </div>

              {/* File Information */}
              <div className="border-t border-gray-200 dark:border-gray-600 pt-4">
                <label className="text-sm font-medium text-gray-600 dark:text-gray-300 mb-2 block">
                  File Information
                </label>
                <div className="space-y-1 text-sm text-gray-500 dark:text-gray-400">
                  <div>Title: {title}</div>
                  <div>Pages: {pageCount}</div>
                  <div>Current: Page {currentPage}</div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/ppt-viewer.tsx.content:
import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Slider } from "@/components/ui/slider";
import { Input } from "@/components/ui/input";
import { 
  ChevronLeft, 
  ChevronRight, 
  ZoomIn, 
  ZoomOut, 
  Maximize, 
  Minimize,
  RotateCcw,
  RotateCw,
  Download,
  Presentation,
  Play,
  Pause,
  Grid3X3,
  Eye
} from "lucide-react";

interface PPTViewerProps {
  pptUrl: string;
  title: string;
  slideCount?: number;
  onSlideChange?: (slide: number) => void;
}

export default function PPTViewer({ pptUrl, title, slideCount = 0, onSlideChange }: PPTViewerProps) {
  const [currentSlide, setCurrentSlide] = useState(1);
  const [zoom, setZoom] = useState(100);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [rotation, setRotation] = useState(0);
  const [isPresenting, setIsPresenting] = useState(false);
  const [showSlideTransition, setShowSlideTransition] = useState(true);
  const [jumpToSlide, setJumpToSlide] = useState('');
  const [viewMode, setViewMode] = useState<'presentation' | 'grid' | 'normal'>('presentation');
  const [autoPlay, setAutoPlay] = useState(false);
  const [slideInterval, setSlideInterval] = useState(5); // seconds

  const viewerRef = useRef<HTMLDivElement>(null);
  const iframeRef = useRef<HTMLIFrameElement>(null);
  const autoPlayRef = useRef<NodeJS.Timeout>();

  // Presentation-like animation styles
  const presentationStyles = `
    .ppt-presentation-container {
      perspective: 1500px;
      transform-style: preserve-3d;
      transition: all 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94);
    }

    .ppt-slide {
      transform-style: preserve-3d;
      transition: all 1s cubic-bezier(0.25, 0.46, 0.45, 0.94);
      box-shadow: 0 15px 35px rgba(0,0,0,0.4);
      border-radius: 12px;
      overflow: hidden;
      border: 3px solid #1e40af;
    }

    .ppt-slide.transitioning {
      transform: rotateX(-10deg) scale(0.9);
      animation: slideTransition 1s ease-in-out;
    }

    @keyframes slideTransition {
      0% { transform: rotateX(0deg) scale(1); opacity: 1; }
      25% { transform: rotateX(-90deg) scale(0.7); opacity: 0.3; }
      75% { transform: rotateX(-90deg) scale(0.7); opacity: 0.3; }
      100% { transform: rotateX(0deg) scale(1); opacity: 1; }
    }

    .presentation-frame {
      background: linear-gradient(135deg, #1e3a8a, #3b82f6, #1e40af);
      padding: 8px;
      border-radius: 16px;
      box-shadow: 0 20px 40px rgba(30, 64, 175, 0.3);
    }

    .slide-corner {
      position: absolute;
      bottom: 0;
      right: 0;
      width: 30px;
      height: 30px;
      background: linear-gradient(135deg, rgba(59, 130, 246, 0.8), rgba(30, 64, 175, 0.6));
      clip-path: polygon(100% 0, 100% 100%, 0 100%);
      transition: all 0.3s ease;
    }

    .ppt-slide:hover .slide-corner {
      width: 50px;
      height: 50px;
    }

    .presentation-border {
      border: 4px solid #3b82f6;
      border-radius: 12px;
      background: rgba(59, 130, 246, 0.1);
      backdrop-filter: blur(10px);
    }

    .slide-thumbnail {
      width: 120px;
      height: 90px;
      border-radius: 8px;
      overflow: hidden;
      cursor: pointer;
      transition: all 0.3s ease;
      border: 2px solid transparent;
    }

    .slide-thumbnail:hover {
      transform: scale(1.05);
      border-color: #3b82f6;
      box-shadow: 0 8px 25px rgba(59, 130, 246, 0.3);
    }

    .slide-thumbnail.active {
      border-color: #1e40af;
      box-shadow: 0 0 20px rgba(30, 64, 175, 0.5);
    }

    .grid-view {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
      gap: 16px;
      padding: 20px;
    }
  `;

  // Slide navigation with animation
  const goToSlide = (slide: number) => {
    if (slide < 1 || slide > slideCount) return;

    setShowSlideTransition(true);
    setTimeout(() => {
      setCurrentSlide(slide);
      onSlideChange?.(slide);
      setShowSlideTransition(false);
    }, 500);
  };

  const nextSlide = () => {
    if (currentSlide < slideCount) {
      goToSlide(currentSlide + 1);
    }
  };

  const prevSlide = () => {
    if (currentSlide > 1) {
      goToSlide(currentSlide - 1);      
    }
  };

  const handleJumpToSlide = () => {
    const slide = parseInt(jumpToSlide);
    if (slide && slide >= 1 && slide <= slideCount) {
      goToSlide(slide);
      setJumpToSlide('');
    }
  };

  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      viewerRef.current?.requestFullscreen();
      setIsFullscreen(true);
    } else {
      document.exitFullscreen();
      setIsFullscreen(false);
    }
  };

  const handleZoom = (newZoom: number[]) => {
    setZoom(newZoom[0]);
  };

  const rotate = (direction: 'left' | 'right') => {
    const newRotation = direction === 'left' 
      ? rotation - 90 
      : rotation + 90;
    setRotation(newRotation);
  };

  const toggleAutoPlay = () => {
    if (autoPlay) {
      clearInterval(autoPlayRef.current);
      setAutoPlay(false);
    } else {
      setAutoPlay(true);
      autoPlayRef.current = setInterval(() => {
        setCurrentSlide(prev => {
          const next = prev + 1;
          if (next > slideCount) {
            clearInterval(autoPlayRef.current);
            setAutoPlay(false);
            return prev;
          }
          return next;
        });
      }, slideInterval * 1000);
    }
  };

  // Embed URL construction for different platforms
  const getEmbedUrl = () => {
    if (pptUrl.includes('docs.google.com/presentation')) {
      // Google Slides
      const presentationId = pptUrl.match(/\/presentation\/d\/([a-zA-Z0-9-_]+)/)?.[1];
      return `https://docs.google.com/presentation/d/${presentationId}/embed?start=false&loop=false&delayms=3000`;
    } else if (pptUrl.includes('onedrive.live.com') || pptUrl.includes('1drv.ms')) {
      // OneDrive PowerPoint
      return pptUrl.replace('/view', '/embed');
    } else if (pptUrl.includes('office.com')) {
      // Office 365 PowerPoint
      return pptUrl.replace('/view', '/embed');
    } else {
      // Direct PPT URL or SlideShare
      return pptUrl;
    }
  };

  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = presentationStyles;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
      if (autoPlayRef.current) {
        clearInterval(autoPlayRef.current);
      }
    };
  }, []);

  useEffect(() => {
    onSlideChange?.(currentSlide);
  }, [currentSlide, onSlideChange]);

  return (
    <div 
      ref={viewerRef}
      className={`relative w-full h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-blue-900 ${isFullscreen ? 'fixed inset-0 z-50' : ''}`}
    >
      <style dangerouslySetInnerHTML={{ __html: presentationStyles }} />

      {/* Header Controls */}
      <div className="absolute top-4 left-4 right-4 z-10 flex items-center justify-between bg-white/90 dark:bg-gray-800/90 backdrop-blur-sm rounded-lg p-3 shadow-lg">
        <div className="flex items-center space-x-3">
          <Presentation className="h-5 w-5 text-blue-600" />
          <h3 className="font-semibold text-gray-800 dark:text-white">{title}</h3>
        </div>

        <div className="flex items-center space-x-2">
          {/* View Mode Toggle */}
          <Button
            variant={viewMode === 'presentation' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setViewMode('presentation')}
          >
            <Presentation className="h-4 w-4" />
          </Button>
          <Button
            variant={viewMode === 'grid' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setViewMode('grid')}
          >
            <Grid3X3 className="h-4 w-4" />
          </Button>
          <Button
            variant={viewMode === 'normal' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setViewMode('normal')}
          >
            <Eye className="h-4 w-4" />
          </Button>

          {/* Auto Play Toggle */}
          <Button 
            variant={autoPlay ? 'default' : 'outline'} 
            size="sm" 
            onClick={toggleAutoPlay}
          >
            {autoPlay ? <Pause className="h-4 w-4" /> : <Play className="h-4 w-4" />}
          </Button>

          {/* Rotation Controls */}
          <Button variant="outline" size="sm" onClick={() => rotate('left')}>
            <RotateCcw className="h-4 w-4" />
          </Button>
          <Button variant="outline" size="sm" onClick={() => rotate('right')}>
            <RotateCw className="h-4 w-4" />
          </Button>

          {/* Fullscreen Toggle */}
          <Button variant="outline" size="sm" onClick={toggleFullscreen}>
            {isFullscreen ? <Minimize className="h-4 w-4" /> : <Maximize className="h-4 w-4" />}
          </Button>

          {/* Download Button */}
          <Button variant="outline" size="sm" asChild>
            <a href={pptUrl} download target="_blank" rel="noopener noreferrer">
              <Download className="h-4 w-4" />
            </a>
          </Button>
        </div>
      </div>

      {/* Main PPT Viewer Area */}
      <div className="pt-20 pb-20 px-4 h-full">
        {viewMode === 'grid' ? (
          <div className="grid-view h-full overflow-y-auto">
            {Array.from({ length: slideCount }, (_, i) => i + 1).map((slideNum) => (
              <div
                key={slideNum}
                className={`slide-thumbnail ${slideNum === currentSlide ? 'active' : ''}`}
                onClick={() => goToSlide(slideNum)}
              >
                <div className="w-full h-full bg-white rounded-lg shadow-md flex items-center justify-center">
                  <span className="text-sm font-medium text-gray-600">Slide {slideNum}</span>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className={`ppt-presentation-container h-full ${viewMode === 'presentation' ? 'presentation-view' : ''}`}>
            <div className={`ppt-slide h-full relative ${showSlideTransition ? 'transitioning' : ''}`}>
              {viewMode === 'presentation' && (
                <div className="presentation-frame h-full">
                  <iframe
                    ref={iframeRef}
                    src={getEmbedUrl()}
                    className="w-full h-full border-0 rounded-lg"
                    style={{
                      transform: `rotate(${rotation}deg) scale(${zoom / 100})`,
                      transformOrigin: 'center center',
                      transition: 'transform 0.3s ease'
                    }}
                    title={title}
                    allow="autoplay"
                  />
                </div>
              )}

              {viewMode === 'normal' && (
                <iframe
                  ref={iframeRef}
                  src={getEmbedUrl()}
                  className="w-full h-full border-0 rounded-lg"
                  style={{
                    transform: `rotate(${rotation}deg) scale(${zoom / 100})`,
                    transformOrigin: 'center center',
                    transition: 'transform 0.3s ease'
                  }}
                  title={title}
                  allow="autoplay"
                />
              )}

              {viewMode === 'presentation' && <div className="slide-corner"></div>}
            </div>
          </div>
        )}
      </div>

      {/* Bottom Controls Panel */}
      <div className="absolute bottom-4 left-4 right-4 z-10">
        <Card className="bg-white/95 dark:bg-gray-800/95 backdrop-blur-sm shadow-xl">
          <CardContent className="p-4">
            {/* Slide Navigation */}
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center space-x-2">
                <Button 
                  variant="outline" 
                  size="sm" 
                  onClick={prevSlide}
                  disabled={currentSlide <= 1}
                >
                  <ChevronLeft className="h-4 w-4" />
                </Button>

                <span className="text-sm font-medium px-3">
                  {currentSlide} / {slideCount}
                </span>

                <Button 
                  variant="outline" 
                  size="sm" 
                  onClick={nextSlide}
                  disabled={currentSlide >= slideCount}
                >
                  <ChevronRight className="h-4 w-4" />
                </Button>
              </div>

              {/* Jump to Slide */}
              <div className="flex items-center space-x-2">
                <Input
                  type="number"
                  placeholder="Slide"
                  value={jumpToSlide}
                  onChange={(e) => setJumpToSlide(e.target.value)}
                  className="w-20 h-8"
                  min={1}
                  max={slideCount}
                />
                <Button size="sm" onClick={handleJumpToSlide}>
                  Go
                </Button>
              </div>
            </div>

            {/* Slide Progress Slider */}
            <div className="mb-4">
              <Slider
                value={[currentSlide]}
                max={slideCount}
                min={1}
                step={1}
                onValueChange={(value) => goToSlide(value[0])}
                className="w-full"
              />
            </div>

            {/* Zoom Controls */}
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <Button 
                  variant="outline" 
                  size="sm" 
                  onClick={() => setZoom(Math.max(25, zoom - 25))}
                >
                  <ZoomOut className="h-4 w-4" />
                </Button>
                <span className="text-sm font-medium min-w-[60px] text-center">
                  {zoom}%
                </span>
                <Button 
                  variant="outline" 
                  size="sm" 
                  onClick={() => setZoom(Math.min(200, zoom + 25))}
                >
                  <ZoomIn className="h-4 w-4" />
                </Button>
              </div>

              {/* Zoom Slider */}
              <div className="flex-1 mx-4">
                <Slider
                  value={[zoom]}
                  max={200}
                  min={25}
                  step={5}
                  onValueChange={handleZoom}
                  className="w-full"
                />
              </div>

              {/* Progress Info */}
              <div className="text-sm text-gray-500 dark:text-gray-400">
                {Math.round((currentSlide / slideCount) * 100)}% Complete
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

