import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  FileText, 
  Presentation, 
  Search, 
  FolderOpen, 
  Calendar,
  ExternalLink,
  Download,
  Eye,
  Trash2
} from "lucide-react";
import type { PdfFile, PptFile } from "@shared/schema";

export default function FilePreview() {
  const [searchTerm, setSearchTerm] = useState("");
  const [activeTab, setActiveTab] = useState("pdf");

  // Fetch all PDF files with folder structure
  const { data: allPdfFiles, isLoading: pdfLoading } = useQuery({
    queryKey: ["/api/admin/pdf/all-files"],
  });

  // Fetch all PPT files with folder structure  
  const { data: allPptFiles, isLoading: pptLoading } = useQuery({
    queryKey: ["/api/admin/ppt/all-files"],
  });

  const filteredPdfFiles = allPdfFiles?.filter((file: any) =>
    file.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    file.batchTitle?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    file.courseTitle?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    file.subjectTitle?.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  const filteredPptFiles = allPptFiles?.filter((file: any) =>
    file.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    file.batchTitle?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    file.courseTitle?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    file.subjectTitle?.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  const getPlatformBadgeColor = (platform: string) => {
    switch (platform) {
      case 'google_drive': return 'bg-blue-100 text-blue-800';
      case 'onedrive': return 'bg-green-100 text-green-800';
      case 'dropbox': return 'bg-purple-100 text-purple-800';
      case 'scribd': return 'bg-orange-100 text-orange-800';
      case 'canva': return 'bg-pink-100 text-pink-800';
      case 'upload': return 'bg-gray-100 text-gray-800';
      case 'html_embed': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const FileCard = ({ file, type }: { file: any; type: 'pdf' | 'ppt' }) => (
    <Card className="hover:shadow-md transition-shadow">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-center space-x-2">
            {type === 'pdf' ? (
              <FileText className="h-5 w-5 text-amber-600" />
            ) : (
              <Presentation className="h-5 w-5 text-blue-600" />
            )}
            <CardTitle className="text-sm font-medium">{file.title}</CardTitle>
          </div>
          <Badge className={`text-xs ${getPlatformBadgeColor(file.platform)}`}>
            {file.platform?.replace('_', ' ')}
          </Badge>
        </div>
      </CardHeader>
      <CardContent className="space-y-3">
        {/* Folder Structure */}
        <div className="text-xs text-gray-600 space-y-1">
          <div className="flex items-center space-x-1">
            <FolderOpen className="h-3 w-3" />
            <span className="font-medium">{file.batchTitle}</span>
          </div>
          {file.courseTitle && (
            <div className="flex items-center space-x-1 ml-4">
              <FolderOpen className="h-3 w-3" />
              <span className="font-medium">{file.courseTitle}</span>
            </div>
          )}
          {file.subjectTitle && (
            <div className="flex items-center space-x-1 ml-8">
              <FolderOpen className="h-3 w-3" />
              <span className="font-medium">{file.subjectTitle}</span>
            </div>
          )}
        </div>

        {/* File Details */}
        <div className="text-xs space-y-1">
          {type === 'pdf' && file.pageCount && (
            <div>Pages: {file.pageCount}</div>
          )}
          {type === 'ppt' && file.slideCount && (
            <div>Slides: {file.slideCount}</div>
          )}
          <div className="flex items-center space-x-1 text-gray-500">
            <Calendar className="h-3 w-3" />
            <span>{new Date(file.createdAt).toLocaleDateString()}</span>
          </div>
        </div>

        {/* Description */}
        {file.description && (
          <p className="text-xs text-gray-600 line-clamp-2">{file.description}</p>
        )}

        {/* Action Buttons */}
        <div className="flex space-x-2 pt-2">
          {file.embedUrl && (
            <Button size="sm" variant="outline" className="text-xs h-7">
              <Eye className="h-3 w-3 mr-1" />
              Preview
            </Button>
          )}
          {file.fileUrl && (
            <Button size="sm" variant="outline" className="text-xs h-7">
              <ExternalLink className="h-3 w-3 mr-1" />
              Open
            </Button>
          )}
        </div>

        {/* URLs */}
        <div className="text-xs space-y-1">
          {file.embedUrl && (
            <div className="truncate">
              <span className="font-medium">Embed: </span>
              <span className="text-blue-600">{file.embedUrl}</span>
            </div>
          )}
          {file.fileUrl && (
            <div className="truncate">
              <span className="font-medium">File: </span>
              <span className="text-green-600">{file.fileUrl}</span>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">File Preview & Management</h1>
          <p className="text-gray-600 mt-1">View all uploaded files and their locations</p>
        </div>
      </div>

      {/* Search */}
      <div className="relative">
        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
        <Input
          placeholder="Search files, folders, or descriptions..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="pl-10"
        />
      </div>

      {/* Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList>
          <TabsTrigger value="pdf" className="flex items-center space-x-2">
            <FileText className="h-4 w-4" />
            <span>PDF Files ({filteredPdfFiles.length})</span>
          </TabsTrigger>
          <TabsTrigger value="ppt" className="flex items-center space-x-2">
            <Presentation className="h-4 w-4" />
            <span>PPT Files ({filteredPptFiles.length})</span>
          </TabsTrigger>
        </TabsList>

        <TabsContent value="pdf" className="space-y-4">
          {pdfLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {Array.from({ length: 6 }).map((_, i) => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-4">
                    <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                    <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : filteredPdfFiles.length === 0 ? (
            <div className="text-center py-12">
              <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No PDF files found</h3>
              <p className="text-gray-500">
                {searchTerm ? 'Try adjusting your search terms' : 'Start by adding some PDF files'}
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {filteredPdfFiles.map((file: any) => (
                <FileCard key={file.id} file={file} type="pdf" />
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="ppt" className="space-y-4">
          {pptLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {Array.from({ length: 6 }).map((_, i) => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-4">
                    <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                    <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : filteredPptFiles.length === 0 ? (
            <div className="text-center py-12">
              <Presentation className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No PPT files found</h3>
              <p className="text-gray-500">
                {searchTerm ? 'Try adjusting your search terms' : 'Start by adding some PPT files'}
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {filteredPptFiles.map((file: any) => (
                <FileCard key={file.id} file={file} type="ppt" />
              ))}
            </div>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

