import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { Folder, Plus, Edit, Trash2, ArrowLeft, FileText, Eye, FolderOpen, Upload, FolderPlus } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { EnhancedFileForm } from "@/components/enhanced-file-form";

// Types for the flexible system
interface PdfCategory {
  id: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PdfSubCategory {
  id: string;
  title: string;
  description: string | null;
  batchId: string; // Parent category
  createdAt: string;
  updatedAt: string;
}

interface PdfSubSubCategory {
  id: string;
  title: string;
  description: string | null;
  courseId: string; // Parent sub-category
  batchId: string; // Parent category
  createdAt: string;
  updatedAt: string;
}

interface PdfFile {
  id: string;
  title: string;
  description: string | null;
  subjectId: string | null; // Sub-sub-category level
  courseId: string | null; // Sub-category level  
  batchId: string; // Category level
  fileUrl: string;
  embedUrl: string;
  platform: string;
  pageCount: number;
  createdAt: string;
  updatedAt: string;
}

type ViewLevel = "categories" | "sub-categories" | "sub-sub-categories" | "all-files";

export default function PDFManagementNew() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Navigation states
  const [currentView, setCurrentView] = useState<ViewLevel>("categories");
  const [selectedCategory, setSelectedCategory] = useState<PdfCategory | null>(null);
  const [selectedSubCategory, setSelectedSubCategory] = useState<PdfSubCategory | null>(null);
  const [selectedSubSubCategory, setSelectedSubSubCategory] = useState<PdfSubSubCategory | null>(null);

  // Dialog states
  const [showCategoryDialog, setShowCategoryDialog] = useState(false);
  const [showSubCategoryDialog, setShowSubCategoryDialog] = useState(false);
  const [showSubSubCategoryDialog, setShowSubSubCategoryDialog] = useState(false);
  const [showFileDialog, setShowFileDialog] = useState(false);

  // Edit states
  const [editingCategory, setEditingCategory] = useState<PdfCategory | null>(null);
  const [editingSubCategory, setEditingSubCategory] = useState<PdfSubCategory | null>(null);
  const [editingSubSubCategory, setEditingSubSubCategory] = useState<PdfSubSubCategory | null>(null);

  // Queries for each level
  const { data: categories = [], isLoading: categoriesLoading } = useQuery<PdfCategory[]>({
    queryKey: ["/api/pdf/batches"],
    enabled: true
  });

  const { data: subCategories = [], isLoading: subCategoriesLoading } = useQuery<PdfSubCategory[]>({
    queryKey: ["/api/pdf/batches", selectedCategory?.id, "courses"],
    enabled: !!selectedCategory && (currentView === "sub-categories" || currentView === "sub-sub-categories" || currentView === "all-files")
  });

  const { data: subSubCategories = [], isLoading: subSubCategoriesLoading } = useQuery<PdfSubSubCategory[]>({
    queryKey: ["/api/pdf/courses", selectedSubCategory?.id, "subjects"],
    enabled: !!selectedSubCategory && (currentView === "sub-sub-categories" || currentView === "all-files")
  });

  // Files at each level
  const { data: categoryFiles = [] } = useQuery<PdfFile[]>({
    queryKey: ["/api/pdf/batches", selectedCategory?.id, "files"],
    enabled: !!selectedCategory && (currentView === "sub-categories" || currentView === "all-files")
  });

  const { data: subCategoryFiles = [] } = useQuery<PdfFile[]>({
    queryKey: ["/api/pdf/courses", selectedSubCategory?.id, "files"],
    enabled: !!selectedSubCategory && (currentView === "sub-sub-categories" || currentView === "all-files")
  });

  const { data: subSubCategoryFiles = [] } = useQuery<PdfFile[]>({
    queryKey: ["/api/pdf/subjects", selectedSubSubCategory?.id, "files"],
    enabled: !!selectedSubSubCategory && currentView === "all-files"
  });

  // Navigation handlers
  const navigateToSubCategories = (category: PdfCategory) => {
    setSelectedCategory(category);
    setCurrentView("sub-categories");
  };

  const navigateToSubSubCategories = (subCategory: PdfSubCategory) => {
    setSelectedSubCategory(subCategory);
    setCurrentView("sub-sub-categories");
  };

  const navigateToAllFiles = (subSubCategory: PdfSubSubCategory) => {
    setSelectedSubSubCategory(subSubCategory);
    setCurrentView("all-files");
  };

  const goBack = () => {
    switch (currentView) {
      case "sub-categories":
        setCurrentView("categories");
        setSelectedCategory(null);
        break;
      case "sub-sub-categories":
        setCurrentView("sub-categories");
        setSelectedSubCategory(null);
        break;
      case "all-files":
        setCurrentView("sub-sub-categories");
        setSelectedSubSubCategory(null);
        break;
    }
  };

  // Create/Edit/Delete mutations for Categories
  const createCategory = useMutation({
    mutationFn: async (data: { title: string; description: string }) => {
      return await apiRequest("/api/pdf/batches", "POST", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches"] });
      setShowCategoryDialog(false);
      toast({ title: "Success", description: "Category created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create category", variant: "destructive" });
    }
  });

  const createSubCategory = useMutation({
    mutationFn: async (data: { title: string; description: string; batchId: string }) => {
      return await apiRequest("/api/pdf/courses", "POST", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches", selectedCategory?.id, "courses"] });
      setShowSubCategoryDialog(false);
      toast({ title: "Success", description: "Sub-category created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create sub-category", variant: "destructive" });
    }
  });

  const createSubSubCategory = useMutation({
    mutationFn: async (data: { title: string; description: string; courseId: string; batchId: string }) => {
      return await apiRequest("/api/pdf/subjects", "POST", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/courses", selectedSubCategory?.id, "subjects"] });
      setShowSubSubCategoryDialog(false);
      toast({ title: "Success", description: "Sub-sub-category created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create sub-sub-category", variant: "destructive" });
    }
  });

  // Delete mutations
  const deleteCategory = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest(`/api/pdf/batches/${id}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches"] });
      toast({ title: "Success", description: "Category deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete category", variant: "destructive" });
    }
  });

  const deleteSubCategory = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest(`/api/pdf/courses/${id}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches", selectedCategory?.id, "courses"] });
      toast({ title: "Success", description: "Sub-category deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete sub-category", variant: "destructive" });
    }
  });

  const deleteSubSubCategory = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest(`/api/pdf/subjects/${id}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/pdf/courses", selectedSubCategory?.id, "subjects"] });
      toast({ title: "Success", description: "Sub-sub-category deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete sub-sub-category", variant: "destructive" });
    }
  });

  // Form components
  const CategoryForm = ({ category, onClose }: { category?: PdfCategory; onClose: () => void }) => {
    const [title, setTitle] = useState(category?.title || "");
    const [description, setDescription] = useState(category?.description || "");

    const handleSubmit = (e: React.FormEvent) => {
      e.preventDefault();
      createCategory.mutate({ title, description });
    };

    return (
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <Label htmlFor="title">Category Name</Label>
          <Input
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter category name"
            required
          />
        </div>
        <div>
          <Label htmlFor="description">Description</Label>
          <Textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter category description"
            rows={3}
          />
        </div>
        <div className="flex gap-2 justify-end">
          <Button type="button" variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button type="submit" disabled={createCategory.isPending}>
            {createCategory.isPending ? "Creating..." : "Create Category"}
          </Button>
        </div>
      </form>
    );
  };

  const SubCategoryForm = ({ onClose }: { onClose: () => void }) => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");

    const handleSubmit = (e: React.FormEvent) => {
      e.preventDefault();
      createSubCategory.mutate({ 
        title, 
        description, 
        batchId: selectedCategory!.id 
      });
    };

    return (
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <Label htmlFor="title">Sub-category Name</Label>
          <Input
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter sub-category name"
            required
          />
        </div>
        <div>
          <Label htmlFor="description">Description</Label>
          <Textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter sub-category description"
            rows={3}
          />
        </div>
        <div className="flex gap-2 justify-end">
          <Button type="button" variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button type="submit" disabled={createSubCategory.isPending}>
            {createSubCategory.isPending ? "Creating..." : "Create Sub-category"}
          </Button>
        </div>
      </form>
    );
  };

  const SubSubCategoryForm = ({ onClose }: { onClose: () => void }) => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");

    const handleSubmit = (e: React.FormEvent) => {
      e.preventDefault();
      createSubSubCategory.mutate({ 
        title, 
        description, 
        courseId: selectedSubCategory!.id,
        batchId: selectedCategory!.id
      });
    };

    return (
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <Label htmlFor="title">Sub-sub-category Name</Label>
          <Input
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter sub-sub-category name"
            required
          />
        </div>
        <div>
          <Label htmlFor="description">Description</Label>
          <Textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter sub-sub-category description"
            rows={3}
          />
        </div>
        <div className="flex gap-2 justify-end">
          <Button type="button" variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button type="submit" disabled={createSubSubCategory.isPending}>
            {createSubSubCategory.isPending ? "Creating..." : "Create Sub-sub-category"}
          </Button>
        </div>
      </form>
    );
  };

  // File upload dialog helper
  const FileUploadDialog = ({ level, title, subjectId }: { level: string; title: string; subjectId: string }) => (
    <Dialog open={showFileDialog} onOpenChange={setShowFileDialog}>
      <DialogTrigger asChild>
        <Button variant="outline" className="bg-green-50 hover:bg-green-100 text-green-700 border-green-200">
          <Upload className="h-4 w-4 mr-2" />
          Add PDF File
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle>Add PDF File - {title}</DialogTitle>
          <DialogDescription>Upload a PDF file to {level} level</DialogDescription>
        </DialogHeader>
        <EnhancedFileForm 
          batchId={selectedCategory?.id}
          courseId={selectedSubCategory?.id}
          subjectId={subjectId}
          fileType="pdf" 
          onClose={() => {
            setShowFileDialog(false);
            // Invalidate all relevant queries
            queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches"] });
            if (selectedCategory) {
              queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches", selectedCategory.id, "files"] });
              queryClient.invalidateQueries({ queryKey: ["/api/pdf/batches", selectedCategory.id, "courses"] });
            }
            if (selectedSubCategory) {
              queryClient.invalidateQueries({ queryKey: ["/api/pdf/courses", selectedSubCategory.id, "files"] });
              queryClient.invalidateQueries({ queryKey: ["/api/pdf/courses", selectedSubCategory.id, "subjects"] });
            }
            if (selectedSubSubCategory) {
              queryClient.invalidateQueries({ queryKey: ["/api/pdf/subjects", selectedSubSubCategory.id, "files"] });
            }
          }}
        />
      </DialogContent>
    </Dialog>
  );

  // Render different views
  const renderCategoriesView = () => (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">PDF Categories</h1>
        <Dialog open={showCategoryDialog} onOpenChange={setShowCategoryDialog}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Create New Category
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New Category</DialogTitle>
              <DialogDescription>Create a main category for organizing PDF files</DialogDescription>
            </DialogHeader>
            <CategoryForm onClose={() => setShowCategoryDialog(false)} />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {categories.map((category: PdfCategory) => (
          <Card key={category.id} className="hover:shadow-lg transition-shadow cursor-pointer">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <Folder className="h-8 w-8 text-blue-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{category.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{category.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingCategory(category);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteCategory.mutate(category.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToSubCategories(category)}
                  className="w-full bg-blue-100 hover:bg-blue-200 text-blue-800"
                  variant="ghost"
                >
                  <FolderOpen className="h-4 w-4 mr-2" />
                  Open Category
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderSubCategoriesView = () => (
    <div>
      <div className="flex items-center mb-4">
        <Button variant="ghost" onClick={goBack} className="mr-4">
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Categories
        </Button>
        <h2 className="text-xl font-semibold flex items-center">
          <Folder className="h-6 w-6 mr-3 text-blue-600" />
          {selectedCategory?.title} - Sub-categories & Files
        </h2>
      </div>

      <div className="flex gap-2 mb-6">
        <Dialog open={showSubCategoryDialog} onOpenChange={setShowSubCategoryDialog}>
          <DialogTrigger asChild>
            <Button className="bg-blue-600 hover:bg-blue-700">
              <FolderPlus className="h-4 w-4 mr-2" />
              Create Sub-category
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create Sub-category</DialogTitle>
              <DialogDescription>Create a sub-category in {selectedCategory?.title}</DialogDescription>
            </DialogHeader>
            <SubCategoryForm onClose={() => setShowSubCategoryDialog(false)} />
          </DialogContent>
        </Dialog>

        <FileUploadDialog 
          level="Category" 
          title={selectedCategory?.title || ""} 
          subjectId="category-direct"
        />
      </div>

      {/* Files at Category Level */}
      {categoryFiles.length > 0 && (
        <div className="mb-8">
          <h3 className="text-lg font-semibold mb-4 flex items-center">
            <FileText className="h-5 w-5 mr-2 text-green-600" />
            PDF Files in "{selectedCategory?.title}" Category
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
            {categoryFiles.map((file: PdfFile) => (
              <Card key={file.id} className="hover:shadow-lg transition-shadow border-green-200">
                <CardContent className="p-4">
                  <div className="flex items-start justify-between mb-3">
                    <div className="flex items-center flex-1">
                      <FileText className="h-6 w-6 text-green-600 mr-3" />
                      <div className="flex-1">
                        <h4 className="font-medium text-sm">{file.title}</h4>
                        <p className="text-xs text-gray-500 mt-1">{file.description}</p>
                      </div>
                    </div>
                    <div className="flex gap-1">
                      <Button size="sm" variant="ghost">
                        <Edit className="h-3 w-3" />
                      </Button>
                      <Button size="sm" variant="ghost" className="text-red-600">
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </div>
                  </div>

                  <div className="flex items-center justify-between">
                    <Badge variant="outline" className="text-xs">
                      {file.platform}
                    </Badge>
                    <Badge variant="secondary" className="text-xs">
                      {file.pageCount || 0} pages
                    </Badge>
                    <Button size="sm" variant="ghost">
                      <Eye className="h-3 w-3" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Sub-categories */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {subCategories.map((subCategory: PdfSubCategory) => (
          <Card key={subCategory.id} className="hover:shadow-lg transition-shadow cursor-pointer border-purple-200">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <FolderOpen className="h-8 w-8 text-purple-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{subCategory.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{subCategory.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingSubCategory(subCategory);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteSubCategory.mutate(subCategory.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToSubSubCategories(subCategory)}
                  className="w-full bg-purple-100 hover:bg-purple-200 text-purple-800"
                  variant="ghost"
                >
                  <FolderOpen className="h-4 w-4 mr-2" />
                  Open Sub-category
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderSubSubCategoriesView = () => (
    <div>
      <div className="flex items-center mb-4">
        <Button variant="ghost" onClick={goBack} className="mr-4">
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Sub-categories
        </Button>
        <h2 className="text-xl font-semibold flex items-center">
          <Folder className="h-6 w-6 mr-3 text-purple-600" />
          {selectedSubCategory?.title} - Sub-sub-categories & Files
        </h2>
      </div>

      <div className="flex gap-2 mb-6">
        <Dialog open={showSubSubCategoryDialog} onOpenChange={setShowSubSubCategoryDialog}>
          <DialogTrigger asChild>
            <Button className="bg-purple-600 hover:bg-purple-700">
              <FolderPlus className="h-4 w-4 mr-2" />
              Create Sub-sub-category
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create Sub-sub-category</DialogTitle>
              <DialogDescription>Create a sub-sub-category in {selectedSubCategory?.title}</DialogDescription>
            </DialogHeader>
            <SubSubCategoryForm onClose={() => setShowSubSubCategoryDialog(false)} />
          </DialogContent>
        </Dialog>

        <FileUploadDialog 
          level="Sub-category" 
          title={selectedSubCategory?.title || ""} 
          subjectId="sub-category-direct"
        />
      </div>

      {/* Files at Sub-category Level */}
      {subCategoryFiles.length > 0 && (
        <div className="mb-8">
          <h3 className="text-lg font-semibold mb-4 flex items-center">
            <FileText className="h-5 w-5 mr-2 text-green-600" />
            PDF Files in "{selectedSubCategory?.title}" Sub-category
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
            {subCategoryFiles.map((file: PdfFile) => (
              <Card key={file.id} className="hover:shadow-lg transition-shadow border-green-200">
                <CardContent className="p-4">
                  <div className="flex items-start justify-between mb-3">
                    <div className="flex items-center flex-1">
                      <FileText className="h-6 w-6 text-green-600 mr-3" />
                      <div className="flex-1">
                        <h4 className="font-medium text-sm">{file.title}</h4>
                        <p className="text-xs text-gray-500 mt-1">{file.description}</p>
                      </div>
                    </div>
                    <div className="flex gap-1">
                      <Button size="sm" variant="ghost">
                        <Edit className="h-3 w-3" />
                      </Button>
                      <Button size="sm" variant="ghost" className="text-red-600">
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </div>
                  </div>

                  <div className="flex items-center justify-between">
                    <Badge variant="outline" className="text-xs">
                      {file.platform}
                    </Badge>
                    <Badge variant="secondary" className="text-xs">
                      {file.pageCount || 0} pages
                    </Badge>
                    <Button size="sm" variant="ghost">
                      <Eye className="h-3 w-3" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Sub-sub-categories */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {subSubCategories.map((subSubCategory: PdfSubSubCategory) => (
          <Card key={subSubCategory.id} className="hover:shadow-lg transition-shadow cursor-pointer border-orange-200">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <FolderOpen className="h-8 w-8 text-orange-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{subSubCategory.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{subSubCategory.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingSubSubCategory(subSubCategory);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteSubSubCategory.mutate(subSubCategory.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToAllFiles(subSubCategory)}
                  className="w-full bg-orange-100 hover:bg-orange-200 text-orange-800"
                  variant="ghost"
                >
                  <FileText className="h-4 w-4 mr-2" />
                  View Files
                </Button>

                <FileUploadDialog 
                  level="Sub-sub-category" 
                  title={subSubCategory.title} 
                  subjectId={subSubCategory.id}
                />
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderAllFilesView = () => (
    <div>
      <div className="flex items-center mb-4">
        <Button variant="ghost" onClick={goBack} className="mr-4">
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Sub-sub-categories
        </Button>
        <h2 className="text-xl font-semibold flex items-center">
          <FileText className="h-6 w-6 mr-3 text-orange-600" />
          Files in "{selectedSubSubCategory?.title}"
        </h2>
      </div>

      <div className="mb-6">
        <FileUploadDialog 
          level="Sub-sub-category" 
          title={selectedSubSubCategory?.title || ""} 
          subjectId={selectedSubSubCategory?.id || ""}
        />
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {subSubCategoryFiles.map((file: PdfFile) => (
          <Card key={file.id} className="hover:shadow-lg transition-shadow border-green-200">
            <CardContent className="p-4">
              <div className="flex items-start justify-between mb-3">
                <div className="flex items-center flex-1">
                  <FileText className="h-6 w-6 text-green-600 mr-3" />
                  <div className="flex-1">
                    <h4 className="font-medium text-sm">{file.title}</h4>
                    <p className="text-xs text-gray-500 mt-1">{file.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost">
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600">
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="flex items-center justify-between">
                <Badge variant="outline" className="text-xs">
                  {file.platform}
                </Badge>
                <Badge variant="secondary" className="text-xs">
                  {file.pageCount || 0} pages
                </Badge>
                <Button size="sm" variant="ghost">
                  <Eye className="h-3 w-3" />
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  // Main render
  return (
    <div className="container mx-auto p-6">
      {currentView === "categories" && renderCategoriesView()}
      {currentView === "sub-categories" && renderSubCategoriesView()}
      {currentView === "sub-sub-categories" && renderSubSubCategoriesView()}
      {currentView === "all-files" && renderAllFilesView()}
    </div>
  );
}

client/src/pages/admin/ppt-management-ne.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { 
  Plus, 
  Edit, 
  Trash2, 
  Folder, 
  FolderOpen, 
  FileText, 
  ArrowLeft,
  Upload
} from "lucide-react";
import { EnhancedFileForm } from "@/components/enhanced-file-form";

// Type definitions for PPT management
interface PptCategory {
  id: string;
  title: string;
  description: string | null;
  createdAt: string;
  updatedAt: string;
}

interface PptSubCategory {
  id: string;
  title: string;
  description: string | null;
  batchId: string; // Parent category
  createdAt: string;
  updatedAt: string;
}

interface PptSubSubCategory {
  id: string;
  title: string;
  description: string | null;
  courseId: string; // Parent sub-category
  batchId: string; // Parent category
  createdAt: string;
  updatedAt: string;
}

interface PptFile {
  id: string;
  title: string;
  description: string | null;
  subjectId: string | null; // Sub-sub-category level
  courseId: string | null; // Sub-category level  
  batchId: string; // Category level
  fileUrl: string;
  embedUrl: string;
  platform: string;
  slideCount: number;
  createdAt: string;
  updatedAt: string;
}

type ViewLevel = "categories" | "sub-categories" | "sub-sub-categories" | "all-files";

export default function PPTManagementNew() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Navigation states
  const [currentView, setCurrentView] = useState<ViewLevel>("categories");
  const [selectedCategory, setSelectedCategory] = useState<PptCategory | null>(null);
  const [selectedSubCategory, setSelectedSubCategory] = useState<PptSubCategory | null>(null);
  const [selectedSubSubCategory, setSelectedSubSubCategory] = useState<PptSubSubCategory | null>(null);

  // Dialog states
  const [showCategoryDialog, setShowCategoryDialog] = useState(false);
  const [showSubCategoryDialog, setShowSubCategoryDialog] = useState(false);
  const [showSubSubCategoryDialog, setShowSubSubCategoryDialog] = useState(false);
  const [showFileDialog, setShowFileDialog] = useState(false);

  // Edit states
  const [editingCategory, setEditingCategory] = useState<PptCategory | null>(null);
  const [editingSubCategory, setEditingSubCategory] = useState<PptSubCategory | null>(null);
  const [editingSubSubCategory, setEditingSubSubCategory] = useState<PptSubSubCategory | null>(null);

  // Queries for each level
  const { data: categories = [], isLoading: categoriesLoading } = useQuery<PptCategory[]>({
    queryKey: ["/api/ppt/batches"],
    enabled: true
  });

  const { data: subCategories = [], isLoading: subCategoriesLoading } = useQuery<PptSubCategory[]>({
    queryKey: ["/api/ppt/batches", selectedCategory?.id, "courses"],
    enabled: !!selectedCategory && (currentView === "sub-categories" || currentView === "sub-sub-categories" || currentView === "all-files")
  });

  const { data: subSubCategories = [], isLoading: subSubCategoriesLoading } = useQuery<PptSubSubCategory[]>({
    queryKey: ["/api/ppt/courses", selectedSubCategory?.id, "subjects"],
    enabled: !!selectedSubCategory && (currentView === "sub-sub-categories" || currentView === "all-files")
  });

  // Files at each level
  const { data: categoryFiles = [] } = useQuery<PptFile[]>({
    queryKey: ["/api/ppt/batches", selectedCategory?.id, "files"],
    enabled: !!selectedCategory && (currentView === "sub-categories" || currentView === "all-files")
  });

  const { data: subCategoryFiles = [] } = useQuery<PptFile[]>({
    queryKey: ["/api/ppt/courses", selectedSubCategory?.id, "files"],
    enabled: !!selectedSubCategory && (currentView === "sub-sub-categories" || currentView === "all-files")
  });

  const { data: subSubCategoryFiles = [] } = useQuery<PptFile[]>({
    queryKey: ["/api/ppt/subjects", selectedSubSubCategory?.id, "files"],
    enabled: !!selectedSubSubCategory && currentView === "all-files"
  });

  // Navigation handlers
  const navigateToSubCategories = (category: PptCategory) => {
    setSelectedCategory(category);
    setCurrentView("sub-categories");
  };

  const navigateToSubSubCategories = (subCategory: PptSubCategory) => {
    setSelectedSubCategory(subCategory);
    setCurrentView("sub-sub-categories");
  };

  const navigateToAllFiles = (subSubCategory: PptSubSubCategory) => {
    setSelectedSubSubCategory(subSubCategory);
    setCurrentView("all-files");
  };

  const goBack = () => {
    switch (currentView) {
      case "sub-categories":
        setCurrentView("categories");
        setSelectedCategory(null);
        break;
      case "sub-sub-categories":
        setCurrentView("sub-categories");
        setSelectedSubCategory(null);
        break;
      case "all-files":
        setCurrentView("sub-sub-categories");
        setSelectedSubSubCategory(null);
        break;
    }
  };

  // Mutation handlers
  const createCategory = useMutation({
    mutationFn: (data: { title: string; description: string }) =>
      apiRequest("/api/ppt/batches", "POST", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches"] });
      setShowCategoryDialog(false);
      toast({ title: "Success", description: "Category created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create category", variant: "destructive" });
    }
  });

  const createSubCategory = useMutation({
    mutationFn: (data: { title: string; description: string; batchId: string }) =>
      apiRequest("/api/ppt/courses", "POST", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches", selectedCategory?.id, "courses"] });
      setShowSubCategoryDialog(false);
      toast({ title: "Success", description: "Sub-category created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create sub-category", variant: "destructive" });
    }
  });

  const createSubSubCategory = useMutation({
    mutationFn: (data: { title: string; description: string; courseId: string; batchId: string }) =>
      apiRequest("/api/ppt/subjects", "POST", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/courses", selectedSubCategory?.id, "subjects"] });
      setShowSubSubCategoryDialog(false);
      toast({ title: "Success", description: "Sub-sub-category created successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to create sub-sub-category", variant: "destructive" });
    }
  });

  const deleteCategory = useMutation({
    mutationFn: (id: string) => apiRequest(`/api/ppt/batches/${id}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches"] });
      toast({ title: "Success", description: "Category deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete category", variant: "destructive" });
    }
  });

  const deleteSubCategory = useMutation({
    mutationFn: (id: string) => apiRequest(`/api/ppt/courses/${id}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches", selectedCategory?.id, "courses"] });
      toast({ title: "Success", description: "Sub-category deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete sub-category", variant: "destructive" });
    }
  });

  const deleteSubSubCategory = useMutation({
    mutationFn: (id: string) => apiRequest(`/api/ppt/subjects/${id}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ppt/courses", selectedSubCategory?.id, "subjects"] });
      toast({ title: "Success", description: "Sub-sub-category deleted successfully" });
    },
    onError: () => {
      toast({ title: "Error", description: "Failed to delete sub-sub-category", variant: "destructive" });
    }
  });

  // Form components
  const CategoryForm = ({ onClose }: { onClose: () => void }) => {
    const [title, setTitle] = useState(editingCategory?.title || "");
    const [description, setDescription] = useState(editingCategory?.description || "");

    const handleSubmit = (e: React.FormEvent) => {
      e.preventDefault();
      createCategory.mutate({ title, description });
    };

    return (
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <Label htmlFor="title">Category Name</Label>
          <Input
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="e.g., Business Presentations"
            required
          />
        </div>
        <div>
          <Label htmlFor="description">Description</Label>
          <Textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Brief description of this category..."
          />
        </div>
        <div className="flex justify-end gap-2">
          <Button type="button" variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button type="submit" disabled={createCategory.isPending}>
            {createCategory.isPending ? "Creating..." : "Create Category"}
          </Button>
        </div>
      </form>
    );
  };

  const SubCategoryForm = ({ onClose }: { onClose: () => void }) => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");

    const handleSubmit = (e: React.FormEvent) => {
      e.preventDefault();
      if (!selectedCategory) return;
      createSubCategory.mutate({ 
        title, 
        description, 
        batchId: selectedCategory.id 
      });
    };

    return (
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <Label htmlFor="title">Sub-Category Name</Label>
          <Input
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="e.g., Marketing Presentations"
            required
          />
        </div>
        <div>
          <Label htmlFor="description">Description</Label>
          <Textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Brief description of this sub-category..."
          />
        </div>
        <div className="flex justify-end gap-2">
          <Button type="button" variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button type="submit" disabled={createSubCategory.isPending}>
            {createSubCategory.isPending ? "Creating..." : "Create Sub-Category"}
          </Button>
        </div>
      </form>
    );
  };

  const SubSubCategoryForm = ({ onClose }: { onClose: () => void }) => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");

    const handleSubmit = (e: React.FormEvent) => {
      e.preventDefault();
      if (!selectedSubCategory || !selectedCategory) return;
      createSubSubCategory.mutate({ 
        title, 
        description, 
        courseId: selectedSubCategory.id,
        batchId: selectedCategory.id 
      });
    };

    return (
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <Label htmlFor="title">Sub-Sub-Category Name</Label>
          <Input
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="e.g., Sales Presentations"
            required
          />
        </div>
        <div>
          <Label htmlFor="description">Description</Label>
          <Textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Brief description of this sub-sub-category..."
          />
        </div>
        <div className="flex justify-end gap-2">
          <Button type="button" variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button type="submit" disabled={createSubSubCategory.isPending}>
            {createSubSubCategory.isPending ? "Creating..." : "Create Sub-Sub-Category"}
          </Button>
        </div>
      </form>
    );
  };

  // File upload dialog
  const FileUploadDialog = () => {
    // Determine the correct subjectId based on current context
    let subjectId = "category-direct"; // Default for category level uploads

    if (currentView === "sub-sub-categories" && selectedSubSubCategory) {
      subjectId = selectedSubSubCategory.id; // Upload to specific sub-sub-category
    } else if (currentView === "sub-categories" && selectedSubCategory) {
      subjectId = "course-direct"; // Upload to sub-category level
    }

    return (
      <Dialog open={showFileDialog} onOpenChange={setShowFileDialog}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Upload PPT File</DialogTitle>
            <DialogDescription>
              Upload a presentation file to {currentView === "categories" ? "this category" : 
              currentView === "sub-categories" ? "this sub-category" : "this sub-sub-category"}
            </DialogDescription>
          </DialogHeader>
          <EnhancedFileForm 
            batchId={selectedCategory?.id}
            courseId={selectedSubCategory?.id}
            subjectId={subjectId}
            fileType="ppt" 
            onClose={() => {
              setShowFileDialog(false);
              // Invalidate all relevant queries
              queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches"] });
              if (selectedCategory) {
                queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches", selectedCategory.id, "files"] });
                queryClient.invalidateQueries({ queryKey: ["/api/ppt/batches", selectedCategory.id, "courses"] });
              }
              if (selectedSubCategory) {
                queryClient.invalidateQueries({ queryKey: ["/api/ppt/courses", selectedSubCategory.id, "files"] });
                queryClient.invalidateQueries({ queryKey: ["/api/ppt/courses", selectedSubCategory.id, "subjects"] });
              }
              if (selectedSubSubCategory) {
                queryClient.invalidateQueries({ queryKey: ["/api/ppt/subjects", selectedSubSubCategory.id, "files"] });
              }
            }}
          />
        </DialogContent>
      </Dialog>
    );
  };

  // Render different views
  const renderCategoriesView = () => (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">PPT Categories</h1>
        <Dialog open={showCategoryDialog} onOpenChange={setShowCategoryDialog}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Create New Category
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create New Category</DialogTitle>
              <DialogDescription>Create a main category for organizing PPT files</DialogDescription>
            </DialogHeader>
            <CategoryForm onClose={() => setShowCategoryDialog(false)} />
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {categories.map((category: PptCategory) => (
          <Card key={category.id} className="hover:shadow-lg transition-shadow cursor-pointer">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <Folder className="h-8 w-8 text-orange-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{category.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{category.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingCategory(category);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteCategory.mutate(category.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToSubCategories(category)}
                  className="w-full bg-orange-100 hover:bg-orange-200 text-orange-800"
                  variant="ghost"
                >
                  <FolderOpen className="h-4 w-4 mr-2" />
                  Open Category
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderSubCategoriesView = () => (
    <div>
      <div className="flex items-center gap-4 mb-6">
        <Button variant="outline" onClick={goBack}>
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Categories
        </Button>
        <div className="flex-1">
          <h1 className="text-2xl font-bold">
            {selectedCategory?.title} - Sub-Categories
          </h1>
          <p className="text-gray-600">{selectedCategory?.description}</p>
        </div>
        <div className="flex gap-2">
          <Button onClick={() => setShowFileDialog(true)}>
            <Upload className="h-4 w-4 mr-2" />
            Upload File
          </Button>
          <Dialog open={showSubCategoryDialog} onOpenChange={setShowSubCategoryDialog}>
            <DialogTrigger asChild>
              <Button>
                <Plus className="h-4 w-4 mr-2" />
                Create Sub-Category
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create Sub-Category</DialogTitle>
                <DialogDescription>Create a sub-category under {selectedCategory?.title}</DialogDescription>
              </DialogHeader>
              <SubCategoryForm onClose={() => setShowSubCategoryDialog(false)} />
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Category Files */}
      {categoryFiles.length > 0 && (
        <div className="mb-8">
          <h2 className="text-lg font-semibold mb-4">Files in this Category</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {categoryFiles.map((file: PptFile) => (
              <Card key={file.id} className="hover:shadow-lg transition-shadow">
                <CardContent className="p-4">
                  <div className="flex items-start justify-between">
                    <div className="flex items-center">
                      <FileText className="h-6 w-6 text-orange-600 mr-3" />
                      <div>
                        <h4 className="font-semibold">{file.title}</h4>
                        <p className="text-sm text-gray-600">{file.platform}</p>
                        <p className="text-xs text-gray-500">{file.slideCount} slides</p>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Sub-Categories */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {subCategories.map((subCategory: PptSubCategory) => (
          <Card key={subCategory.id} className="hover:shadow-lg transition-shadow cursor-pointer">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <Folder className="h-8 w-8 text-green-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{subCategory.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{subCategory.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingSubCategory(subCategory);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteSubCategory.mutate(subCategory.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToSubSubCategories(subCategory)}
                  className="w-full bg-green-100 hover:bg-green-200 text-green-800"
                  variant="ghost"
                >
                  <FolderOpen className="h-4 w-4 mr-2" />
                  Open Sub-Category
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderSubSubCategoriesView = () => (
    <div>
      <div className="flex items-center gap-4 mb-6">
        <Button variant="outline" onClick={goBack}>
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Sub-Categories
        </Button>
        <div className="flex-1">
          <h1 className="text-2xl font-bold">
            {selectedSubCategory?.title} - Sub-Sub-Categories
          </h1>
          <p className="text-gray-600">{selectedSubCategory?.description}</p>
        </div>
        <div className="flex gap-2">
          <Button onClick={() => setShowFileDialog(true)}>
            <Upload className="h-4 w-4 mr-2" />
            Upload File
          </Button>
          <Dialog open={showSubSubCategoryDialog} onOpenChange={setShowSubSubCategoryDialog}>
            <DialogTrigger asChild>
              <Button>
                <Plus className="h-4 w-4 mr-2" />
                Create Sub-Sub-Category
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create Sub-Sub-Category</DialogTitle>
                <DialogDescription>Create a sub-sub-category under {selectedSubCategory?.title}</DialogDescription>
              </DialogHeader>
              <SubSubCategoryForm onClose={() => setShowSubSubCategoryDialog(false)} />
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Sub-Category Files */}
      {subCategoryFiles.length > 0 && (
        <div className="mb-8">
          <h2 className="text-lg font-semibold mb-4">Files in this Sub-Category</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {subCategoryFiles.map((file: PptFile) => (
              <Card key={file.id} className="hover:shadow-lg transition-shadow">
                <CardContent className="p-4">
                  <div className="flex items-start justify-between">
                    <div className="flex items-center">
                      <FileText className="h-6 w-6 text-orange-600 mr-3" />
                      <div>
                        <h4 className="font-semibold">{file.title}</h4>
                        <p className="text-sm text-gray-600">{file.platform}</p>
                        <p className="text-xs text-gray-500">{file.slideCount} slides</p>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Sub-Sub-Categories */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {subSubCategories.map((subSubCategory: PptSubSubCategory) => (
          <Card key={subSubCategory.id} className="hover:shadow-lg transition-shadow cursor-pointer">
            <CardContent className="p-6">
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  <Folder className="h-8 w-8 text-purple-600 mr-3" />
                  <div>
                    <h3 className="font-semibold text-lg">{subSubCategory.title}</h3>
                    <p className="text-sm text-gray-600 mt-1">{subSubCategory.description}</p>
                  </div>
                </div>
                <div className="flex gap-1">
                  <Button size="sm" variant="ghost" onClick={(e) => {
                    e.stopPropagation();
                    setEditingSubSubCategory(subSubCategory);
                  }}>
                    <Edit className="h-3 w-3" />
                  </Button>
                  <Button size="sm" variant="ghost" className="text-red-600" onClick={(e) => {
                    e.stopPropagation();
                    deleteSubSubCategory.mutate(subSubCategory.id);
                  }}>
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>

              <div className="space-y-2">
                <Button 
                  onClick={() => navigateToAllFiles(subSubCategory)}
                  className="w-full bg-purple-100 hover:bg-purple-200 text-purple-800"
                  variant="ghost"
                >
                  <FolderOpen className="h-4 w-4 mr-2" />
                  View Files
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderAllFilesView = () => (
    <div>
      <div className="flex items-center gap-4 mb-6">
        <Button variant="outline" onClick={goBack}>
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Sub-Sub-Categories
        </Button>
        <div className="flex-1">
          <h1 className="text-2xl font-bold">
            {selectedSubSubCategory?.title} - Files
          </h1>
          <p className="text-gray-600">{selectedSubSubCategory?.description}</p>
        </div>
        <Button onClick={() => setShowFileDialog(true)}>
          <Upload className="h-4 w-4 mr-2" />
          Upload File
        </Button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {subSubCategoryFiles.map((file: PptFile) => (
          <Card key={file.id} className="hover:shadow-lg transition-shadow">
            <CardContent className="p-4">
              <div className="flex items-start justify-between">
                <div className="flex items-center">
                  <FileText className="h-6 w-6 text-orange-600 mr-3" />
                  <div>
                    <h4 className="font-semibold">{file.title}</h4>
                    <p className="text-sm text-gray-600">{file.platform}</p>
                    <p className="text-xs text-gray-500">{file.slideCount} slides</p>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  // Loading states
  if (categoriesLoading) {
    return <div className="p-6">Loading categories...</div>;
  }

  return (
    <div className="space-y-6">
      {currentView === "categories" && renderCategoriesView()}
      {currentView === "sub-categories" && renderSubCategoriesView()}
      {currentView === "sub-sub-categories" && renderSubSubCategoriesView()}
      {currentView === "all-files" && renderAllFilesView()}

      <FileUploadDialog />
    </div>
  );
}

client/src/components/file-preview.tsx.content:
import { useState, useEffect } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { FileText, Eye, EyeOff, Download, Loader2, Trash2, ExternalLink } from 'lucide-react';

interface FilePreviewProps {
  id?: string;
  url?: string;
  title: string;
  description?: string;
  platform: string;
  fileUrl?: string;
  embedUrl?: string;
  pageCount?: number;
  slideCount?: number;
  thumbnailUrl?: string;
  fileSize?: number;
  type?: "pdf" | "ppt";
  onClose?: () => void;
  onView?: () => void;
  onDelete?: () => void;
  showControls?: boolean;
}

export default function FilePreview({ 
  id, 
  url, 
  title, 
  description, 
  platform, 
  fileUrl, 
  embedUrl, 
  pageCount, 
  slideCount, 
  thumbnailUrl, 
  fileSize, 
  type = "pdf", 
  onClose, 
  onView, 
  onDelete, 
  showControls = true 
}: FilePreviewProps) {
  const [isLoading, setIsLoading] = useState(true);
  const [showPreview, setShowPreview] = useState(true);

  const getEmbedUrl = () => {
    // Use embedUrl if available, otherwise fallback to fileUrl, then url
    const targetUrl = embedUrl || fileUrl || url;
    if (!targetUrl) return '';

    if (targetUrl.includes('drive.google.com')) {
      // Google Drive PDF
      const fileId = targetUrl.match(/\/d\/([a-zA-Z0-9-_]+)/)?.[1];
      return `https://drive.google.com/file/d/${fileId}/preview`;
    } else if (targetUrl.includes('onedrive.live.com') || targetUrl.includes('1drv.ms')) {
      // OneDrive PDF  
      return targetUrl.replace('/view', '/embed');
    } else if (targetUrl.includes('mozilla.github.io/pdf.js')) {
      // Already a PDF.js viewer URL
      return targetUrl;
    } else if (targetUrl.includes('docs.google.com/viewer')) {
      // Already a Google Docs viewer URL
      return targetUrl;
    } else {
      // For direct PDF URLs, use Google Docs viewer for better rendering
      return `https://docs.google.com/viewer?url=${encodeURIComponent(targetUrl)}&embedded=true`;
    }
  };

  return (
    <Card className="w-full h-64 relative overflow-hidden border border-gray-200 dark:border-gray-700 hover:border-blue-300 dark:hover:border-blue-600 transition-colors">
      <CardContent className="p-0 h-full">
        {/* Preview Header */}
        {showControls && (
          <div className="absolute top-2 left-2 right-2 z-10 flex items-center justify-between">
            <div className="flex items-center space-x-2 bg-white/95 dark:bg-gray-800/95 backdrop-blur-sm rounded-lg px-2 py-1 shadow-sm">
              <FileText className="h-3 w-3 text-blue-600 flex-shrink-0" />
              <span className="text-xs font-medium text-gray-800 dark:text-white truncate max-w-[120px]">
                {title}
              </span>
              {(pageCount || slideCount) && (
                <span className="text-xs text-gray-500 bg-gray-100 dark:bg-gray-700 px-1 rounded">
                  {type === 'pdf' ? pageCount : slideCount}
                </span>
              )}
            </div>

            <div className="flex items-center space-x-1">
              {onView && (
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={onView}
                  className="bg-white/95 dark:bg-gray-800/95 backdrop-blur-sm p-1 h-auto"
                  title="View File"
                >
                  <ExternalLink className="h-3 w-3" />
                </Button>
              )}

              <Button 
                variant="ghost" 
                size="sm" 
                onClick={() => setShowPreview(!showPreview)}
                className="bg-white/95 dark:bg-gray-800/95 backdrop-blur-sm p-1 h-auto"
                title={showPreview ? "Hide Preview" : "Show Preview"}
              >
                {showPreview ? <EyeOff className="h-3 w-3" /> : <Eye className="h-3 w-3" />}
              </Button>

              {onDelete && (
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={onDelete}
                  className="bg-red-50/95 dark:bg-red-900/95 backdrop-blur-sm p-1 h-auto hover:bg-red-100 dark:hover:bg-red-800"
                  title="Delete File"
                >
                  <Trash2 className="h-3 w-3 text-red-600 dark:text-red-400" />
                </Button>
              )}

              {onClose && (
                <Button 
                  variant="ghost" 
                  size="sm" 
                  onClick={onClose}
                  className="bg-white/95 dark:bg-gray-800/95 backdrop-blur-sm p-1 h-auto"
                  title="Close"
                >
                  ‚úï
                </Button>
              )}
            </div>
          </div>
        )}

        {/* Preview Content */}
        {showPreview ? (
          <div className="relative w-full h-full">
            {isLoading && (
              <div className="absolute inset-0 flex items-center justify-center bg-gray-100 dark:bg-gray-800">
                <div className="flex flex-col items-center space-y-2">
                  <Loader2 className="h-8 w-8 animate-spin text-blue-600" />
                  <span className="text-sm text-gray-600 dark:text-gray-300">Loading preview...</span>
                </div>
              </div>
            )}

            <iframe
              src={getEmbedUrl()}
              className="w-full h-full border-0"
              title={`Preview of ${title}`}
              onLoad={() => setIsLoading(false)}
              onError={() => setIsLoading(false)}
            />
          </div>
        ) : (
          <div className="w-full h-full flex items-center justify-center bg-gray-100 dark:bg-gray-800">
            <div className="text-center">
              <FileText className="h-12 w-12 text-gray-400 mx-auto mb-2" />
              <p className="text-gray-600 dark:text-gray-300 text-sm">Preview hidden</p>
              <p className="text-gray-500 dark:text-gray-400 text-xs">Click eye icon to show</p>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

server/fileMetadata.ts.content:
import { createWriteStream, unlinkSync, readFileSync } from 'fs';
import { tmpdir } from 'os';
import { join } from 'path';
import https from 'https';
import http from 'http';

// Dynamic import for pdf-parse
let pdfParse: any;

interface FileMetadata {
  pageCount?: number;
  slideCount?: number;
  thumbnailUrl?: string;
  fileSize?: number;
  title?: string;
  extractedText?: string;
}

/**
 * Extract metadata from PDF/PPT files
 */
export async function extractFileMetadata(file: Buffer | string, platform: string, fileType: 'pdf' | 'ppt'): Promise<FileMetadata> {
  try {
    console.log(`Extracting metadata for ${fileType} from ${platform}`);

    // Initialize pdf-parse if needed
    if (!pdfParse && fileType === 'pdf') {
      pdfParse = (await import('pdf-parse')).default;
    }

    // If we have a Buffer (uploaded file), extract metadata directly
    if (Buffer.isBuffer(file) && fileType === 'pdf') {
      return await extractPdfFromBuffer(file);
    }

    // Handle URL-based platforms
    if (typeof file === 'string') {
      switch (platform) {
        case 'google_drive':
          return await extractGoogleDriveMetadata(file, fileType);
        case 'onedrive':
          return await extractOneDriveMetadata(file, fileType);
        case 'dropbox':
          return await extractDropboxMetadata(file, fileType);
        case 'scribd':
          return await extractScribdMetadata(file, fileType);
        case 'canva':
          return await extractCanvaMetadata(file, fileType);
        case 'google_slides':
          return await extractGoogleSlidesMetadata(file);
        case 'powerpoint_online':
          return await extractPowerPointOnlineMetadata(file);
        default:
          // Try generic URL extraction
          return await extractGenericUrlMetadata(file, fileType);
      }
    }

    // Default fallback
    return {
      pageCount: fileType === 'pdf' ? 1 : undefined,
      slideCount: fileType === 'ppt' ? 1 : undefined,
      fileSize: 0
    };
  } catch (error) {
    console.error(`Error extracting metadata:`, error);
    return {
      pageCount: fileType === 'pdf' ? 1 : undefined,
      slideCount: fileType === 'ppt' ? 1 : undefined,
      fileSize: 0
    };
  }
}

/**
 * Extract metadata directly from PDF buffer
 */
async function extractPdfFromBuffer(buffer: Buffer): Promise<FileMetadata> {
  try {
    if (!pdfParse) {
      pdfParse = (await import('pdf-parse')).default;
    }

    const data = await pdfParse(buffer);

    return {
      pageCount: data.numpages,
      fileSize: buffer.length,
      title: data.info?.Title || undefined,
      extractedText: data.text?.substring(0, 500) // First 500 chars for preview
    };
  } catch (error) {
    console.error('Error parsing PDF buffer:', error);
    return {
      pageCount: 1,
      fileSize: buffer.length
    };
  }
}

/**
 * Extract metadata from Google Drive URLs
 */
async function extractGoogleDriveMetadata(url: string, fileType: 'pdf' | 'ppt'): Promise<FileMetadata> {
  try {
    // Extract file ID from Google Drive URL
    const fileIdMatch = url.match(/\/d\/([a-zA-Z0-9-_]+)/);
    if (!fileIdMatch) {
      throw new Error('Invalid Google Drive URL');
    }

    const fileId = fileIdMatch[1];

    // For PDFs, we can try to download and parse
    if (fileType === 'pdf') {
      const downloadUrl = `https://drive.google.com/uc?id=${fileId}&export=download`;
      return await downloadAndExtractPdfMetadata(downloadUrl);
    } else {
      // For PPT files, estimate based on typical presentation length
      return {
        slideCount: 10, // Default estimate
        thumbnailUrl: `https://drive.google.com/thumbnail?id=${fileId}&sz=w400-h300`
      };
    }
  } catch (error) {
    console.error('Error extracting Google Drive metadata:', error);
    return getDefaultMetadata(fileType);
  }
}

/**
 * Extract metadata from OneDrive URLs
 */
async function extractOneDriveMetadata(url: string, fileType: 'pdf' | 'ppt'): Promise<FileMetadata> {
  // OneDrive metadata extraction would require API access
  // For now, return estimated values
  return {
    pageCount: fileType === 'pdf' ? 5 : undefined,
    slideCount: fileType === 'ppt' ? 8 : undefined
  };
}

/**
 * Extract metadata from Dropbox URLs
 */
async function extractDropboxMetadata(url: string, fileType: 'pdf' | 'ppt'): Promise<FileMetadata> {
  // Dropbox metadata extraction would require API access
  // For now, return estimated values
  return {
    pageCount: fileType === 'pdf' ? 3 : undefined,
    slideCount: fileType === 'ppt' ? 6 : undefined
  };
}

/**
 * Extract metadata from Scribd URLs
 */
async function extractScribdMetadata(url: string, fileType: 'pdf' | 'ppt'): Promise<FileMetadata> {
  // Scribd metadata would need web scraping or API access
  return {
    pageCount: fileType === 'pdf' ? 8 : undefined,
    slideCount: fileType === 'ppt' ? 12 : undefined
  };
}

/**
 * Extract metadata from Canva URLs
 */
async function extractCanvaMetadata(url: string, fileType: 'pdf' | 'ppt'): Promise<FileMetadata> {
  // Canva metadata would need API access
  return {
    pageCount: fileType === 'pdf' ? 4 : undefined,
    slideCount: fileType === 'ppt' ? 7 : undefined
  };
}

/**
 * Extract metadata from Google Slides URLs
 */
async function extractGoogleSlidesMetadata(url: string): Promise<FileMetadata> {
  // Google Slides metadata extraction would require API access
  return {
    slideCount: 9 // Default estimate
  };
}

/**
 * Extract metadata from PowerPoint Online URLs
 */
async function extractPowerPointOnlineMetadata(url: string): Promise<FileMetadata> {
  // PowerPoint Online metadata would require API access
  return {
    slideCount: 11 // Default estimate
  };
}

/**
 * Extract metadata from uploaded files
 */
async function extractUploadedFileMetadata(fileUrl: string, fileType: 'pdf' | 'ppt'): Promise<FileMetadata> {
  if (fileType === 'pdf') {
    return await downloadAndExtractPdfMetadata(fileUrl);
  } else {
    // PPT files would need specialized libraries
    return {
      slideCount: 6 // Default estimate
    };
  }
}

/**
 * Generic URL metadata extraction
 */
async function extractGenericUrlMetadata(url: string, fileType: 'pdf' | 'ppt'): Promise<FileMetadata> {
  if (fileType === 'pdf' && url.toLowerCase().includes('.pdf')) {
    return await downloadAndExtractPdfMetadata(url);
  }

  return getDefaultMetadata(fileType);
}

/**
 * Download and extract PDF metadata
 */
async function downloadAndExtractPdfMetadata(url: string): Promise<FileMetadata> {
  return new Promise(async (resolve) => {
    try {
      // Dynamically import pdf-parse
      if (!pdfParse) {
        pdfParse = (await import('pdf-parse')).default;
      }

      const tempFilePath = join(tmpdir(), `temp-pdf-${Date.now()}.pdf`);
      const file = createWriteStream(tempFilePath);

      const request = url.startsWith('https') ? https : http;

      const req = request.get(url, (response) => {
        if (response.statusCode !== 200) {
          resolve(getDefaultMetadata('pdf'));
          return;
        }

        response.pipe(file);

        file.on('finish', async () => {
          file.close();

          try {
            const dataBuffer = readFileSync(tempFilePath);
            const pdfData = await pdfParse(dataBuffer);

            // Clean up temp file
            unlinkSync(tempFilePath);

            resolve({
              pageCount: pdfData.numpages,
              fileSize: dataBuffer.length,
              extractedText: pdfData.text.substring(0, 500), // First 500 chars
              title: extractTitleFromText(pdfData.text)
            });
          } catch (error) {
            console.error('Error parsing PDF:', error);
            // Clean up temp file
            try {
              unlinkSync(tempFilePath);
            } catch {}
            resolve(getDefaultMetadata('pdf'));
          }
        });
      });

      req.on('error', () => {
        resolve(getDefaultMetadata('pdf'));
      });

      // Timeout after 30 seconds
      req.setTimeout(30000, () => {
        req.destroy();
        resolve(getDefaultMetadata('pdf'));
      });

    } catch (error) {
      console.error('Error downloading PDF:', error);
      resolve(getDefaultMetadata('pdf'));
    }
  });
}

/**
 * Extract title from PDF text content
 */
function extractTitleFromText(text: string): string | undefined {
  const lines = text.split('\n').filter(line => line.trim().length > 0);
  if (lines.length > 0) {
    // Return first meaningful line as potential title
    const firstLine = lines[0].trim();
    if (firstLine.length > 3 && firstLine.length < 100) {
      return firstLine;
    }
  }
  return undefined;
}

/**
 * Get default metadata for file types
 */
function getDefaultMetadata(fileType: 'pdf' | 'ppt'): FileMetadata {
  return {
    pageCount: fileType === 'pdf' ? 1 : undefined,
    slideCount: fileType === 'ppt' ? 1 : undefined,
    fileSize: 0
  };
}

/**
 * Generate thumbnail URL for preview (placeholder implementation)
 */
export function generateThumbnailUrl(url: string, platform: string, fileType: 'pdf' | 'ppt'): string | undefined {
  switch (platform) {
    case 'google_drive':
      const fileIdMatch = url.match(/\/d\/([a-zA-Z0-9-_]+)/);
      if (fileIdMatch) {
        return `https://drive.google.com/thumbnail?id=${fileIdMatch[1]}&sz=w400-h300`;
      }
      break;
    case 'canva':
      // Canva might have thumbnail endpoints
      return url.replace('/view', '/thumbnail');
    default:
      break;
  }

  // Return placeholder thumbnail
  return `data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="400" height="300" viewBox="0 0 400 300"><rect width="400" height="300" fill="%23f3f4f6"/><text x="200" y="150" text-anchor="middle" fill="%236b7280" font-family="sans-serif" font-size="16">${fileType.toUpperCase()} Preview</text></svg>`;
}

