import { useState, useEffect, useRef } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Slider } from '@/components/ui/slider';
import { 
  ChevronLeft, 
  ChevronRight, 
  ZoomIn, 
  ZoomOut, 
  RotateCw, 
  RotateCcw, 
  Download, 
  Maximize, 
  Minimize,
  FileText,
  Home,
  RefreshCw
} from 'lucide-react';

interface DedicatedPdfViewerProps {
  pdfUrl: string;
  title: string;
  onClose?: () => void;
}

export default function DedicatedPdfViewer({ pdfUrl, title, onClose }: DedicatedPdfViewerProps) {
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [zoom, setZoom] = useState(100);
  const [rotation, setRotation] = useState(0);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [jumpToPage, setJumpToPage] = useState('');
  const [viewerType, setViewerType] = useState<'google' | 'pdfjs' | 'embed'>('google');

  const viewerRef = useRef<HTMLDivElement>(null);
  const iframeRef = useRef<HTMLIFrameElement>(null);

  // Multiple PDF viewer strategies for maximum compatibility
  const getViewerUrl = () => {
    const encodedUrl = encodeURIComponent(pdfUrl);

    switch (viewerType) {
      case 'google':
        return `https://docs.google.com/viewer?url=${encodedUrl}&embedded=true`;
      case 'pdfjs':
        return `https://mozilla.github.io/pdf.js/web/viewer.html?file=${encodedUrl}`;
      case 'embed':
        return pdfUrl;
      default:
        return `https://docs.google.com/viewer?url=${encodedUrl}&embedded=true`;
    }
  };

  // Handle page navigation
  const nextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const prevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleJumpToPage = () => {
    const pageNum = parseInt(jumpToPage);
    if (pageNum >= 1 && pageNum <= totalPages) {
      setCurrentPage(pageNum);
      setJumpToPage('');
    }
  };

  // Zoom controls
  const zoomIn = () => {
    setZoom(Math.min(zoom + 25, 300));
  };

  const zoomOut = () => {
    setZoom(Math.max(zoom - 25, 50));
  };

  const resetZoom = () => {
    setZoom(100);
  };

  // Rotation controls
  const rotateLeft = () => {
    setRotation((rotation - 90) % 360);
  };

  const rotateRight = () => {
    setRotation((rotation + 90) % 360);
  };

  // Fullscreen toggle
  const toggleFullscreen = () => {
    setIsFullscreen(!isFullscreen);
  };

  // Switch viewer type if current one fails
  const switchViewer = () => {
    if (viewerType === 'google') {
      setViewerType('pdfjs');
    } else if (viewerType === 'pdfjs') {
      setViewerType('embed');
    } else {
      setViewerType('google');
    }
    setIsLoading(true);
  };

  const handleIframeLoad = () => {
    setIsLoading(false);
  };

  const handleIframeError = () => {
    setIsLoading(false);
    // Auto-switch to next viewer type
    setTimeout(switchViewer, 1000);
  };

  return (
    <div className={`${isFullscreen ? 'fixed inset-0 z-50' : 'relative'} bg-gray-50 dark:bg-gray-900`}>
      {/* Top Control Bar */}
      <div className="bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700 p-2 lg:p-4">
        <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-3">
          {/* Title and Info */}
          <div className="flex items-center gap-3 min-w-0 flex-1">
            <FileText className="h-5 w-5 text-blue-600 flex-shrink-0" />
            <h2 className="font-semibold text-gray-900 dark:text-white truncate">{title}</h2>
            <div className="hidden lg:flex items-center gap-2 text-sm text-gray-500">
              <span>Viewer: {viewerType.toUpperCase()}</span>
              <Button variant="ghost" size="sm" onClick={switchViewer} className="h-6 px-2">
                <RefreshCw className="h-3 w-3" />
              </Button>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex items-center gap-2 flex-wrap">
            {/* Page Navigation - Mobile Optimized */}
            <div className="flex items-center gap-1 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
              <Button variant="ghost" size="sm" onClick={prevPage} disabled={currentPage <= 1} className="h-8 px-2">
                <ChevronLeft className="h-4 w-4" />
              </Button>
              <span className="text-sm font-medium px-2 py-1 min-w-[60px] text-center">
                {currentPage} / {totalPages}
              </span>
              <Button variant="ghost" size="sm" onClick={nextPage} disabled={currentPage >= totalPages} className="h-8 px-2">
                <ChevronRight className="h-4 w-4" />
              </Button>
            </div>

            {/* Jump to Page - Hidden on mobile */}
            <div className="hidden md:flex items-center gap-1">
              <Input
                type="number"
                placeholder="Page"
                value={jumpToPage}
                onChange={(e) => setJumpToPage(e.target.value)}
                className="w-16 h-8 text-sm"
                min={1}
                max={totalPages}
              />
              <Button size="sm" onClick={handleJumpToPage} className="h-8 px-3">
                Go
              </Button>
            </div>

            {/* Zoom Controls */}
            <div className="hidden lg:flex items-center gap-1 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
              <Button variant="ghost" size="sm" onClick={zoomOut} className="h-8 px-2">
                <ZoomOut className="h-4 w-4" />
              </Button>
              <span className="text-sm font-medium px-2 py-1 min-w-[50px] text-center">
                {zoom}%
              </span>
              <Button variant="ghost" size="sm" onClick={zoomIn} className="h-8 px-2">
                <ZoomIn className="h-4 w-4" />
              </Button>
            </div>

            {/* Rotation - Desktop Only */}
            <div className="hidden xl:flex items-center gap-1 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
              <Button variant="ghost" size="sm" onClick={rotateLeft} className="h-8 px-2">
                <RotateCcw className="h-4 w-4" />
              </Button>
              <Button variant="ghost" size="sm" onClick={rotateRight} className="h-8 px-2">
                <RotateCw className="h-4 w-4" />
              </Button>
            </div>

            {/* Utility Buttons */}
            <div className="flex items-center gap-1">
              <Button variant="ghost" size="sm" onClick={toggleFullscreen} className="h-8 px-2">
                {isFullscreen ? <Minimize className="h-4 w-4" /> : <Maximize className="h-4 w-4" />}
              </Button>
              <Button variant="ghost" size="sm" asChild className="h-8 px-2">
                <a href={pdfUrl} download target="_blank" rel="noopener noreferrer">
                  <Download className="h-4 w-4" />
                </a>
              </Button>
              {onClose && (
                <Button variant="ghost" size="sm" onClick={onClose} className="h-8 px-2">
                  <Home className="h-4 w-4" />
                </Button>
              )}
            </div>
          </div>
        </div>

        {/* Mobile-Only Secondary Controls */}
        <div className="lg:hidden mt-3 flex items-center justify-between gap-2">
          {/* Mobile Zoom */}
          <div className="flex items-center gap-2 flex-1">
            <ZoomOut className="h-4 w-4 text-gray-500" />
            <Slider
              value={[zoom]}
              onValueChange={(value) => setZoom(value[0])}
              max={300}
              min={50}
              step={25}
              className="flex-1"
            />
            <ZoomIn className="h-4 w-4 text-gray-500" />
            <span className="text-sm font-medium min-w-[45px]">{zoom}%</span>
          </div>

          {/* Mobile Jump to Page */}
          <div className="flex items-center gap-1">
            <Input
              type="number"
              placeholder="Page"
              value={jumpToPage}
              onChange={(e) => setJumpToPage(e.target.value)}
              className="w-14 h-7 text-sm"
              min={1}
              max={totalPages}
            />
            <Button size="sm" onClick={handleJumpToPage} className="h-7 px-2 text-xs">
              Go
            </Button>
          </div>
        </div>
      </div>

      {/* PDF Viewer Area */}
      <div 
        ref={viewerRef}
        className={`${isFullscreen ? 'h-[calc(100vh-120px)]' : 'h-[calc(100vh-200px)]'} relative bg-gray-100 dark:bg-gray-800`}
      >
        {isLoading && (
          <div className="absolute inset-0 flex items-center justify-center bg-white/80 dark:bg-gray-900/80 z-10">
            <div className="flex flex-col items-center gap-3">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
              <p className="text-sm text-gray-600 dark:text-gray-400">Loading PDF...</p>
              <p className="text-xs text-gray-500">Using {viewerType.toUpperCase()} viewer</p>
            </div>
          </div>
        )}

        <iframe
          ref={iframeRef}
          src={getViewerUrl()}
          className="w-full h-full border-0"
          style={{
            transform: `scale(${zoom / 100}) rotate(${rotation}deg)`,
            transformOrigin: 'center center'
          }}
          onLoad={handleIframeLoad}
          onError={handleIframeError}
          title={`PDF Viewer - ${title}`}
          sandbox="allow-scripts allow-same-origin allow-popups allow-popups-to-escape-sandbox"
        />

        {/* Fallback Message */}
        {!isLoading && (
          <div className="absolute bottom-4 right-4 z-10">
            <Card className="bg-white/90 dark:bg-gray-800/90 backdrop-blur-sm">
              <CardContent className="p-3">
                <p className="text-xs text-gray-600 dark:text-gray-400">
                  Having trouble? Try switching viewer type
                </p>
                <Button variant="outline" size="sm" onClick={switchViewer} className="w-full mt-2">
                  Switch Viewer
                </Button>
              </CardContent>
            </Card>
          </div>
        )}
      </div>

      {/* Bottom Status Bar - Mobile */}
      <div className="lg:hidden bg-white dark:bg-gray-800 border-t border-gray-200 dark:border-gray-700 p-2">
        <div className="flex items-center justify-between text-sm text-gray-600 dark:text-gray-400">
          <span>Page {currentPage} of {totalPages}</span>
          <span>Zoom: {zoom}%</span>
          <span>{viewerType.toUpperCase()} Viewer</span>
        </div>
      </div>
    </div>
  );
}

