import { useLocation, useRoute } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { useState } from 'react';
import DedicatedPdfViewer from '@/components/dedicated-pdf-viewer';
import { Button } from '@/components/ui/button';
import { ArrowLeft, FileText, AlertCircle } from 'lucide-react';
import { Card, CardContent } from '@/components/ui/card';

export default function PdfViewerPage() {
  const [, params] = useRoute('/pdf-viewer/:id');
  const [, setLocation] = useLocation();

  const fileId = params?.id;

  // Fetch PDF file data
  const { data: file, isLoading, error } = useQuery({
    queryKey: ['/api/pdf/files', fileId],
    enabled: !!fileId,
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading PDF...</p>
        </div>
      </div>
    );
  }

  if (error || !file) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center p-4">
        <Card className="max-w-md w-full">
          <CardContent className="p-6 text-center">
            <AlertCircle className="h-12 w-12 text-red-500 mx-auto mb-4" />
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
              PDF Not Found
            </h2>
            <p className="text-gray-600 dark:text-gray-400 mb-4">
              The PDF file you're looking for could not be found or accessed.
            </p>
            <Button onClick={() => setLocation('/pdf-management')} className="w-full">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to PDF Management
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const pdfUrl = file.embedUrl || file.fileUrl;

  if (!pdfUrl) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center p-4">
        <Card className="max-w-md w-full">
          <CardContent className="p-6 text-center">
            <FileText className="h-12 w-12 text-gray-500 mx-auto mb-4" />
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
              No PDF URL Available
            </h2>
            <p className="text-gray-600 dark:text-gray-400 mb-4">
              This PDF file doesn't have a valid URL for viewing.
            </p>
            <Button onClick={() => setLocation('/pdf-management')} className="w-full">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to PDF Management
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="h-screen overflow-hidden">
      <DedicatedPdfViewer
        pdfUrl={pdfUrl}
        title={file.title}
        onClose={() => setLocation('/pdf-management')}
      />
    </div>
  );
}

