import { useState, useRef, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { 
  Upload, 
  File, 
  Eye, 
  Download, 
  X, 
  Check,
  FileText,
  Link,
  Folder,
  AlertCircle,
  Loader2,
  Copy,
  ExternalLink,
  Cloud
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

interface PDFFile {
  id: string;
  title: string;
  description?: string;
  fileUrl: string;
  embedUrl?: string;
  platform: 'upload' | 'google_drive' | 'onedrive' | 'dropbox' | 'canva' | 'direct_url';
  fileSize?: number;
  pageCount?: number;
  thumbnailUrl?: string;
  createdAt: string;
}

interface UploadManagerProps {
  onClose?: () => void;
  onFileUploaded?: (file: PDFFile) => void;
}

export default function PDFUploadManager({ onClose, onFileUploaded }: UploadManagerProps) {
  const [activeTab, setActiveTab] = useState<'upload' | 'embed'>('upload');
  const [dragActive, setDragActive] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string>('');
  const [isPreviewLoading, setIsPreviewLoading] = useState(false);

  // Form states
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [embedUrl, setEmbedUrl] = useState('');
  const [platform, setPlatform] = useState<string>('direct_url');
  const [selectedBatch, setSelectedBatch] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('');

  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch batches for organization
  const { data: batches = [] } = useQuery<any[]>({
    queryKey: ['/api/pdf/batches'],
  });

  // Fetch courses when batch is selected
  const { data: courses = [] } = useQuery<any[]>({
    queryKey: ['/api/pdf/batches', selectedBatch, 'courses'],
    enabled: !!selectedBatch,
  });

  // Fetch subjects when course is selected
  const { data: subjects = [] } = useQuery<any[]>({
    queryKey: ['/api/pdf/batches', selectedBatch, 'courses', selectedCourse, 'subjects'],
    enabled: !!selectedBatch && !!selectedCourse,
  });

  // Upload mutation
  const uploadMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('/api/pdf/upload', {
        method: 'POST',
        body: JSON.stringify({
          title: title.trim(),
          description: description.trim(),
          batchId: selectedBatch || null,
          courseId: selectedCourse || null,
          subjectId: selectedSubject || null,
        }),
      });
      return response as PDFFile;
    },
    onSuccess: (data) => {
      toast({ title: 'Success', description: 'PDF uploaded successfully!' });
      onFileUploaded?.(data);
      queryClient.invalidateQueries({ queryKey: ['/api/admin/pdf/all-files'] });
      resetForm();
    },
    onError: (error) => {
      toast({ 
        title: 'Upload Failed', 
        description: 'Failed to upload PDF',
        variant: 'destructive'
      });
    }
  });

  // Embed mutation
  const embedMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('/api/pdf/embed', {
        method: 'POST',
        body: JSON.stringify({
          title: title.trim(),
          description: description.trim(),
          fileUrl: embedUrl.trim(),
          embedUrl: previewUrl !== embedUrl ? previewUrl : null,
          platform,
          batchId: selectedBatch || null,
          courseId: selectedCourse || null,
          subjectId: selectedSubject || null,
        }),
      });
      return response as PDFFile;
    },
    onSuccess: (data) => {
      toast({ title: 'Success', description: 'PDF embedded successfully!' });
      onFileUploaded?.(data);
      queryClient.invalidateQueries({ queryKey: ['/api/admin/pdf/all-files'] });
      resetForm();
    },
    onError: (error) => {
      toast({ 
        title: 'Embed Failed', 
        description: 'Failed to embed PDF',
        variant: 'destructive'
      });
    }
  });

  const resetForm = () => {
    setSelectedFile(null);
    setPreviewUrl('');
    setTitle('');
    setDescription('');
    setEmbedUrl('');
    setSelectedBatch('');
    setSelectedCourse('');
    setSelectedSubject('');
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleDrag = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const files = e.dataTransfer.files;
    if (files?.[0]) {
      handleFileSelect(files[0]);
    }
  }, []);

  const handleFileSelect = (file: File) => {
    if (file.type !== 'application/pdf') {
      toast({
        title: 'Invalid File',
        description: 'Please select a PDF file',
        variant: 'destructive'
      });
      return;
    }

    setSelectedFile(file);
    setTitle(file.name.replace('.pdf', ''));

    // Create preview URL
    const url = URL.createObjectURL(file);
    setPreviewUrl(url);
  };

  const handleUrlChange = async (url: string) => {
    setEmbedUrl(url);
    setIsPreviewLoading(true);

    try {
      // Extract title from URL if possible
      if (url.includes('drive.google.com')) {
        setTitle('Google Drive PDF');
        setPlatform('google_drive');
      } else if (url.includes('onedrive.live.com') || url.includes('1drv.ms')) {
        setTitle('OneDrive PDF');
        setPlatform('onedrive');
      } else if (url.includes('dropbox.com')) {
        setTitle('Dropbox PDF');
        setPlatform('dropbox');
      } else if (url.includes('canva.com')) {
        setTitle('Canva PDF');
        setPlatform('canva');
      } else {
        setTitle('External PDF');
        setPlatform('direct_url');
      }

      // Generate platform-specific preview URL
      let transformedUrl = url;
      let finalPreviewUrl = '';

      if (url.includes('canva.com')) {
        // Transform Canva URLs to proper embed format
        if (url.includes('/design/')) {
          // Convert design URLs to view/embed format
          transformedUrl = url.replace('/design/', '/view/').replace('?', '/embed?');
        }
        finalPreviewUrl = transformedUrl;
      } else if (url.includes('drive.google.com')) {
        // For Google Drive, use the file view URL
        const fileId = url.match(/\/d\/([a-zA-Z0-9-_]+)/)?.[1];
        if (fileId) {
          finalPreviewUrl = `https://drive.google.com/file/d/${fileId}/preview`;
        } else {
          finalPreviewUrl = url;
        }
      } else if (url.includes('dropbox.com')) {
        // For Dropbox, add ?raw=1 for direct access
        finalPreviewUrl = url.includes('?') ? `${url}&raw=1` : `${url}?raw=1`;
      } else {
        // For other URLs, use Google Docs Viewer as fallback
        finalPreviewUrl = `https://docs.google.com/viewer?url=${encodeURIComponent(url)}&embedded=true`;
      }

      setPreviewUrl(finalPreviewUrl);
    } catch (error) {
      console.error('Error processing URL:', error);
    } finally {
      setIsPreviewLoading(false);
    }
  };

  const handleUpload = () => {
    if (!selectedFile || !title.trim()) {
      toast({
        title: 'Missing Information',
        description: 'Please select a file and enter a title',
        variant: 'destructive'
      });
      return;
    }

    uploadMutation.mutate();
  };

  const handleEmbed = () => {
    if (!embedUrl.trim() || !title.trim()) {
      toast({
        title: 'Missing Information',
        description: 'Please enter a URL and title',
        variant: 'destructive'
      });
      return;
    }

    embedMutation.mutate();
  };

  const generateShortcode = () => {
    if (embedUrl) {
      const shortcode = `[pdf url="${embedUrl}" title="${title}"]`;
      navigator.clipboard.writeText(shortcode);
      toast({ title: 'Copied!', description: 'Shortcode copied to clipboard' });
    }
  };

  return (
    <div className="w-full max-w-6xl mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">PDF Manager</h1>
          <p className="text-gray-600 dark:text-gray-300">Upload or embed PDF files with live preview</p>
        </div>
        {onClose && (
          <Button variant="ghost" onClick={onClose}>
            <X className="h-4 w-4" />
          </Button>
        )}
      </div>

      <div className="grid lg:grid-cols-2 gap-6">
        {/* Left Panel - Upload/Embed Form */}
        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Cloud className="h-5 w-5 text-blue-600" />
                PDF Upload & Embed
              </CardTitle>
            </CardHeader>
            <CardContent>
              <Tabs value={activeTab} onValueChange={(v) => setActiveTab(v as 'upload' | 'embed')}>
                <TabsList className="grid w-full grid-cols-2">
                  <TabsTrigger value="upload">Upload File</TabsTrigger>
                  <TabsTrigger value="embed">Embed URL</TabsTrigger>
                </TabsList>

                <TabsContent value="upload" className="space-y-4">
                  {/* File Upload Area */}
                  <div
                    className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors ${
                      dragActive 
                        ? 'border-blue-500 bg-blue-50 dark:bg-blue-950' 
                        : 'border-gray-300 dark:border-gray-600'
                    }`}
                    onDragEnter={handleDrag}
                    onDragLeave={handleDrag}
                    onDragOver={handleDrag}
                    onDrop={handleDrop}
                  >
                    {selectedFile ? (
                      <div className="space-y-3">
                        <FileText className="h-12 w-12 text-blue-600 mx-auto" />
                        <div>
                          <p className="font-medium">{selectedFile.name}</p>
                          <p className="text-sm text-gray-500">
                            {(selectedFile.size / (1024 * 1024)).toFixed(2)} MB
                          </p>
                        </div>
                        <Button variant="outline" onClick={() => setSelectedFile(null)}>
                          <X className="h-4 w-4 mr-2" />
                          Remove
                        </Button>
                      </div>
                    ) : (
                      <div className="space-y-3">
                        <Upload className="h-12 w-12 text-gray-400 mx-auto" />
                        <div>
                          <p className="text-lg font-medium">Drop PDF file here</p>
                          <p className="text-sm text-gray-500">or click to browse</p>
                        </div>
                        <Button
                          variant="outline"
                          onClick={() => fileInputRef.current?.click()}
                        >
                          Choose File
                        </Button>
                        <input
                          ref={fileInputRef}
                          type="file"
                          accept=".pdf"
                          onChange={(e) => e.target.files?.[0] && handleFileSelect(e.target.files[0])}
                          className="hidden"
                        />
                      </div>
                    )}
                  </div>
                </TabsContent>

                <TabsContent value="embed" className="space-y-4">
                  <div className="space-y-3">
                    <Label>PDF URL</Label>
                    <Input
                      placeholder="https://example.com/document.pdf"
                      value={embedUrl}
                      onChange={(e) => handleUrlChange(e.target.value)}
                    />
                    <div className="flex gap-2">
                      <Select value={platform} onValueChange={setPlatform}>
                        <SelectTrigger className="w-40">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="google_drive">Google Drive</SelectItem>
                          <SelectItem value="onedrive">OneDrive</SelectItem>
                          <SelectItem value="dropbox">Dropbox</SelectItem>
                          <SelectItem value="canva">Canva</SelectItem>
                          <SelectItem value="direct_url">Direct URL</SelectItem>
                        </SelectContent>
                      </Select>
                      {embedUrl && (
                        <Button variant="outline" onClick={generateShortcode}>
                          <Copy className="h-4 w-4 mr-2" />
                          Shortcode
                        </Button>
                      )}
                    </div>
                  </div>
                </TabsContent>
              </Tabs>

              <Separator className="my-6" />

              {/* Form Fields */}
              <div className="space-y-4">
                <div>
                  <Label>Title *</Label>
                  <Input
                    placeholder="Enter PDF title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                  />
                </div>

                <div>
                  <Label>Description</Label>
                  <Textarea
                    placeholder="Brief description of this PDF"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    rows={3}
                  />
                </div>

                {/* Organization */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                  <div>
                    <Label>Category</Label>
                    <Select value={selectedBatch} onValueChange={setSelectedBatch}>
                      <SelectTrigger>
                        <SelectValue placeholder="Select category" />
                      </SelectTrigger>
                      <SelectContent>
                        {batches.map((batch: any) => (
                          <SelectItem key={batch.id} value={batch.id}>
                            {batch.title}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  {selectedBatch && (
                    <div>
                      <Label>Module</Label>
                      <Select value={selectedCourse} onValueChange={setSelectedCourse}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select module" />
                        </SelectTrigger>
                        <SelectContent>
                          {courses.map((course: any) => (
                            <SelectItem key={course.id} value={course.id}>
                              {course.title}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                  )}

                  {selectedCourse && (
                    <div>
                      <Label>Section</Label>
                      <Select value={selectedSubject} onValueChange={setSelectedSubject}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select section" />
                        </SelectTrigger>
                        <SelectContent>
                          {subjects.map((subject: any) => (
                            <SelectItem key={subject.id} value={subject.id}>
                              {subject.title}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                  )}
                </div>

                {/* Action Buttons */}
                <div className="flex gap-3 pt-4">
                  {activeTab === 'upload' ? (
                    <Button 
                      onClick={handleUpload}
                      disabled={uploadMutation.isPending || !selectedFile || !title.trim()}
                      className="flex-1"
                    >
                      {uploadMutation.isPending ? (
                        <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      ) : (
                        <Upload className="h-4 w-4 mr-2" />
                      )}
                      Upload PDF
                    </Button>
                  ) : (
                    <Button 
                      onClick={handleEmbed}
                      disabled={embedMutation.isPending || !embedUrl.trim() || !title.trim()}
                      className="flex-1"
                    >
                      {embedMutation.isPending ? (
                        <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      ) : (
                        <Link className="h-4 w-4 mr-2" />
                      )}
                      Embed PDF
                    </Button>
                  )}

                  <Button variant="outline" onClick={resetForm}>
                    Reset
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Right Panel - Live Preview */}
        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Eye className="h-5 w-5 text-green-600" />
                Live Preview
                {previewUrl && (
                  <Badge variant="secondary">Ready</Badge>
                )}
              </CardTitle>
            </CardHeader>
            <CardContent>
              {previewUrl ? (
                <div className="space-y-4">
                  {/* Preview Info */}
                  <div className="flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                    <div className="flex items-center gap-2">
                      <FileText className="h-4 w-4 text-blue-600" />
                      <span className="font-medium truncate">{title || 'Untitled'}</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <Button variant="ghost" size="sm" asChild>
                        <a href={previewUrl} target="_blank" rel="noopener noreferrer">
                          <ExternalLink className="h-4 w-4" />
                        </a>
                      </Button>
                    </div>
                  </div>

                  {/* Preview Frame */}
                  <div className="relative">
                    {isPreviewLoading && (
                      <div className="absolute inset-0 flex items-center justify-center bg-white/80 dark:bg-gray-900/80 z-10">
                        <Loader2 className="h-6 w-6 animate-spin" />
                      </div>
                    )}
                    <iframe
                      src={previewUrl}
                      className="w-full h-96 border border-gray-200 dark:border-gray-700 rounded-lg"
                      title="PDF Preview"
                      sandbox="allow-same-origin allow-scripts allow-popups allow-forms"
                      referrerPolicy="no-referrer"
                      onLoad={() => setIsPreviewLoading(false)}
                      onError={() => {
                        setIsPreviewLoading(false);
                        toast({
                          title: 'Preview Error',
                          description: 'Unable to load preview. The file may still embed correctly.',
                          variant: 'destructive'
                        });
                      }}
                    />
                  </div>

                  {/* Preview Actions */}
                  <div className="flex gap-2">
                    <Button variant="outline" size="sm" className="flex-1">
                      <Eye className="h-4 w-4 mr-2" />
                      Preview Looks Good
                    </Button>
                  </div>
                </div>
              ) : (
                <div className="text-center py-12 text-gray-500">
                  <FileText className="h-12 w-12 mx-auto mb-3 opacity-50" />
                  <p>Select a file or enter URL to see live preview</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

