attached_assets/deepseek_javascript_2025.content:
jQuery(document).ready(function($) {
    // Handle PDF file selection for preview
    $('#pdf-file').on('change', function(e) {
        var file = e.target.files[0];
        if (file && file.type === 'application/pdf') {
            var fileURL = URL.createObjectURL(file);
            renderPDFPreview(fileURL, 'pdf-preview');
            $('#pdf-preview-container').show();
            $('#confirm-upload').show();
        }
    });

    // Handle PDF URL for embed preview
    $('#pdf-embed-form').on('submit', function(e) {
        e.preventDefault();
        var pdfUrl = $('#pdf-url').val();
        if (pdfUrl) {
            renderPDFPreview(pdfUrl, 'embed-preview');
            $('#embed-preview-container').show();

            // Generate embed code
            var embedCode = '[pdf_embed url="' + pdfUrl + '" title="' + $('#embed-title').val() + '"]';
            $('#embed-code').val(embedCode);
        }
    });

    // Copy embed code to clipboard
    $('#copy-embed-code').on('click', function() {
        var embedCode = $('#embed-code');
        embedCode.select();
        document.execCommand('copy');
        $(this).text('Copied!');
        setTimeout(function() {
            $('#copy-embed-code').text('Copy to Clipboard');
        }, 2000);
    });

    // Tab switching
    $('.nav-tab-wrapper a').on('click', function(e) {
        e.preventDefault();
        var target = $(this).attr('href');

        $('.nav-tab-wrapper a').removeClass('nav-tab-active');
        $(this).addClass('nav-tab-active');

        $('.tab-content').removeClass('active');
        $(target).addClass('active');
    });

    // PDF preview rendering function
    function renderPDFPreview(url, containerId) {
        // Initialize PDF.js
        pdfjsLib.getDocument(url).promise.then(function(pdf) {
            // Fetch the first page
            pdf.getPage(1).then(function(page) {
                var scale = 1.5;
                var viewport = page.getViewport({ scale: scale });

                // Prepare canvas
                var canvas = document.createElement('canvas');
                var context = canvas.getContext('2d');
                canvas.height = viewport.height;
                canvas.width = viewport.width;

                // Clear previous preview
                $('#' + containerId).html(canvas);

                // Render PDF page
                page.render({
                    canvasContext: context,
                    viewport: viewport
                });
            });
        }).catch(function(error) {
            $('#' + containerId).html('<div class="error">Error loading PDF: ' + error.message + '</div>');
        });
    }

    // Handle PDF upload form submission
    $('#pdf-upload-form').on('submit', function(e) {
        e.preventDefault();

        var formData = new FormData(this);

        $.ajax({
            url: pdfViewer.ajaxurl,
            type: 'POST',
            data: formData,
            processData: false,
            contentType: false,
            dataType: 'json',
            beforeSend: function(xhr) {
                xhr.setRequestHeader('X-WP-Nonce', pdfViewer.nonce);
            },
            success: function(response) {
                if (response.success) {
                    alert('PDF uploaded successfully!');
                    window.location.reload();
                } else {
                    alert('Error: ' + response.data);
                }
            },
            error: function(xhr, status, error) {
                alert('AJAX Error: ' + error);
            }
        });
    });

    // Handle PDF deletion
    $('.delete-pdf').on('click', function(e) {
        e.preventDefault();
        if (confirm('Are you sure you want to delete this PDF?')) {
            var pdfId = $(this).data('id');

            $.ajax({
                url: pdfViewer.ajaxurl,
                type: 'POST',
                data: {
                    action: 'delete_pdf',
                    id: pdfId,
                    nonce: pdfViewer.nonce
                },
                success: function(response) {
                    if (response.success) {
                        alert('PDF deleted successfully!');
                        window.location.reload();
                    } else {
                        alert('Error: ' + response.data);
                    }
                }
            });
        }
    });
});

client/src/components/CompletePdfManager.content:
import React, { useState, useRef, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { 
  Upload, 
  Link, 
  FileText, 
  Eye, 
  Trash2, 
  Copy, 
  Download,
  ZoomIn,
  ZoomOut,
  RotateCcw,
  Share2,
  CheckCircle,
  AlertCircle
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';

interface PDFFile {
  id: string;
  title: string;
  description?: string;
  fileUrl: string;
  embedUrl?: string;
  platform: string;
  fileSize?: number;
  uploadDate: string;
}

const CompletePdfManager: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'upload' | 'embed' | 'manage'>('upload');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [embedUrl, setEmbedUrl] = useState('');
  const [embedTitle, setEmbedTitle] = useState('');
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);
  const [showPreview, setShowPreview] = useState(false);
  const [zoom, setZoom] = useState(100);
  const [selectedPdf, setSelectedPdf] = useState<PDFFile | null>(null);

  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch all PDFs
  const { data: pdfFiles = [], refetch } = useQuery<PDFFile[]>({
    queryKey: ['/api/admin/pdf/all-files'],
    retry: false, // Don't retry on auth failures
  });

  // Upload mutation
  const uploadMutation = useMutation({
    mutationFn: async () => {
      if (!selectedFile) throw new Error('Please select a file');
      const finalTitle = title.trim() || selectedFile.name.replace(/\.[^/.]+$/, '');
      if (!finalTitle) throw new Error('Title is required');

      const formData = new FormData();
      formData.append('file', selectedFile);
      formData.append('title', finalTitle);
      formData.append('description', description || '');

      const response = await fetch('/api/pdf/upload-file', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Upload failed');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({ title: 'Success', description: 'PDF uploaded successfully!' });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/pdf/all-files'] });
      resetUploadForm();
    },
    onError: (error: any) => {
      toast({ 
        title: 'Upload Failed', 
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  // Embed mutation
  const embedMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/pdf/embed', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          title: embedTitle,
          description,
          fileUrl: embedUrl,
          platform: detectPlatform(embedUrl),
        }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Embed failed');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({ title: 'Success', description: 'PDF embedded successfully!' });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/pdf/all-files'] });
      resetEmbedForm();
    },
    onError: (error: any) => {
      toast({ 
        title: 'Embed Failed', 
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  // Delete mutation
  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/pdf/delete/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Delete failed');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({ title: 'Success', description: 'PDF deleted successfully!' });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/pdf/all-files'] });
    },
    onError: (error: any) => {
      toast({ 
        title: 'Delete Failed', 
        description: error.message,
        variant: 'destructive'
      });
    }
  });

  const detectPlatform = (url: string): string => {
    if (url.includes('drive.google.com')) return 'google_drive';
    if (url.includes('onedrive.live.com') || url.includes('1drv.ms')) return 'onedrive';
    if (url.includes('dropbox.com')) return 'dropbox';
    if (url.includes('canva.com')) return 'canva';
    if (url.includes('scribd.com')) return 'scribd';
    return 'direct_url';
  };

  const handleFileSelect = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    if (file.type !== 'application/pdf') {
      toast({
        title: 'Invalid File',
        description: 'Please select a PDF file',
        variant: 'destructive'
      });
      return;
    }

    setSelectedFile(file);
    const fileName = file.name.replace(/\.[^/.]+$/, ''); // Remove any extension
    setTitle(fileName);

    // Generate preview URL
    const fileURL = URL.createObjectURL(file);
    setPreviewUrl(fileURL);
    setShowPreview(true);
  }, [toast]);

  const generateEmbedPreview = useCallback(() => {
    if (!embedUrl) return;

    let processedUrl = embedUrl;
    const platform = detectPlatform(embedUrl);

    // Transform URLs for better embedding
    switch (platform) {
      case 'google_drive':
        const fileId = embedUrl.match(/\/d\/([a-zA-Z0-9-_]+)/)?.[1];
        if (fileId) {
          processedUrl = `https://drive.google.com/file/d/${fileId}/preview`;
        }
        break;
      case 'canva':
        if (embedUrl.includes('/design/')) {
          processedUrl = embedUrl.replace('/design/', '/view/');
          if (!processedUrl.includes('embed')) {
            processedUrl += processedUrl.includes('?') ? '&embed' : '?embed';
          }
        }
        break;
      case 'dropbox':
        processedUrl = embedUrl.includes('?') ? `${embedUrl}&raw=1` : `${embedUrl}?raw=1`;
        break;
      default:
        // Use Google Docs Viewer as fallback
        processedUrl = `https://docs.google.com/viewer?url=${encodeURIComponent(embedUrl)}&embedded=true`;
    }

    setPreviewUrl(processedUrl);
    setShowPreview(true);

    if (!embedTitle) {
      setEmbedTitle(`${platform.replace('_', ' ').toUpperCase()} PDF`);
    }
  }, [embedUrl, embedTitle]);

  const resetUploadForm = () => {
    setSelectedFile(null);
    setTitle('');
    setDescription('');
    setPreviewUrl(null);
    setShowPreview(false);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const resetEmbedForm = () => {
    setEmbedUrl('');
    setEmbedTitle('');
    setDescription('');
    setPreviewUrl(null);
    setShowPreview(false);
  };

  const generateShortcode = (pdf: PDFFile) => {
    const shortcode = `[pdf id="${pdf.id}" title="${pdf.title}"]`;
    navigator.clipboard.writeText(shortcode);
    toast({ title: 'Copied!', description: 'Shortcode copied to clipboard' });
  };

  const handleDelete = (id: string) => {
    if (window.confirm('Are you sure you want to delete this PDF?')) {
      deleteMutation.mutate(id);
    }
  };

  const formatFileSize = (bytes?: number) => {
    if (!bytes) return 'Unknown';
    const mb = bytes / (1024 * 1024);
    return `${mb.toFixed(1)} MB`;
  };

  const getPlatformBadge = (platform: string) => {
    const colors: Record<string, string> = {
      upload: 'bg-blue-100 text-blue-800',
      google_drive: 'bg-green-100 text-green-800',
      onedrive: 'bg-purple-100 text-purple-800',
      dropbox: 'bg-cyan-100 text-cyan-800',
      canva: 'bg-orange-100 text-orange-800',
      direct_url: 'bg-gray-100 text-gray-800'
    };

    return (
      <Badge className={colors[platform] || colors.direct_url}>
        {platform.replace('_', ' ').toUpperCase()}
      </Badge>
    );
  };

  return (
    <div className="w-full max-w-7xl mx-auto p-6">
      <div className="mb-6">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
          PDF Manager Pro
        </h1>
        <p className="text-gray-600 dark:text-gray-300">
          Complete PDF upload, embed, and management solution
        </p>
      </div>

      <Tabs value={activeTab} onValueChange={(v) => setActiveTab(v as any)}>
        <TabsList className="grid w-full grid-cols-3 mb-6">
          <TabsTrigger value="upload">Upload PDF</TabsTrigger>
          <TabsTrigger value="embed">Embed URL</TabsTrigger>
          <TabsTrigger value="manage">Manage PDFs</TabsTrigger>
        </TabsList>

        {/* Upload Tab */}
        <TabsContent value="upload">
          <div className="grid lg:grid-cols-2 gap-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Upload className="h-5 w-5" />
                  Upload PDF File
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label>Select PDF File</Label>
                  <input
                    ref={fileInputRef}
                    type="file"
                    accept=".pdf"
                    onChange={handleFileSelect}
                    className="mt-2 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                  />
                  {selectedFile && (
                    <div className="mt-2 p-3 bg-green-50 rounded-lg border border-green-200">
                      <div className="flex items-center gap-2">
                        <CheckCircle className="h-4 w-4 text-green-600" />
                        <span className="text-sm text-green-700 font-medium">
                          {selectedFile.name}
                        </span>
                      </div>
                      <p className="text-xs text-green-600 mt-1">
                        Size: {formatFileSize(selectedFile.size)}
                      </p>
                    </div>
                  )}
                </div>

                <div>
                  <Label>Title *</Label>
                  <Input
                    placeholder="Enter PDF title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                  />
                </div>

                <div>
                  <Label>Description</Label>
                  <Textarea
                    placeholder="Brief description (optional)"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    rows={3}
                  />
                </div>

                <Button 
                  onClick={() => uploadMutation.mutate()}
                  disabled={uploadMutation.isPending || !selectedFile}
                  className="w-full"
                >
                  {uploadMutation.isPending ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      Uploading...
                    </>
                  ) : (
                    <>
                      <Upload className="h-4 w-4 mr-2" />
                      Upload PDF
                    </>
                  )}
                </Button>
              </CardContent>
            </Card>

            {showPreview && previewUrl && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Eye className="h-5 w-5" />
                    Preview
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="border border-gray-200 dark:border-gray-700 rounded-lg overflow-hidden">
                    <iframe
                      src={previewUrl}
                      className="w-full h-96"
                      title="PDF Preview"
                      sandbox="allow-same-origin allow-scripts allow-popups allow-forms"
                    />
                  </div>
                </CardContent>
              </Card>
            )}
          </div>
        </TabsContent>

        {/* Embed Tab */}
        <TabsContent value="embed">
          <div className="grid lg:grid-cols-2 gap-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Link className="h-5 w-5" />
                  Embed PDF URL
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label>PDF URL</Label>
                  <Input
                    placeholder="https://example.com/document.pdf"
                    value={embedUrl}
                    onChange={(e) => setEmbedUrl(e.target.value)}
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    Supports: Google Drive, OneDrive, Dropbox, Canva, Scribd, and direct PDF URLs
                  </p>
                </div>

                <div>
                  <Label>Title *</Label>
                  <Input
                    placeholder="Enter PDF title"
                    value={embedTitle}
                    onChange={(e) => setEmbedTitle(e.target.value)}
                  />
                </div>

                <div>
                  <Label>Description</Label>
                  <Textarea
                    placeholder="Brief description (optional)"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    rows={3}
                  />
                </div>

                <div className="flex gap-2">
                  <Button 
                    onClick={generateEmbedPreview}
                    variant="outline"
                    disabled={!embedUrl}
                    className="flex-1"
                  >
                    <Eye className="h-4 w-4 mr-2" />
                    Preview
                  </Button>

                  <Button 
                    onClick={() => embedMutation.mutate()}
                    disabled={embedMutation.isPending || !embedUrl || !embedTitle}
                    className="flex-1"
                  >
                    {embedMutation.isPending ? (
                      <>
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                        Embedding...
                      </>
                    ) : (
                      <>
                        <Link className="h-4 w-4 mr-2" />
                        Embed PDF
                      </>
                    )}
                  </Button>
                </div>
              </CardContent>
            </Card>

            {showPreview && previewUrl && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Eye className="h-5 w-5" />
                    Preview
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="border border-gray-200 dark:border-gray-700 rounded-lg overflow-hidden">
                    <iframe
                      src={previewUrl}
                      className="w-full h-96"
                      title="PDF Preview"
                      sandbox="allow-same-origin allow-scripts allow-popups allow-forms"
                    />
                  </div>
                </CardContent>
              </Card>
            )}
          </div>
        </TabsContent>

        {/* Manage Tab */}
        <TabsContent value="manage">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="h-5 w-5" />
                Manage PDFs ({pdfFiles.length})
              </CardTitle>
            </CardHeader>
            <CardContent>
              {pdfFiles.length === 0 ? (
                <div className="text-center py-8">
                  <FileText className="h-12 w-12 mx-auto text-gray-400 mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                    No PDFs found
                  </h3>
                  <p className="text-gray-500 mb-4">
                    Upload or embed your first PDF to get started
                  </p>
                  <Button onClick={() => setActiveTab('upload')}>
                    <Upload className="h-4 w-4 mr-2" />
                    Upload PDF
                  </Button>
                </div>
              ) : (
                <div className="grid gap-4">
                  {pdfFiles.map((pdf: PDFFile) => (
                    <div key={pdf.id} className="border border-gray-200 dark:border-gray-700 rounded-lg p-4">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-2">
                            <h3 className="font-medium text-gray-900 dark:text-white">
                              {pdf.title}
                            </h3>
                            {getPlatformBadge(pdf.platform)}
                          </div>

                          {pdf.description && (
                            <p className="text-sm text-gray-600 dark:text-gray-300 mb-2">
                              {pdf.description}
                            </p>
                          )}

                          <div className="flex items-center gap-4 text-xs text-gray-500">
                            <span>Uploaded: {new Date(pdf.uploadDate).toLocaleDateString()}</span>
                            {pdf.fileSize && <span>Size: {formatFileSize(pdf.fileSize)}</span>}
                          </div>

                          <div className="mt-2 flex items-center gap-2">
                            <code className="text-xs bg-gray-100 dark:bg-gray-800 px-2 py-1 rounded">
                              [pdf id="{pdf.id}"]
                            </code>
                            <Button
                              size="sm"
                              variant="ghost"
                              onClick={() => generateShortcode(pdf)}
                            >
                              <Copy className="h-3 w-3" />
                            </Button>
                          </div>
                        </div>

                        <div className="flex items-center gap-2 ml-4">
                          <Button size="sm" variant="outline">
                            <Eye className="h-4 w-4" />
                          </Button>
                          <Button size="sm" variant="outline">
                            <Download className="h-4 w-4" />
                          </Button>
                          <Button 
                            size="sm" 
                            variant="destructive"
                            onClick={() => handleDelete(pdf.id)}
                            disabled={deleteMutation.isPending}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
};

export default CompletePdfManager;

