import { useState, useRef } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Upload, Link, FileText, AlertCircle, CheckCircle, Copy } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

interface PDFFile {
  id: string;
  title: string;
  description?: string;
  fileUrl: string;
  platform: string;
  createdAt: string;
}

interface SimplePdfUploaderProps {
  onFileUploaded?: (file: PDFFile) => void;
}

export default function SimplePdfUploader({ onFileUploaded }: SimplePdfUploaderProps) {
  const [activeTab, setActiveTab] = useState<'upload' | 'embed'>('upload');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [embedUrl, setEmbedUrl] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [uploadSuccess, setUploadSuccess] = useState(false);

  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Simple upload mutation
  const uploadMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/pdf/upload', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          title: title || 'Untitled PDF',
          description: description || '',
          batchId: null,
          courseId: null,
          subjectId: null,
        }),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Upload failed');
      }
      return response.json() as Promise<PDFFile>;
    },
    onSuccess: (data) => {
      setUploadSuccess(true);
      toast({ title: 'Success', description: 'PDF uploaded successfully!' });
      onFileUploaded?.(data);
      queryClient.invalidateQueries({ queryKey: ['/api/admin/pdf/all-files'] });
      resetForm();
    },
    onError: (error: any) => {
      toast({ 
        title: 'Upload Failed', 
        description: error.message || 'Failed to upload PDF',
        variant: 'destructive'
      });
    }
  });

  // Simple embed mutation
  const embedMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch('/api/pdf/embed', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          title: title || 'Embedded PDF',
          description: description || '',
          fileUrl: embedUrl,
          embedUrl: null,
          platform: detectPlatform(embedUrl),
          batchId: null,
          courseId: null,
          subjectId: null,
        }),
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Embed failed');
      }
      return response.json() as Promise<PDFFile>;
    },
    onSuccess: (data) => {
      setUploadSuccess(true);
      toast({ title: 'Success', description: 'PDF embedded successfully!' });
      onFileUploaded?.(data);
      queryClient.invalidateQueries({ queryKey: ['/api/admin/pdf/all-files'] });
      resetForm();
    },
    onError: (error: any) => {
      toast({ 
        title: 'Embed Failed', 
        description: error.message || 'Failed to embed PDF',
        variant: 'destructive'
      });
    }
  });

  const detectPlatform = (url: string) => {
    if (url.includes('drive.google.com')) return 'google_drive';
    if (url.includes('onedrive.live.com') || url.includes('1drv.ms')) return 'onedrive';
    if (url.includes('dropbox.com')) return 'dropbox';
    if (url.includes('canva.com')) return 'canva';
    return 'direct_url';
  };

  const resetForm = () => {
    setSelectedFile(null);
    setTitle('');
    setDescription('');
    setEmbedUrl('');
    setUploadSuccess(false);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleFileSelect = (file: File) => {
    if (file.type !== 'application/pdf') {
      toast({
        title: 'Invalid File',
        description: 'Please select a PDF file',
        variant: 'destructive'
      });
      return;
    }
    setSelectedFile(file);
    setTitle(file.name.replace('.pdf', ''));
  };

  const handleUpload = () => {
    if (!title.trim()) {
      toast({
        title: 'Title Required',
        description: 'Please enter a title for the PDF',
        variant: 'destructive'
      });
      return;
    }
    uploadMutation.mutate();
  };

  const handleEmbed = () => {
    if (!embedUrl.trim() || !title.trim()) {
      toast({
        title: 'Information Required',
        description: 'Please enter both URL and title',
        variant: 'destructive'
      });
      return;
    }
    embedMutation.mutate();
  };

  const generateShortcode = () => {
    const shortcode = `[pdf url="${embedUrl}" title="${title}"]`;
    navigator.clipboard.writeText(shortcode);
    toast({ title: 'Copied!', description: 'Shortcode copied to clipboard' });
  };

  return (
    <div className="w-full max-w-4xl mx-auto p-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="h-6 w-6" />
            PDF Upload & Embed Tool
          </CardTitle>
        </CardHeader>
        <CardContent>
          <Tabs value={activeTab} onValueChange={(v) => setActiveTab(v as 'upload' | 'embed')}>
            <TabsList className="grid w-full grid-cols-2 mb-6">
              <TabsTrigger value="upload">Upload PDF File</TabsTrigger>
              <TabsTrigger value="embed">Embed URL</TabsTrigger>
            </TabsList>

            <TabsContent value="upload" className="space-y-4">
              <div className="space-y-4">
                <div>
                  <Label>Select PDF File</Label>
                  <div className="mt-2">
                    <input
                      ref={fileInputRef}
                      type="file"
                      accept=".pdf"
                      onChange={(e) => e.target.files?.[0] && handleFileSelect(e.target.files[0])}
                      className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                    />
                  </div>
                  {selectedFile && (
                    <div className="mt-2 p-2 bg-green-50 rounded flex items-center gap-2">
                      <CheckCircle className="h-4 w-4 text-green-600" />
                      <span className="text-sm text-green-700">
                        {selectedFile.name} ({(selectedFile.size / (1024 * 1024)).toFixed(2)} MB)
                      </span>
                    </div>
                  )}
                </div>

                <div>
                  <Label>Title *</Label>
                  <Input
                    placeholder="Enter PDF title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                  />
                </div>

                <div>
                  <Label>Description</Label>
                  <Textarea
                    placeholder="Brief description (optional)"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    rows={3}
                  />
                </div>

                <Button 
                  onClick={handleUpload}
                  disabled={uploadMutation.isPending || !title.trim()}
                  className="w-full"
                >
                  {uploadMutation.isPending ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      Uploading...
                    </>
                  ) : (
                    <>
                      <Upload className="h-4 w-4 mr-2" />
                      Upload PDF
                    </>
                  )}
                </Button>
              </div>
            </TabsContent>

            <TabsContent value="embed" className="space-y-4">
              <div className="space-y-4">
                <div>
                  <Label>PDF URL</Label>
                  <Input
                    placeholder="https://example.com/document.pdf"
                    value={embedUrl}
                    onChange={(e) => {
                      setEmbedUrl(e.target.value);
                      if (e.target.value && !title) {
                        setTitle(detectPlatform(e.target.value).replace('_', ' ').toUpperCase() + ' PDF');
                      }
                    }}
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    Supports: Google Drive, OneDrive, Dropbox, Canva, and direct PDF URLs
                  </p>
                </div>

                <div>
                  <Label>Title *</Label>
                  <Input
                    placeholder="Enter PDF title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                  />
                </div>

                <div>
                  <Label>Description</Label>
                  <Textarea
                    placeholder="Brief description (optional)"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    rows={3}
                  />
                </div>

                <div className="flex gap-2">
                  <Button 
                    onClick={handleEmbed}
                    disabled={embedMutation.isPending || !embedUrl.trim() || !title.trim()}
                    className="flex-1"
                  >
                    {embedMutation.isPending ? (
                      <>
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                        Embedding...
                      </>
                    ) : (
                      <>
                        <Link className="h-4 w-4 mr-2" />
                        Embed PDF
                      </>
                    )}
                  </Button>

                  {embedUrl && (
                    <Button variant="outline" onClick={generateShortcode}>
                      <Copy className="h-4 w-4 mr-2" />
                      Shortcode
                    </Button>
                  )}
                </div>
              </div>
            </TabsContent>
          </Tabs>

          {uploadSuccess && (
            <div className="mt-4 p-4 bg-green-50 rounded-lg border border-green-200">
              <div className="flex items-center gap-2">
                <CheckCircle className="h-5 w-5 text-green-600" />
                <span className="text-green-700 font-medium">Success!</span>
              </div>
              <p className="text-green-600 text-sm mt-1">
                Your PDF has been {activeTab === 'upload' ? 'uploaded' : 'embedded'} successfully.
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

