  // Filter files based on search
  const filteredFiles = pdfFiles.filter(file =>
    file.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    file.description?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    file.batchTitle?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    file.courseTitle?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleFileUploaded = (newFile: PDFFile) => {
    refetch();
    setActiveView('manager');
  };

  const openPdfViewer = (file: PDFFile) => {
    setSelectedFile(file);
    setShowViewer(true);
  };

  const formatFileSize = (bytes?: number) => {
    if (!bytes) return 'Unknown';
    return `${(bytes / (1024 * 1024)).toFixed(1)} MB`;
  };

  const getPlatformIcon = (platform: string) => {
    switch (platform) {
      case 'google_drive':
        return 'üìÑ';
      case 'onedrive':
        return 'üìò';
      case 'dropbox':
        return 'üì¶';
      case 'canva':
        return 'üé®';
      case 'upload':
        return '‚¨ÜÔ∏è';
      default:
        return 'üîó';
    }
  };

  if (showViewer && selectedFile) {
    return (
      <DedicatedPdfViewer
        pdfUrl={selectedFile.embedUrl || selectedFile.fileUrl}
        title={selectedFile.title}
        onClose={() => {
          setShowViewer(false);
          setSelectedFile(null);
        }}
      />
    );
  }

  // Use the complete PDF manager instead of the old system
  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <CompletePdfManager />
    </div>
  );
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto p-6 space-y-6">
        {/* Header */}
        <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-4">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">PDF Library</h1>
            <p className="text-gray-600 dark:text-gray-300">
              Manage your PDF collection with drag-and-drop uploads and live previews
            </p>
          </div>

          <div className="flex items-center gap-3">
            <Button 
              onClick={() => setActiveView('upload')}
              className="bg-blue-600 hover:bg-blue-700"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add PDF
            </Button>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-blue-100 dark:bg-blue-900 rounded-lg">
                  <FileText className="h-5 w-5 text-blue-600" />
                </div>
                <div>
                  <p className="text-2xl font-bold">{pdfFiles.length}</p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Total PDFs</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-green-100 dark:bg-green-900 rounded-lg">
                  <Upload className="h-5 w-5 text-green-600" />
                </div>
                <div>
                  <p className="text-2xl font-bold">
                    {pdfFiles.filter(f => f.platform === 'upload').length}
                  </p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Uploaded</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-purple-100 dark:bg-purple-900 rounded-lg">
                  <Cloud className="h-5 w-5 text-purple-600" />
                </div>
                <div>
                  <p className="text-2xl font-bold">
                    {pdfFiles.filter(f => f.platform !== 'upload').length}
                  </p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Embedded</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-orange-100 dark:bg-orange-900 rounded-lg">
                  <Folder className="h-5 w-5 text-orange-600" />
                </div>
                <div>
                  <p className="text-2xl font-bold">
                    {new Set(pdfFiles.map(f => f.batchTitle).filter(Boolean)).size}
                  </p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Categories</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Controls */}
        <div className="flex flex-col sm:flex-row items-stretch sm:items-center gap-4">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
            <Input
              placeholder="Search PDFs, descriptions, or categories..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>

          <div className="flex items-center gap-2">
            <Button
              variant={viewMode === 'grid' ? 'default' : 'outline'}
              size="sm"
              onClick={() => setViewMode('grid')}
            >
              <Grid className="h-4 w-4" />
            </Button>
            <Button
              variant={viewMode === 'list' ? 'default' : 'outline'}
              size="sm"
              onClick={() => setViewMode('list')}
            >
              <List className="h-4 w-4" />
            </Button>
          </div>
        </div>

        {/* File Grid/List */}
        {isLoading ? (
          <div className="text-center py-12">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
            <p className="mt-4 text-gray-600 dark:text-gray-400">Loading PDFs...</p>
          </div>
        ) : filteredFiles.length === 0 ? (
          <Card>
            <CardContent className="text-center py-12">
              <FileText className="h-16 w-16 text-gray-300 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                {searchTerm ? 'No PDFs Found' : 'No PDFs Yet'}
              </h3>
              <p className="text-gray-600 dark:text-gray-400 mb-6">
                {searchTerm 
                  ? `No PDFs match "${searchTerm}". Try a different search term.`
                  : 'Upload your first PDF or embed one from cloud services.'
                }
              </p>
              {!searchTerm && (
                <Button onClick={() => setActiveView('upload')}>
                  <Plus className="h-4 w-4 mr-2" />
                  Add Your First PDF
                </Button>
              )}
            </CardContent>
          </Card>
        ) : (
          <div className={viewMode === 'grid' 
            ? 'grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4'
            : 'space-y-3'
          }>
            {filteredFiles.map((file) => (
              <Card 
                key={file.id} 
                className={`hover:shadow-lg transition-shadow cursor-pointer ${
                  viewMode === 'list' ? 'p-4' : ''
                }`}
                onClick={() => openPdfViewer(file)}
              >
                {viewMode === 'grid' ? (
                  <CardContent className="p-4">
                    <div className="space-y-3">
                      {/* File Icon & Platform */}
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          <FileText className="h-6 w-6 text-red-500" />
                          <span className="text-lg">{getPlatformIcon(file.platform)}</span>
                        </div>
                        <Badge variant="secondary" className="text-xs">
                          {file.pageCount ? `${file.pageCount} pages` : 'PDF'}
                        </Badge>
                      </div>

                      {/* Title */}
                      <div>
                        <h3 className="font-medium text-gray-900 dark:text-white line-clamp-2">
                          {file.title}
                        </h3>
                        {file.description && (
                          <p className="text-sm text-gray-600 dark:text-gray-400 line-clamp-2 mt-1">
                            {file.description}
                          </p>
                        )}
                      </div>

                      {/* Location */}
                      {file.batchTitle && (
                        <div className="text-xs text-gray-500">
                          {[file.batchTitle, file.courseTitle, file.subjectTitle]
                            .filter(Boolean)
                            .join(' ‚Ä∫ ')}
                        </div>
                      )}

                      {/* Actions */}
                      <div className="flex items-center justify-between pt-2 border-t">
                        <span className="text-xs text-gray-500">
                          {formatFileSize(file.fileSize)}
                        </span>
                        <div className="flex items-center gap-1">
                          <Button 
                            variant="ghost" 
                            size="sm" 
                            onClick={(e) => {
                              e.stopPropagation();
                              openPdfViewer(file);
                            }}
                          >
                            <Eye className="h-3 w-3" />
                          </Button>
                          <Button 
                            variant="ghost" 
                            size="sm" 
                            asChild
                            onClick={(e) => e.stopPropagation()}
                          >
                            <a href={file.fileUrl} target="_blank" rel="noopener noreferrer">
                              <Download className="h-3 w-3" />
                            </a>
                          </Button>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                ) : (
                  <div className="flex items-center gap-4 p-4">
                    <div className="flex items-center gap-3">
                      <FileText className="h-8 w-8 text-red-500" />
                      <span className="text-xl">{getPlatformIcon(file.platform)}</span>
                    </div>

                    <div className="flex-1 min-w-0">
                      <h3 className="font-medium text-gray-900 dark:text-white truncate">
                        {file.title}
                      </h3>
                      <div className="flex items-center gap-3 text-sm text-gray-600 dark:text-gray-400">
                        <span>{formatFileSize(file.fileSize)}</span>
                        {file.pageCount && <span>{file.pageCount} pages</span>}
                        {file.batchTitle && (
                          <span className="truncate">
                            {[file.batchTitle, file.courseTitle].filter(Boolean).join(' ‚Ä∫ ')}
                          </span>
                        )}
                      </div>
                    </div>

                    <div className="flex items-center gap-2">
                      <Button variant="ghost" size="sm">
                        <Eye className="h-4 w-4" />
                      </Button>
                      <Button variant="ghost" size="sm" asChild>
                        <a href={file.fileUrl} target="_blank" rel="noopener noreferrer">
                          <Download className="h-4 w-4" />
                        </a>
                      </Button>
                    </div>
                  </div>
                )}
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

