        </CardContent>
      </Card>
    </div>
  );

  const renderNativeAd = (ad: AdConfig) => (
    <Card key={ad.id} className="cursor-pointer hover:shadow-lg transition-shadow" onClick={() => handleAdClick(ad)}>
