      responsive: true,
      displayDuration: 30,
      autoHide: true,
      showCloseButton: true
    }
  });

  const [previewMode, setPreviewMode] = useState(false);

  const adTypes = [
    {
      id: 'popunder',
      name: 'Popunder Ads',
