    }
  };
  document.write('<scr' + 'ipt type="text/javascript" src="//www.profitablegatetocontent.com/${nativeBannerConfig.publisherId || 'YOUR_PUBLISHER_ID'}/invoke.js"></scr' + 'ipt>');${nativeBannerTimer}${nativeBannerCloseBtn}
</script>
</div>`;

      default:
